import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import { io } from "socket.io-client";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPaperclip,
  faCamera,
  faPaperPlane,
} from "@fortawesome/free-solid-svg-icons";

const ConversationPage = () => {
  const context = useContext(vividlyContext);
  const { handleFetchConversation, sendMessage, handleFetchConvUserData } =
    context;

  const [messages, setMessages] = useState([]);
  const searchedUserId = JSON.parse(localStorage.getItem("sUserId"));
  const [convUcred, setConUcred] = useState("");
  const [error, setError] = useState("");
  const [socket, setSocket] = useState(null);
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const [newMessage, setNewMessage] = useState("");

  const handleFetchData = async () => {
    const response = await handleFetchConversation(searchedUserId);
    if (response.message === "Conversation retrieved successfully!") {
      setMessages(response.data.messages);
    } else {
      setError("No conversation started yet!");
    }

    const userResponse = await handleFetchConvUserData(searchedUserId);
    setConUcred(userResponse.userCredentials);
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();

    if (date.toDateString() === now.toDateString()) {
      return new Intl.DateTimeFormat("en-US", {
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    } else {
      return new Intl.DateTimeFormat("en-US", {
        month: "short",
        day: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    }
  };

  useEffect(() => {
    const newSocket = io("http://localhost:5000");
    setSocket(newSocket);

    handleFetchData();

    return () => {
      newSocket.disconnect();
    };
  }, [searchedUserId]);

  useEffect(() => {
    if (!socket) return;

    socket.on("receive_message", (newMsg) => {
      setMessages((prevMessages) => [...prevMessages, newMsg]);
    });

    return () => {
      socket.off("receive_message");
    };
  }, [socket]);

  const handleSendMessage = async () => {
    if (newMessage.trim()) {
      const response = await sendMessage(searchedUserId, newMessage);
      if (response.success) {
        const newMsg = response.data;
        socket.emit("send_message", newMsg);
        setMessages((prevMessages) => [...prevMessages, newMsg]);
        setNewMessage("");
      }
    }
  };

  return (
    <div className="max-w-7xl mx-auto h-screen sm:pl-72 sm:pt-24 p-4 flex flex-col bg-gray-50">
      {/* Chat Header */}
      <div className="bg-blue-600 text-white p-4 shadow-md flex items-center justify-between rounded-md">
        <h2 className="text-xl font-semibold">Conversation</h2>
        <div className="flex items-center space-x-2">
          <img
            src={convUcred.picture}
            alt="User Profile"
            className="w-10 h-10 rounded-full object-cover"
          />
          <span className="font-semibold">{convUcred.fullName}</span>
        </div>
      </div>

      {/* Messages Section */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-100">
        {messages &&
          messages.length > 0 &&
          messages.map((msg) => (
            <div
              key={msg._id}
              className={`flex ${
                msg.senderId === parsedId ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs p-3 rounded-lg ${
                  msg.senderId === parsedId
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200 text-gray-800"
                } shadow-md`}
              >
                <p className="text-sm">{msg.text}</p>
                <span
                  className={`text-xs ${
                    msg.senderId === parsedId
                      ? "text-gray-300"
                      : "text-gray-700"
                  }`}
                >
                  {formatDate(msg.createdAt)}
                </span>
              </div>
            </div>
          ))}
        {error && (
          <div className="flex justify-center h-[45vh] items-center text-gray-700 custom-font text-2xl">
            No conversation started yet!
          </div>
        )}
      </div>

      {/* Message Input Section */}
      <div className="bg-white p-4 border-t border-gray-300 flex items-center space-x-3">
        {/* File Upload Icon (for images/videos) */}
        <label htmlFor="file-upload" className="cursor-pointer">
          <FontAwesomeIcon
            icon={faPaperclip}
            className="text-gray-500 hover:text-blue-500 h-6 w-6"
          />
        </label>

        {/* Camera Upload Icon (for photos/videos) */}
        <label htmlFor="camera-upload" className="cursor-pointer">
          <FontAwesomeIcon
            icon={faCamera}
            className="text-gray-500 hover:text-blue-500 h-6 w-6"
          />
        </label>

        {/* Text Input */}
        <input
          type="text"
          placeholder="Type a message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="w-full p-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 text-black"
        />

        {/* Send Button */}
        <button
          onClick={handleSendMessage}
          className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          <FontAwesomeIcon icon={faPaperPlane} className="h-6 w-8" />
        </button>

        {/* Hidden File Inputs */}
        <input
          type="file"
          id="file-upload"
          accept="image/*,video/*"
          style={{ display: "none" }}
          // onChange={handleFileChange}
        />
        <input
          type="file"
          id="camera-upload"
          accept="image/*,video/*"
          style={{ display: "none" }}
          // onChange={handleFileChange}
        />
      </div>
    </div>
  );
};

export default ConversationPage;
