import React from "react";

const SkeletonLoader = () => {
  return (
    <div className="w-full h-screen flex flex-col justify-center items-center bg-gray-100">
      {/* Cover Image Skeleton (Shimmering Line) */}
      <div className="w-full h-16 bg-gray-300 mb-4 rounded-lg animate-pulse"></div>

      {/* Profile Section Skeleton */}
      <div className="w-full max-w-xs h-8 bg-gray-300 mb-4 rounded-lg animate-pulse"></div>

      <div className="w-full max-w-xs h-6 bg-gray-300 mb-4 rounded-lg animate-pulse"></div>

      {/* Stats Skeleton (Optional) */}
      <div className="w-full max-w-md flex space-x-6 mb-6">
        <div className="w-24 h-6 bg-gray-300 rounded-lg animate-pulse"></div>
        <div className="w-24 h-6 bg-gray-300 rounded-lg animate-pulse"></div>
        <div className="w-24 h-6 bg-gray-300 rounded-lg animate-pulse"></div>
      </div>
    </div>
  );
};

export default SkeletonLoader;
