import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";

const UserFollowingPage = () => {
  const [following, setFollowing] = useState([]);
  const [loading, setLoading] = useState(true);

  const context = useContext(vividlyContext);
  const { fetchUserFollowingList } = context;

  const [followingList, setFollowingList] = useState([]);

  const handleUserFollowingListFunc = async () => {
    const response = await fetchUserFollowingList();
    if (response.success) {
      setFollowingList(response.userData);
    }
    setLoading(false);
  };

  useEffect(() => {
    handleUserFollowingListFunc();
  }, []);

  const handleUnfollow = (id) => {
    setFollowing(following.filter((user) => user.id !== id));
  };

  if (loading) {
    return (
      <div className="flex justify-center">
        <div className="text-xl font-bold text-gray-700">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-28 max-w-8xl mx-auto p-6 bg-gray-50">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Following</h1>
      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
        {followingList.map((user) => (
          <div
            key={user._id}
            className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center space-y-3"
          >
            <img
              src={user.picture}
              alt="failed to load img!"
              className="w-16 h-16 rounded-full object-cover"
            />
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-700">
                {user.fullName}
              </h3>
              <p className="text-sm text-gray-500">{user.userName}</p>
            </div>
            <button
              onClick={() => handleUnfollow(user.id)}
              className="px-4 py-2 bg-red-500 text-white font-semibold rounded-lg shadow hover:bg-red-600 transition"
            >
              Unfollow
            </button>
          </div>
        ))}
      </div>
      {followingList.length === 0 && (
        <div className="text-center mt-10 text-gray-600 text-lg">
          You are not following anyone at the moment.
        </div>
      )}
    </div>
  );
};

export default UserFollowingPage;
