import React, { useContext, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import vividlyContext from "../Context/vividlyContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit } from "@fortawesome/free-solid-svg-icons";
import Avatar from "../Images/Avatar.jpeg";
import OTPVerification from "./OTPVerification";
import Alert from "./Alert";
import SkeletonLoader from "./SkeletonLoader";

const Settings = () => {
  const navigate = useNavigate();
  const context = useContext(vividlyContext);
  const { deleteUserAccount, sendOTPForUpdSettings, verifyOTPFORUpdSettings } =
    context;
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const [step, setStep] = useState(1);

  const [showSkeleton, setShowSkeleton] = useState(false);
  const [countLoading, setCountLoading] = useState(0);

  const [uSetting, setUSetting] = useState("");
  const userSetting = async () => {
    if (countLoading === 0) {
      setShowSkeleton(true);
    }
    const id = localStorage.getItem("id");
    const parsedId = JSON.parse(id);

    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    try {
      const response = await fetch(
        `${ENDPOINT}/api/user/userSetting/${parsedId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setUSetting(data);
      } else {
        console.log("Error");
      }
      setCountLoading(1);
      setShowSkeleton(false);
    } catch (error) {
      console.log("Error:", error.message);
      setCountLoading(1);
      setShowSkeleton(false);
    }
  };

  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    userSetting();
  }, [isModalOpen]);

  const [theme, setTheme] = useState("light");

  const handleThemeChange = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };
  const [updSettings, setUpdSettings] = useState({
    fullName: "",
    password: "",
    gender: "",
    dob: "",
    picture: "",
  });

  const handleSettingsOnChange = (e) => {
    setUpdSettings({
      ...updSettings,
      [e.target.name]: e.target.value,
    });
  };
  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formdata = new FormData();
      formdata.append("image", file);
      try {
        const data = await fetch(`${ENDPOINT}/api/user/signupPic`, {
          method: "POST",
          body: formdata,
        });
        const response = await data.json();
        if (response.success) {
          setUpdSettings({ ...updSettings, picture: response.url });
        }
      } catch (error) {
        console.log(error);
      }
    }
  };

  const handleOpenModelFunc = () => {
    setUpdSettings({
      fullName: uSetting.moreUserDetails.fullName,
      password: uSetting.settings.password,
      gender: uSetting.moreUserDetails.gender,
      dob: uSetting.moreUserDetails.DOB,
      picture: uSetting.moreUserDetails.picture,
    });

    setIsModalOpen(true);
  };

  const handleSendOTP = async (e) => {
    e.preventDefault();
    await sendOTPForUpdSettings(uSetting.moreUserDetails.email);
    setStep(2);
  };

  const [timeLeft, setTimeLeft] = useState(3600);
  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes.toString().padStart(2, "0")}:${secs
      .toString()
      .padStart(2, "0")}`;
  };

  const [alert, setAlert] = useState(null);
  const showAlert = (type, msg) => {
    setAlert({
      type: type,
      msg: msg,
    });
    setTimeout(() => {
      setAlert("");
    }, 2000);
  };

  const [showerror, setShowError] = useState(false);

  const [otp, setOtp] = useState(["", "", "", "", "", ""]);

  const handleChange = (e, index) => {
    const newOtp = [...otp];
    newOtp[index] = e.target.value.slice(0, 1);
    setOtp(newOtp);

    if (e.target.value) {
      const nextInput = document.getElementById(`otp-input-${index + 1}`);
      if (nextInput) nextInput.focus();
    }
  };

  const handleKeyDown = (e, index) => {
    if (e.key === "Backspace" && otp[index] === "") {
      const prevInput = document.getElementById(`otp-input-${index - 1}`);
      if (prevInput) prevInput.focus();
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    const JoinOtp = otp.join("");
    if (JoinOtp.length < 6) {
      setShowError(true);
      return;
    }
    const response = await verifyOTPFORUpdSettings(
      updSettings.fullName,
      uSetting.moreUserDetails.email,
      updSettings.password,
      updSettings.gender,
      updSettings.dob,
      updSettings.picture,
      JoinOtp
    );

    if (response.success) {
      setIsModalOpen(false);
      setTimeLeft(3600);
    } else {
      showAlert("error", "Invalid or expired OTP!");
    }
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 max-w-8xl mx-auto p-6 bg-gray-50">
      {showSkeleton && <SkeletonLoader />}
      {!showSkeleton && (
        <>
          {uSetting && (
            <>
              {/* Account Settings */}
              <div className="p-6 bg-white shadow-lg rounded-lg max-w-8xl mx-auto mb-4">
                <h2 className="text-2xl font-bold text-gray-800 mb-6">
                  User Settings
                </h2>

                <div className="flex items-center mb-4">
                  <img
                    src={uSetting.moreUserDetails.picture || Avatar}
                    alt="Profile"
                    className="w-20 h-20 rounded-full border"
                  />
                  <FontAwesomeIcon
                    icon={faEdit}
                    className="ml-4 cursor-pointer text-gray-500 hover:text-blue-500"
                    onClick={handleOpenModelFunc}
                  />
                </div>

                <p className="text-gray-800">
                  <strong className="text-black">Full Name: </strong>
                  {uSetting.moreUserDetails.fullName}
                </p>
                <p className="text-gray-800">
                  <strong className="text-black">Username: </strong>
                  {uSetting.moreUserDetails.userName}
                </p>
                <p className="text-gray-800">
                  <strong className="text-black">Password: </strong>
                  {"*".repeat(uSetting.settings.password.length)}
                </p>

                <p className="text-gray-800">
                  <strong className="text-black">Gender:</strong>{" "}
                  {uSetting.moreUserDetails.gender}
                </p>
                <p className="text-gray-800">
                  <strong className="text-black">Date of Birth:</strong>{" "}
                  {new Date(uSetting.moreUserDetails.DOB).toDateString()}
                </p>

                {isModalOpen && (
                  <div className="fixed inset-0 p-3 flex items-center justify-center bg-black bg-opacity-50 z-50">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-96 sm:w-[400px] relative z-50">
                      <h3 className="text-lg font-semibold mb-2 text-black">
                        Edit Profile
                      </h3>
                      <form
                        className="max-h-[85vh] overflow-y-auto p-2"
                        onSubmit={handleSendOTP}
                      >
                        {step === 1 && (
                          <>
                            {/* Full Name */}
                            <label className="block mb-2 text-black">
                              Full Name
                            </label>
                            <input
                              type="text"
                              name="fullName"
                              value={updSettings.fullName}
                              onChange={handleSettingsOnChange}
                              className="w-full border p-2 rounded mb-2 text-gray-800"
                            />
                            <label className="block mb-2 text-black">
                              New Password
                            </label>
                            <input
                              type="password"
                              name="password"
                              defaultValue={"***"}
                              onChange={handleSettingsOnChange}
                              className="w-full border p-2 rounded mb-2 text-gray-800"
                            />

                            {/* Gender */}
                            <label className="block mb-2 text-black">
                              Gender
                            </label>
                            <select
                              name="gender"
                              value={updSettings.gender}
                              onChange={handleSettingsOnChange}
                              className="w-full border p-2 rounded mb-2 text-gray-800"
                            >
                              <option value="Male">Male</option>
                              <option value="Female">Female</option>
                              <option value="Other">Other</option>
                            </select>

                            {/* Date of Birth */}
                            <label className="block mb-2 text-black">
                              Date of Birth
                            </label>
                            <input
                              type="date"
                              name="dob"
                              value={
                                updSettings.dob
                                  ? new Date(updSettings.dob)
                                      .toISOString()
                                      .split("T")[0]
                                  : ""
                              }
                              onChange={handleSettingsOnChange}
                              className="w-full border p-2 rounded mb-2 text-gray-800"
                              onFocus={(e) =>
                                e.target.showPicker && e.target.showPicker()
                              }
                            />

                            {/* Profile Picture */}
                            <label className="block mb-2 text-black">
                              Profile Picture
                            </label>
                            <input
                              type="file"
                              accept="image/*"
                              name="picture"
                              onChange={(e) => {
                                handleSettingsOnChange(e);
                                handleImageChange(e);
                              }}
                              className="w-full border p-2 rounded mb-4 text-gray-800"
                            />

                            {/* Buttons */}
                            <div className="flex justify-end">
                              <button
                                type="submit"
                                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                              >
                                Verify Changes
                              </button>
                              <button
                                onClick={() => setIsModalOpen(false)}
                                type="button"
                                className="ml-4 bg-gray-400 text-white px-4 py-2 rounded hover:bg-gray-500"
                              >
                                Cancel
                              </button>
                            </div>
                          </>
                        )}

                        {step === 2 && (
                          <>
                            {/* Timer Section */}
                            <div className="flex justify-end mx-3 mt-2">
                              <p
                                className={`text-lg font-semibold ${
                                  timeLeft <= 500
                                    ? "text-red-500"
                                    : "text-gray-700"
                                }`}
                              >
                                OTP Expires in:{" "}
                                <span className="font-semibold">
                                  {formatTime(timeLeft)}
                                </span>
                              </p>
                            </div>
                            <div className="mt-1">
                              <h4 className="text-2xl font-semibold text-black mb-2">
                                Verify Your OTP
                              </h4>
                              <p className="text-gray-600">
                                Please enter the 6-digit OTP sent to your email
                                to update your information.
                              </p>

                              <div className="h-[26px] mb-2">
                                <Alert alert={alert} />
                              </div>
                              <div className="flex space-x-2">
                                {otp.map((digit, index) => (
                                  <input
                                    key={index}
                                    id={`otp-input-${index}`}
                                    type="password"
                                    value={digit}
                                    maxLength="1"
                                    required
                                    onChange={(e) => handleChange(e, index)}
                                    onKeyDown={(e) => handleKeyDown(e, index)}
                                    className="h-9 w-9 lmd:w-12 lmd:h-12 text-center border border-gray-300 rounded-md text-xl text-black"
                                  />
                                ))}
                              </div>
                              <div className="h-10 mt-2">
                                {showerror && (
                                  <p className="text-red-500 text-sm">
                                    Please enter 6 digit OTP!
                                  </p>
                                )}
                              </div>
                              <div className="flex justify-center gap-4">
                                {/* Back Button */}
                                <button
                                  type="button"
                                  onClick={() => {
                                    setStep(1);
                                    setTimeLeft(3600);
                                  }}
                                  className="bg-gray-300 text-black font-medium py-2 px-6 rounded-lg hover:bg-gray-400 transition-all duration-300"
                                >
                                   Back
                                </button>

                                {/* Verify Button */}
                                <button
                                  type="submit"
                                  onClick={handleVerifyOTP}
                                  className="bg-blue-600 text-white font-medium py-2 px-6 rounded-lg hover:bg-blue-700 transition-all duration-300"
                                >
                                  Verify OTP
                                </button>
                              </div>
                            </div>
                          </>
                        )}
                      </form>
                    </div>
                  </div>
                )}
              </div>

              {/* Privacy Settings */}
              <div className="bg-white p-6 rounded-lg shadow-md mb-6">
                <h2 className="text-xl font-semibold mb-4 text-black">
                  Privacy and Security
                </h2>
                <label className="block text-black font-semibold mb-1">
                  Account Type
                </label>

                <div className="space-y-4">
                  <div className="flex justify-between">
                    <div>
                      <select className="rounded-lg min-w-48 text-black">
                        {uSetting.settings.accountType === "public" ? (
                          <>
                            <option value="public">Public</option>
                            <option value="private">Private</option>
                          </>
                        ) : (
                          <>
                            <option value="private">Private</option>
                            <option value="public">Public</option>
                          </>
                        )}
                      </select>
                    </div>
                    <div>
                      <button className="bg-blue-500 text-white px-4 py-2 rounded-lg">
                        Change
                      </button>
                    </div>
                  </div>
                  <div>
                    <label className="block text-black font-semibold mb-3">
                      Blocked Users
                    </label>
                    <Link
                      to="/viewBlock_user"
                      className="bg-gray-700 text-white px-4 py-2 rounded-lg"
                    >
                      View Blocked Users
                    </Link>
                  </div>
                </div>
              </div>

              {/* Theme Settings */}
              <div className="bg-white p-6 rounded-lg shadow-md mb-6">
                <h2 className="text-xl font-semibold mb-4 text-black">
                  General Settings
                </h2>
                <div className="flex items-center justify-between">
                  <label className="text-black text-xl">Theme</label>

                  <button
                    onClick={handleThemeChange}
                    className="bg-blue-500 text-white px-4 py-2 rounded-lg"
                  >
                    Switch to{" "}
                    {uSetting.settings.theme === "light" ? "Dark" : "Light"}{" "}
                    Mode
                  </button>
                </div>
              </div>

              {/* Delete/Deactivate Account */}
              <div className="bg-white p-6 rounded-lg shadow-md mb-6">
                <h2 className="text-xl font-semibold mb-4 text-black">
                  Account Management
                </h2>
                <button
                  className="bg-red-500 text-white px-4 py-2 rounded-lg"
                  onClick={async () => {
                    const confirm = window.confirm(
                      "Are you sure you want to delete your account?"
                    );
                    if (confirm) {
                      const response = await deleteUserAccount();
                      if (response.success) {
                        navigate("/");
                        localStorage.removeItem("id");
                        localStorage.removeItem("userName");
                        localStorage.removeItem("token");
                      }
                    }
                  }}
                >
                  Delete Account
                </button>
                <p className="text-gray-600 text-sm mt-2">
                  Deleting your account is permanent and cannot be undone.
                </p>
              </div>
            </>
          )}
        </>
      )}
      <h1 className="text-3xl font-bold mb-6 text-black">Settings</h1>
    </div>
  );
};

export default Settings;
