import React, { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPenToSquare,
  faHeart as LikeHeart,
} from "@fortawesome/free-solid-svg-icons";
import {
  faComment,
  faHeart,
  faPaperPlane,
  faPenToSquare as faPenRegular,
  faTrashAlt,
} from "@fortawesome/free-regular-svg-icons";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";

const SearchedUserProfile = () => {
  const context = useContext(vividlyContext);
  const { chkIsAlreadyFollow, searchedUserAllDataFunc } = context;
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const [cText, setCText] = useState("");
  const [specificPostId, setSpecificPosId] = useState("");
  const [specificPostComments, setSpecificPostComments] = useState([]);
  const [statuses, setStatuses] = useState([]);

  const [userData, setUserData] = useState("");
  const searchedUserId = JSON.parse(localStorage.getItem("sUserId"));

  const handleFetchUData = async () => {
    const response = await searchedUserAllDataFunc(searchedUserId);
    setUserData(response);
  };
  const [isFollow, setIsFollow] = useState(false);

  useEffect(() => {
    handleFetchUData();
  }, [isFollow]);

  useEffect(() => {
    if (userData) {
      handleFetchLikeStatus();
      handleChkIsFollow();
    }
  }, [userData]);

  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);

  const handleFetchLikeStatus = async () => {
    const Statuses = await Promise.all(
      userData.posts.map(async (post) => {
        const likes = await handleFetchPostLikes(post._id);
        const comments = await handleFetchPostComments(post._id);
        let likeStatus = likes.some((like) => like.user === parsedId)
          ? "Liked"
          : "Not Liked";

        return { ...post, likes, comments, likeStatus };
      })
    );
    setStatuses(Statuses);
  };

  const handleChkIsFollow = async () => {
    const chk = await chkIsAlreadyFollow(userData.userCredentials.userName);
    if (chk.message === "Already follow") {
      setIsFollow(true);
    } else if (chk.message === "Not follow") {
      setIsFollow(false);
    }
  };

  const handleFetchPostLikes = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostLikes/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postLikes.likes;
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleFetchPostComments = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostComments/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postComments.comments;
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleLikePost = async (postId) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);
    const data = await fetch(`${ENDPOINT}/api/post/likePost/${postId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token": parsedToken,
      },
    });
    const response = await data.json();
    const userId = localStorage.getItem("id");
    const parsedUserId = JSON.parse(userId);

    const post = statuses.find((post) => post._id === postId);
    if (post) {
      const userAlreadyLiked = post.likes.some(
        (like) => like.user === parsedUserId
      );

      if (userAlreadyLiked) {
        setStatuses(
          statuses.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: p.likes.filter((like) => like.user !== parsedUserId),
                  likeStatus: "Not Liked",
                }
              : p
          )
        );
      } else {
        setStatuses(
          statuses.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: [...p.likes, { user: parsedUserId }],
                  likeStatus: "Liked",
                }
              : p
          )
        );
      }
    }

    return response;
  };

  const handleSpecificPostComments = (id) => {
    const post = statuses.find((post) => post._id === id);
    setSpecificPostComments(post.comments);
  };

  const handleStoreSpecificPostId = (id) => {
    setSpecificPosId(id);
  };

  const handleCommentOnChange = (e) => {
    setCText(e.target.value);
  };

  const handleAddComment = async (postId, text, event) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    if (event.key === "Enter" || event.key === "enter") {
      setCText("");
      try {
        const response = await fetch(
          `${ENDPOINT}/api/post/commentOnPost/${postId}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              "auth-token": parsedToken,
            },
            body: JSON.stringify({
              text,
            }),
          }
        );
        const data = await response.json();
        if (data.success) {
          setSpecificPostComments(data.updatedComment.comments);
          setStatuses(
            statuses.map((p) =>
              p._id === postId
                ? {
                    ...p,
                    comments: data.updatedComment.comments,
                  }
                : p
            )
          );
        }
      } catch (error) {
        console.log("Error:", error.message);
      }
    }
  };
  const [loading, setLoading] = useState(false); // Track loading state

  const handleFollowUnFollow = async () => {
    try {
      setLoading(true); // Start loading

      const id = localStorage.getItem("id");
      const parsedId = JSON.parse(id);
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);

      const response = await fetch(
        `${ENDPOINT}/api/user/followUser/${parsedId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
          body: JSON.stringify({ user: searchedUserId }),
        }
      );
      const data = await response.json();
      console.log(data);
      if (data.message === "Followed the user successfully!") {
        setIsFollow(true);
      } else {
        setIsFollow(false);
      }
    } catch (error) {
      console.log("Error:", error.message);
    } finally {
      setLoading(false); // Stop loading when request is finished
    }
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 max-w-8xl mx-auto p-6 bg-gray-50">
      {/* User Overview */}

      <div className="flex flex-col lg:flex-row lg:justify-between bg-white shadow rounded-lg">
        <div className="flex items-center space-x-4 p-4">
          <img
            src={(userData && userData.userCredentials.picture) || Avatar}
            alt="Failed to load img"
            className="w-20 h-20 rounded-full object-cover"
          />
          <div>
            <h2 className="text-xl font-bold text-black">
              {userData && userData.userCredentials.fullName}
            </h2>
            <p className="text-gray-600">
              {userData && userData.userCredentials.userName}
            </p>
            <button
              className={`mt-2 px-6 py-3 w-32 rounded-lg text-white font-semibold transition-all duration-300 ease-in-out transform ${
                isFollow
                  ? "bg-red-500 hover:bg-red-600"
                  : "bg-blue-500 hover:bg-blue-600"
              } ${loading ? "opacity-50 cursor-not-allowed scale-95" : ""}`}
              onClick={handleFollowUnFollow}
              disabled={loading}
            >
              {loading ? (
                <div className="flex items-center justify-center">
                  <svg
                    className="w-5 h-5 text-white animate-spin"
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <circle
                      className="opacity-25"
                      cx="12"
                      cy="12"
                      r="10"
                      stroke="currentColor"
                      strokeWidth="4"
                    ></circle>
                    <path
                      className="opacity-75"
                      fill="currentColor"
                      d="M4 12a8 8 0 118 8 8 8 0 01-8-8z"
                    ></path>
                  </svg>
                  <span className="ml-2">Loading...</span>
                </div>
              ) : isFollow ? (
                "Unfollow"
              ) : (
                "Follow"
              )}
            </button>
          </div>
        </div>
        {/* Followers and Following */}
        <div className="flex items-center p-6 pl-8 pb-4 space-x-4 sm:space-x-6 lg:space-x-8 lg:pl-0 lg:pb-4">
          <div>
            <h4 className="text-lg font-bold text-black">Posts</h4>
            <p className="text-gray-700">{userData && userData.posts.length}</p>
          </div>
          <div>
            <h4 className="text-lg font-bold text-black">Followers</h4>
            <p className="text-gray-700">
              {userData && userData.followersList.followersList.length}
            </p>
          </div>
          <div>
            <h4 className="text-lg font-bold text-black">Following</h4>
            <p className="text-gray-700">
              {userData && userData.followingList.followingList.length}
            </p>
          </div>
        </div>
      </div>

      {/* Mutual Connections */}
      <div className="mt-6 bg-white shadow rounded-lg p-4">
        <h3 className="text-lg font-bold mb-4 text-black">
          Mutual Connections
        </h3>
        <div className="flex space-x-4">
          {Array(3)
            .fill("")
            .map((_, index) => (
              <div key={index} className="flex flex-col items-center">
                <img
                  src={`https://via.placeholder.com/100?text=User+${index + 1}`}
                  alt={`User ${index + 1}`}
                  className="w-16 h-16 rounded-full object-cover"
                />
                <span className="text-gray-700 mt-2">User {index + 1}</span>
              </div>
            ))}
        </div>
      </div>

      {/* Posts */}
      <div className="mt-6 bg-white shadow rounded-lg p-4">
        <h3 className="text-lg font-bold mb-4 text-black">Posts</h3>
        <div className="grid grid-cols-2 lsm:grid-cols-3 gap-4">
          {statuses.map((post) => {
            return (
              <div
                key={post._id}
                className="bg-gray-200 rounded-lg overflow-hidden"
              >
                {post.mediaType === "image" ? (
                  <img
                    src={post.media}
                    alt="Failed to load img"
                    className="w-full h-56 lg:h-72 object-cover"
                  />
                ) : (
                  <video
                    src={post.media}
                    alt="Failed to load video"
                    controls
                    className="w-full h-56 lg:h-72 object-cover"
                  />
                )}
                <div className="w-full p-2 bg-gray-50 shadow-2xl rounded-b-lg border-b-2 border-gray-700">
                  <div className="flex">
                    <div className="ml-2 mr-1 hover:cursor-pointer">
                      <FontAwesomeIcon
                        className={`text-2xl transition-transform duration-300 ease-in-out transform ${
                          post.likeStatus === "Liked"
                            ? "text-red-600 scale-105"
                            : "text-black scale-100"
                        }`}
                        icon={post.likeStatus === "Liked" ? LikeHeart : faHeart}
                        onClick={async () => {
                          await handleLikePost(post._id);
                        }}
                      />
                    </div>
                    <div className="text-black w-8">
                      {post.likes && post.likes.length}
                    </div>
                    <dialog id="addComment_Modal" className="modal">
                      <div className="modal-box min-h-[80vh] overflow-hidden bg-white">
                        <form method="dialog">
                          <button className="btn btn-sm btn-circle btn-ghost absolute right-2 top-2 text-black">
                            âœ•
                          </button>
                        </form>
                        <div className="flex justify-center mb-3">
                          <h1 className="cus-font text-black font-bold text-3xl mb-5">
                            Add a comment
                          </h1>
                        </div>

                        <div>
                          {specificPostComments.length > 0 ? (
                            <div className="space-y-3 overflow-y-auto max-h-[60vh]">
                              {specificPostComments.map((cData) => {
                                return (
                                  <div
                                    className="flex bg-gray-100 rounded-xl p-1 shadow-sm border border-gray-400 mb-3"
                                    key={cData._id}
                                  >
                                    <img
                                      src={cData.userPic}
                                      alt="failed to load user img"
                                      className="rounded-[50%] h-14 w-16"
                                    />
                                    <div className="flex flex-col">
                                      <p className="ml-3 font-bold text-black">
                                        {cData.userName}
                                      </p>
                                      <p className="ml-3 text-black">
                                        {cData.text}
                                      </p>
                                    </div>
                                  </div>
                                );
                              })}
                            </div>
                          ) : (
                            <div className="flex justify-center items-center h-[55vh] cus-font text-3xl text-black">
                              No Comments Here
                            </div>
                          )}

                          <div className="flex justify-start">
                            <input
                              className="w-[87%] fixed rounded-lg bottom-5 bg-gray-100 focus:ring-black text-black"
                              type="text"
                              placeholder="Add a comment"
                              value={cText}
                              onChange={handleCommentOnChange}
                              onKeyDown={(event) =>
                                handleAddComment(specificPostId, cText, event)
                              }
                            />
                          </div>
                        </div>
                      </div>
                    </dialog>

                    <div className="hover:cursor-pointer">
                      <FontAwesomeIcon
                        className="text-black text-2xl mr-1"
                        icon={faComment}
                        onClick={async () => {
                          handleSpecificPostComments(post._id);
                          handleStoreSpecificPostId(post._id);
                          document
                            .getElementById("addComment_Modal")
                            .showModal();
                        }}
                      />
                    </div>
                    <div className="text-black w-8">
                      {post.comments && post.comments.length}
                    </div>

                    <div className="hover:cursor-pointer">
                      <FontAwesomeIcon
                        className="text-black text-2xl mr-1"
                        icon={faPaperPlane}
                      />
                    </div>
                    <div className="text-black">2</div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
        {userData && userData.posts.length === 0 ? (
          <p className="text-gray-700 font-semibold text-center mb-6">
            No posts have been shared yet.
          </p>
        ) : (
          ""
        )}
      </div>
    </div>
  );
};

export default SearchedUserProfile;
