import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import { useNavigate } from "react-router-dom";
const PostPreview = () => {
  const context = useContext(vividlyContext);
  const [postData, setPostData] = useState("");
  const { fetchPostPreview } = context;
  const navigate = useNavigate();

  const handlePostData = async () => {
    const parsedPId = JSON.parse(localStorage.getItem("postPreview"));
    const response = await fetchPostPreview(parsedPId);

    setPostData(response);
  };
  useEffect(() => {
    handlePostData();
  }, []);
  const handSearchUser = (uId) => {
    localStorage.setItem("sUserId", JSON.stringify(uId));
    navigate("/searchUser");
  };
  return (
    <div className="max-w-7xl mx-auto min-h-screen sm:pl-72 sm:pt-24 p-4 bg-white shadow-lg rounded-lg overflow-hidden">
      <div className="flex items-center px-6 py-4 bg-gray-100 rounded-lg mb-3">
        <img
          src={postData.fetchUserData.picture}
          alt="Creator Avatar"
          className="w-12 h-12 rounded-full object-cover hover:cursor-pointer"
          onClick={() => handSearchUser(postData.fetchUserData._id)}
        />
        <div className="ml-4">
          <h3
            className="text-lg font-semibold text-gray-800 hover:cursor-pointer"
            onClick={() => handSearchUser(postData.fetchUserData._id)}
          >
            {postData.fetchUserData.fullName}
          </h3>
          <p
            className="text-sm text-gray-500 hover:cursor-pointer"
            onClick={() => handSearchUser(postData.fetchUserData._id)}
          >
            @{postData.fetchUserData.userName}
          </p>
        </div>
      </div>

      <div className="relative">
        {postData.findPost.mediaType === "video" ? (
          <video
            src={postData.findPost.media}
            alt="Post Media"
            className="w-full h-[400px] object-cover rounded-lg"
          />
        ) : (
          <img
            src={postData.findPost.media}
            alt="Post Media"
            className="w-full h-[400px] object-cover rounded-lg"
          />
        )}
      </div>

      <div className="p-6">
        <p className="text-gray-700">{postData.findPost.description}</p>
      </div>

      <div className="flex justify-between items-center px-6 py-4 border-t border-gray-200">
        <div className="flex items-center space-x-6">
          <div className="flex items-center space-x-2">
            <div className="text-black">Likes</div>
            <p className="text-gray-600">{postData.fetchLikes.likes.length}</p>
          </div>
          <div className="flex items-center space-x-2">
            <div className="text-black">Comments</div>
            <p className="text-gray-600">
              {postData.fetchComments.comments.length}
            </p>
          </div>
        </div>
      </div>

      <div className="p-6 border-t border-gray-200">
        <h4 className="text-lg font-semibold text-gray-800 mb-4">Comments</h4>
        <div className="space-y-4 max-h-56 overflow-y-auto">
          {postData.fetchComments.comments.map((c) => {
            return (
              <div className="flex items-start space-x-4">
                <img
                  src={c.userPic}
                  alt="Commenter Avatar"
                  className="w-10 h-10 rounded-full object-cover"
                />
                <div>
                  <p className="text-gray-800 font-semibold">{c.name}</p>
                  <p className="text-gray-700 text-sm">{c.text}</p>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default PostPreview;
