import React, { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart,
  faComment,
  faShareSquare,
} from "@fortawesome/free-regular-svg-icons";
import { faHeart as LikeHeart } from "@fortawesome/free-solid-svg-icons";
import vividlyContext from "../Context/vividlyContext";

const Reels = () => {
  const context = useContext(vividlyContext);
  const { fetchTrendingReelsFunc } = context;
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;

  const handleFetchPostLikes = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostLikes/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postLikes.likes;
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const handleLikePost = async (postId) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);
    const data = await fetch(`${ENDPOINT}/api/post/likePost/${postId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token": parsedToken,
      },
    });
    const response = await data.json();
    const userId = localStorage.getItem("id");
    const parsedUserId = JSON.parse(userId);

    const post = tReelsDetails.find((post) => post._id === postId);
    if (post) {
      const userAlreadyLiked = post.likes.some(
        (like) => like.user === parsedUserId
      );

      if (userAlreadyLiked) {
        setTReelsDetails(
          tReelsDetails.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: p.likes.filter((like) => like.user !== parsedUserId),
                  likeStatus: "Not Liked",
                }
              : p
          )
        );
      } else {
        setTReelsDetails(
          tReelsDetails.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: [...p.likes, { user: parsedUserId }],
                  likeStatus: "Liked",
                }
              : p
          )
        );
      }
    }

    return response;
  };

  const handleFetchPostComments = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostComments/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postComments.comments; // Return the likes for a given post
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const [tReelsDetails, setTReelsDetails] = useState([]);

  const trendingReelsDetailsFunc = async () => {
    const posts = await fetchTrendingReelsFunc();
    const postsWithDetails = await Promise.all(
      posts.map(async (post) => {
        const likes = await handleFetchPostLikes(post._id);
        const comments = await handleFetchPostComments(post._id);
        let likeStatus = likes.some((like) => like.user === parsedId)
          ? "Liked"
          : "Not Liked";

        return { ...post, likes, comments, likeStatus };
      })
    );
    setTReelsDetails(postsWithDetails);
  };

  const [specificPostComments, setSpecificPostComments] = useState([]);
  const handleSpecificPostComments = (id) => {
    const post = tReelsDetails.find((post) => post._id === id);
    setSpecificPostComments(post.comments);
  };
  const [cText, setCText] = useState("");
  const handleCommentOnChange = (e) => {
    setCText(e.target.value);
  };
  const handleAddComment = async (postId, text, event) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    if (event.key === "Enter" || event.key === "enter") {
      setCText("");
      try {
        const response = await fetch(
          `${ENDPOINT}/api/post/commentOnPost/${postId}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              "auth-token": parsedToken,
            },
            body: JSON.stringify({
              text,
            }),
          }
        );
        const data = await response.json();
        if (data.success) {
          setSpecificPostComments(data.updatedComment.comments);
          setTReelsDetails(
            tReelsDetails.map((p) =>
              p._id === postId
                ? {
                    ...p,
                    comments: data.updatedComment.comments,
                  }
                : p
            )
          );
        }
      } catch (error) {
        console.log("Error:", error.message);
      }
    }
  };

  const [specificPostId, setSpecificPosId] = useState("");

  const handleStoreSpecificPostId = (id) => {
    setSpecificPosId(id);
  };

  useEffect(() => {
    trendingReelsDetailsFunc();
  }, []);

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-4 sm:p-6">
      {/* Header Section */}
      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-3xl font-bold">Reels</h1>
        <div className="mt-4">
          <input
            type="text"
            placeholder="Search Reels"
            className="w-full px-4 py-2 rounded-lg shadow-md bg-white text-gray-800"
          />
        </div>
      </div>
      <dialog id="addComment_Modal" className="modal">
        <div className="modal-box min-h-[80vh] overflow-hidden bg-white rounded-lg shadow-lg relative">
          <form method="dialog">
            <button className="btn btn-sm btn-circle btn-ghost absolute right-4 top-4 text-black hover:bg-opacity-70 transition">
              âœ•
            </button>
          </form>

          <div className="flex justify-center mb-5">
            <h1 className="cus-font text-black font-semibold text-4xl mb-4">
              Add a comment
            </h1>
          </div>

          <div className="space-y-3 overflow-y-auto max-h-[60vh] pr-2 mb-[80px]">
            {specificPostComments.length > 0 ? (
              specificPostComments.map((cData) => (
                <div
                  key={cData._id}
                  className="flex bg-gray-100 rounded-xl p-2 shadow-md border border-gray-300 hover:bg-gray-200 transition"
                >
                  <img
                    src={cData.userPic}
                    alt="Failed to load user img"
                    className="rounded-full h-16 w-16 border-2 border-gray-400"
                  />
                  <div className="flex flex-col ml-4">
                    <p className="font-semibold text-black">{cData.userName}</p>
                    <p className="text-gray-700">{cData.text}</p>
                  </div>
                </div>
              ))
            ) : (
              <div className="flex justify-center items-center h-[55vh] custom-font text-2xl sm:text-3xl text-gray-600">
                No Comments Here
              </div>
            )}
          </div>

          {/* Fixed position input */}
          <div className="absolute bottom-5 left-0 w-full px-4">
            <input
              className="w-full rounded-full bg-gray-100 focus:ring-1  focus:ring-blue-500 py-2 px-4 text-black shadow-md focus:outline-none transition"
              type="text"
              placeholder="Add a comment"
              value={cText}
              onChange={handleCommentOnChange}
              onKeyDown={(event) =>
                handleAddComment(specificPostId, cText, event)
              }
            />
          </div>
        </div>
      </dialog>

      {/* Reels Feed */}
      <div className="space-y-8">
        {console.log(tReelsDetails)}
        {tReelsDetails.map((reel) => (
          <div
            key={reel._id}
            className="relative bg-white rounded-lg shadow-lg overflow-hidden transition-transform duration-300 hover:scale-[101%] hover:shadow-xl"
          >
            {/* Reel Video Section */}
            <div className="relative">
              <video
                className="w-full max-h-[85vh] object-cover rounded-t-lg"
                src={reel.media}
                controls
                autoPlay
                muted
                loop
              />
            </div>

            {/* Reel Info Section */}
            <div className="p-5">
              {/* User Info */}
              <div className="flex items-center space-x-4 mb-4">
                <img
                  src={reel.userId.picture}
                  alt="User profile pic"
                  className="w-14 h-14 rounded-full border-2 border-blue-500 object-cover"
                />
                <div>
                  <p className="text-lg font-semibold text-gray-800">
                    {reel.userId.fullName}
                  </p>
                  <p className="text-sm text-gray-500">
                    Posted on: {new Date(reel.createdAt).toLocaleDateString()}
                  </p>
                </div>
              </div>

              {/* Description */}
              <p className="text-gray-700 mb-4">
                <span className="font-bold text-gray-900">Description:</span>{" "}
                {reel.description}
              </p>

              {/* Interaction Buttons */}
              <div className="flex justify-around mt-4 border-t pt-4">
                <button
                  className="flex items-center text-gray-600 hover:text-red-500 space-x-2 text-lg"
                  onClick={() => handleLikePost(reel._id)}
                >
                  <FontAwesomeIcon
                    icon={reel.likeStatus === "Liked" ? LikeHeart : faHeart}
                    className={`text-2xl cursor-pointer ${
                      reel.likeStatus === "Liked"
                        ? "text-red-600"
                        : "text-gray-600"
                    } hover:scale-110 transition-transform`}
                  />
                  <span> {reel.likes?.length}</span>
                </button>

                <button
                  className="flex items-center text-gray-600 hover:text-blue-500 space-x-2 text-lg"
                  onClick={() => {
                    handleSpecificPostComments(reel._id);
                    handleStoreSpecificPostId(reel._id);
                    document.getElementById("addComment_Modal").showModal();
                  }}
                >
                  <FontAwesomeIcon icon={faComment} size="lg" />
                  <span> {reel.comments?.length}</span>
                </button>

                <button className="flex items-center text-gray-600 hover:text-green-500 space-x-2 text-lg">
                  <FontAwesomeIcon icon={faShareSquare} size="lg" />
                  <span>{reel.shares} Shares</span>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
      {tReelsDetails.length === 0 && (
        <p className="text-2xl text-center text-gray-800 font-semibold">
          No reels available at the moment
        </p>
      )}
    </div>
  );
};

export default Reels;
