import { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { io } from "socket.io-client";
import {
  faCog,
  faComments,
  faRemove,
  faTrash,
} from "@fortawesome/free-solid-svg-icons";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";
import { useNavigate } from "react-router-dom";
import Alert2 from "./Alert2";
import Alert from "./Alert";

const GroupChatConversationPage = () => {
  const context = useContext(vividlyContext);
  const {
    handleFetchGroupData,
    handleFetchGroupMessages,
    sendMessageInGroup,
    handledeleteGroup,
    searchUsers,
    searchUserFunc,
  } = context;

  const [alert, setAlert] = useState(null);
  const showAlert = (type, msg) => {
    setAlert({
      type: type,
      msg: msg,
    });
    setTimeout(() => {
      setAlert("");
    }, 2000);
  };

  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const [groupData, setGroupData] = useState("");
  const [groupMessages, setGroupMessages] = useState([]);
  const groupId = localStorage.getItem("groupId");
  const parsedGroupId = JSON.parse(groupId);
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const [showModal, setShowModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(Avatar);

  const navigate = useNavigate();

  const [socket, setSocket] = useState("");

  const [message, setMessage] = useState("");
  const [addedMembersDetails, setAddedMembersDetails] = useState([]);

  const fetchGroupDataFunc = async () => {
    const response = await handleFetchGroupData(parsedGroupId);
    if (response.success) {
      setGroupData(response.data);
      setAddedMembersDetails(response.userDetails);
    }
  };
  const fetchGroupMessagesFunc = async () => {
    const response = await handleFetchGroupMessages(parsedGroupId);
    if (response.success) {
      setGroupMessages(response.data);
    }
  };

  useEffect(() => {
    fetchGroupDataFunc();
    fetchGroupMessagesFunc();
  }, []);

  useEffect(() => {
    if (!socket) return;

    socket.on("receive_message", (newMsg) => {
      setGroupMessages((prevGroupMessages) => [...prevGroupMessages, newMsg]);
    });

    return () => {
      socket.off("receive_message");
    };
  }, [socket]);

  const handleSendMessage = async (groupId, senderId, text) => {
    if (message.trim()) {
      const response = await sendMessageInGroup(groupId, senderId, text);
      if (response.success) {
        setMessage("");
        const newMsg = response.data;
        socket.emit("send_message", newMsg[0]);
        setGroupMessages((prevGroupMessages) => [
          ...prevGroupMessages,
          response.data[0],
        ]);
      }
    }
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formdata = new FormData();
      formdata.append("media", file);
      try {
        const data = await fetch(`${ENDPOINT}/api/post/media`, {
          method: "POST",
          body: formdata,
        });
        const response = await data.json();
        if (response.success) {
          setSelectedImage(response.url);
        }
      } catch (error) {
        console.log(error);
      }
    }
  };

  useEffect(() => {
    const newSocket = io("http://localhost:5000");
    setSocket(newSocket);

    return () => {
      newSocket.disconnect();
    };
  }, [parsedGroupId]);

  const handSearchUser = (uId) => {
    localStorage.setItem("sUserId", JSON.stringify(uId));
    navigate("/searchUser");
  };

  const handleDeleteFunc = async (groudId) => {
    if (!groupData.admins.includes(parsedId)) {
      return showAlert("error", "You are not alowed to delete group!");
    }
    const response = await handledeleteGroup(groudId);
    if (response.success) {
      navigate("/groups");
    }
  };

  const [keyword, setKeyword] = useState("");
  const [show, setShow] = useState(true);
  const handleOnchange = (e) => {
    const searchTerm = e.target.value;
    setKeyword(e.target.value);
    if (searchTerm.length > 0) {
      searchUserFunc(e.target.value);
      setShow(false);
    } else {
      setShow(true);
    }
  };

  const handleRemoveMember = (userId) => {
    setAddedMembersDetails(
      addedMembersDetails.filter((member) => member._id !== userId)
    );
  };

  const [updGroupInfo, setUpdGroupInfo] = useState({
    groupName: "",
    groupDescription: "",
  });

  const handleOpenModelFunc = () => {
    setUpdGroupInfo({
      groupName: groupData.groupName,
      groupDescription: groupData.description,
    });
  };

  const handleGroupInfoOnChange = (e) => {
    setUpdGroupInfo({
      ...updGroupInfo,
      [e.target.name]: e.target.value,
    });
  };

  const handleAddMember = (id, fullName, picture) => {
    if (!addedMembersDetails.some((member) => member._id === id)) {
      setAddedMembersDetails((prevAddedMembers) => [
        ...prevAddedMembers,
        { _id: id, fullName, picture }, // Correct object syntax
      ]);
      setKeyword("");
    }
  };

  const handleUpdateGroupFunc = () => {};

  return (
    <div className="max-w-7xl mx-auto  sm:pl-72 sm:pt-24 p-4 flex flex-col bg-gray-50">
      <div className="flex flex-col h-[80vh] bg-gray-50">
        <Alert2 alert={alert} />
        {console.log(addedMembersDetails)}

        {/* Header Section */}
        <div className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white p-5 flex items-center justify-between rounded-t-md">
          <div className="flex items-center space-x-4">
            {/* Group Image */}
            <img
              src={groupData.groupPicture || Avatar}
              alt="Group Logo"
              className="w-12 h-12 rounded-full object-cover"
            />
            {/* Group Name */}
            <div className="text-3xl font-semibold">{groupData.groupName}</div>
          </div>
          {console.log(groupData)}
          <div className="flex items-center space-x-4">
            <button className="bg-gray-800 text-white rounded-full p-3 hover:bg-gray-700 transition duration-300">
              <FontAwesomeIcon
                icon={faTrash}
                className="text-lg hover:text-red-600"
                onClick={() => {
                  const isConfirm = window.confirm(
                    "Are you sure to delete the group!"
                  );
                  if (isConfirm) {
                    handleDeleteFunc(groupData._id);
                  }
                }}
              />
            </button>
            <button className="bg-gray-800 text-white rounded-full p-3 hover:bg-gray-700 transition duration-300">
              <FontAwesomeIcon
                icon={faCog}
                className="text-lg"
                onClick={() => {
                  handleOpenModelFunc();
                  setShowModal(true);
                }}
              />
            </button>
          </div>
        </div>

        {showModal && (
          <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center z-50">
            <div className="bg-white p-4 rounded-lg shadow-lg w-96 max-h-[90vh] overflow-y-auto sm:w-80 md:w-[600px]">
              <h2 className="text-2xl font-bold text-black">
                Update Group Info
              </h2>
              <div className="h-[26px] mb-2">
                <Alert alert={alert} />
              </div>
              <input
                type="text"
                placeholder="Group Name"
                className="p-2 border rounded-lg w-full mb-4 text-black"
                name="groupName"
                value={updGroupInfo.groupName}
                onChange={handleGroupInfoOnChange}
              />

              <textarea
                placeholder="Group Description"
                className="p-2 border rounded-lg w-full mb-4 text-black"
                rows="3"
                name="groupDescription"
                value={updGroupInfo.groupDescription}
                onChange={handleGroupInfoOnChange}
              />

              <input
                type="text"
                placeholder="Search user and click to add more members"
                className="p-2 border rounded-lg w-full mb-1 text-black"
                value={keyword}
                onChange={handleOnchange}
              />
              <div className="max-h-[90vh] overflow-y-auto">
                {!show &&
                  searchUsers &&
                  searchUsers.map((user) => {
                    return (
                      <div
                        className="flex items-center p-2 bg-gray-50 rounded-lg shadow-md text-black hover:bg-gray-200 transition-all duration-300 cursor-pointer border-black border-b-2 mb-3"
                        onClick={() => {
                          setShow(true);
                          handleAddMember(
                            user._id,
                            user.fullName,
                            user.picture
                          );
                        }}
                        key={user._id}
                      >
                        <img
                          src={user.picture}
                          alt="Failed to load img"
                          className="w-12 h-12 ml-2 object-cover rounded-full border-2 border-gray-800"
                        />
                        <div className="ml-4">
                          <h2 className="text-base font-semibold ">
                            {user.fullName}
                          </h2>
                          <p className="text-sm ">@{user.userName}</p>
                        </div>
                      </div>
                    );
                  })}
                {!show && !searchUsers ? (
                  <p className="text-lg text-center text-gray-500 font-medium py-4">
                    {" "}
                    No users found. Try searching with a different keyword!
                  </p>
                ) : (
                  ""
                )}
              </div>

              <div className="mt-4">
                <div className="text-sm font-medium text-gray-700 mb-2">
                  Added Members:
                </div>

                <div className="flex flex-wrap gap-2 mb-2">
                  {addedMembersDetails.map((member) => (
                    <div
                      key={member._id}
                      className="flex items-center space-x-2 bg-gray-200 p-2 rounded-full text-sm hover:bg-gray-300 transition-all duration-200 ease-in-out"
                      style={{ minWidth: "110px" }}
                    >
                      <img
                        src={member.picture}
                        alt={member.fullName}
                        className="w-6 h-6 rounded-full border-2 text-gray-700 border-gray-300"
                      />
                      <span className="truncate font-medium text-gray-700">
                        {member.fullName}
                      </span>
                      <button
                        onClick={() => handleRemoveMember(member._id)}
                        className="text-red-500 text-lg hover:text-red-700 transition-all duration-200 ease-in-out"
                      >
                        <FontAwesomeIcon icon={faRemove} />
                      </button>
                    </div>
                  ))}
                </div>
              </div>
              <input
                type="file"
                className="p-2 border rounded-lg w-full mb-4 text-black"
                onChange={handleImageChange}
              />
              <div className="flex justify-between">
                <button
                  className="px-4 py-2 bg-gray-600 text-white rounded-full hover:bg-gray-700"
                  onClick={() => setShowModal(false)}
                >
                  Cancel
                </button>
                <button
                  className="px-4 py-2 bg-indigo-600 text-white rounded-full hover:bg-indigo-700"
                  onClick={handleUpdateGroupFunc}
                >
                  UpdateGroup
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Chat Messages Section */}
        <div className="flex-grow overflow-y-auto p-6 space-y-4">
          {groupMessages.length === 0 ? (
            <p className="mt-8 text-center custom-font text-2xl font-bold text-indigo-600">
              <span className="mr-2">
                <FontAwesomeIcon icon={faComments} className="text-xl" />
              </span>
              No conversation yet!
            </p>
          ) : (
            groupMessages.map((msg) => (
              <div
                key={msg._id}
                className={`flex ${
                  msg.senderId._id === parsedId
                    ? "justify-end"
                    : "justify-start"
                }`}
              >
                <div
                  className={`w-12 h-12 rounded-full overflow-hidden border-2 border-white ${
                    msg.senderId._id === parsedId ? "mr-2" : "ml-2"
                  }`}
                >
                  <img
                    src={msg.senderId.picture || Avatar}
                    alt={msg.senderId.fullName}
                    className="object-cover w-full h-full hover:cursor-pointer"
                    onClick={() => handSearchUser(msg.senderId._id)}
                  />
                </div>

                <div
                  className={`min-w-48 max-w-xs p-4 rounded-lg text-white ${
                    msg.senderId._id === parsedId
                      ? "bg-blue-600"
                      : "bg-gray-700"
                  }`}
                >
                  <div className="font-medium text-sm">
                    {msg.senderId.fullName}
                  </div>
                  <p className="mt-1">{msg.text}</p>
                </div>
              </div>
            ))
          )}
        </div>

        <div className="bg-white p-4 border-t border-gray-200">
          <div className="flex items-center space-x-4">
            <input
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="flex-grow p-3 border border-gray-300 text-black rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="Type a message..."
            />
            <button
              onClick={() =>
                handleSendMessage(parsedGroupId, parsedId, message)
              }
              className="bg-indigo-600 text-white rounded-full p-3 hover:bg-indigo-700 transition duration-300"
            >
              <span className="material-icons">send</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GroupChatConversationPage;
