import React, { useState } from "react";
import VividlyContext from "./vividlyContext";

const VividlyProvider = (props) => {
  const [blockUsers, setBlockUsers] = useState([]);
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const [suggestions, setSuggestions] = useState([]);
  const [reels, setReels] = useState([]);
  const [likedPosts, setLikedPosts] = useState([]);

  const [searchUsers, setSearchUsers] = useState([]);
  const [searchedUsersAllData, setSearchedUsersAllData] = useState("");

  const [sUserPic, setSUserPic] = useState("");
  const [sUserFullName, setSUserFullName] = useState("");
  const [sUserName, setsUserName] = useState("");
  const [sUserPosts, setSUserPosts] = useState([]);
  const [sUserFollowers, setSUserFollowers] = useState([]);
  const [sUserFollowing, setSUserFollowing] = useState([]);

  const sendOtp = async (email) => {
    try {
      const response = await fetch(`${ENDPOINT}/api/user/signup`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }),
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const verifyOTP = async (fullName, userName, email, password, Enterotp) => {
    try {
      const response = await fetch(`${ENDPOINT}/api/user/verfiyOtp`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ fullName, userName, email, password, Enterotp }),
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const addAdditionalInfo = async (picture, DOB, gender, userName) => {
    try {
      const response = await fetch(`${ENDPOINT}/api/user/addAdditionalInfo`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ picture, DOB, gender, userName }),
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const skipAdditionalInfo = async (picture, userName) => {
    try {
      const response = await fetch(`${ENDPOINT}/api/user/skipAdditionalInfo`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ picture, userName }),
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const chkUserNameExists = async (userName) => {
    try {
      const response = await fetch(`${ENDPOINT}/api/user/chkUNameExists`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ userName }),
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const login = async (email, password) => {
    try {
      const response = await fetch(`${ENDPOINT}/api/user/login`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const deleteUserAccount = async () => {
    try {
      const id = localStorage.getItem("id");
      const parsedId = JSON.parse(id);
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/user/deleteUserAccount/${parsedId}`,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const getBlockUsers = async () => {
    try {
      const id = localStorage.getItem("id");
      const parsedId = JSON.parse(id);
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/user/getBlockUsers/${parsedId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      setBlockUsers(data.blockUsers);
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const blockUnblockUser = async (blockUserId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/user/blockUser`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
        body: JSON.stringify({ blockUserId }),
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const showSuggestionFunc = async () => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/user/showSuggestion`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
      });
      const data = await response.json();
      setSuggestions(data.suggestion);
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const fetchAllreelsFunc = async () => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/post/fetchAllReels`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
      });
      const data = await response.json();
      setReels(data.reels);
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const fetchLikedPostsFunc = async () => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/post/fetchLikedPosts`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
      });
      const data = await response.json();
      setLikedPosts(data.likedPost);
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const searchUserFunc = async (keyword) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/user?search=${keyword}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
      });
      const data = await response.json();
      setSearchUsers(data.userData);
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const searchedUserAllDataFunc = async (id) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/user/fetchSearchedUserData/${id}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      setSearchedUsersAllData(data);
      setSUserPic(data.userCredentials.picture);
      setSUserFullName(data.userCredentials.fullName);
      setsUserName(data.userCredentials.userName);
      setSUserPosts(data.posts);
      setSUserFollowers(data.followersList.followersList);
      setSUserFollowing(data.followingList.followingList);
      localStorage.setItem(
        "sUserPic",
        JSON.stringify(data.userCredentials.picture)
      );
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  return (
    <VividlyContext.Provider
      value={{
        sendOtp,
        verifyOTP,
        addAdditionalInfo,
        skipAdditionalInfo,
        chkUserNameExists,
        login,
        deleteUserAccount,
        getBlockUsers,
        blockUsers,
        blockUnblockUser,
        showSuggestionFunc,
        suggestions,
        fetchAllreelsFunc,
        reels,
        fetchLikedPostsFunc,
        likedPosts,
        searchUserFunc,
        searchUsers,
        searchedUserAllDataFunc,
        searchedUsersAllData,
        sUserPic,
        sUserFullName,
        sUserName,
        sUserPosts,
        sUserFollowers,
        sUserFollowing,
      }}
    >
      {props.children}
    </VividlyContext.Provider>
  );
};

export default VividlyProvider;
