import React, { useContext, useEffect, useState } from "react";
import "./App.css";
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom";
import AuthPage from "./Components/AuthPage";
import Navbar from "./Components/Navbar";

import Sidebar from "./Components/Sidebar";
import Home from "./Components/Home";
import Groups from "./Components/Groups";
import Events from "./Components/Events";
import Marketplace from "./Components/Marketplace";
import Friends from "./Components/Friends";
import Explore from "./Components/Explore";
import Messages from "./Components/Messages";
import Notifications from "./Components/Notifications";
import Settings from "./Components/Settings";
import HelpCenter from "./Components/HelpCenter";
import Dashboard from "./Components/Dashboard";
import Subscriptions from "./Components/Subscriptions";
import Likes from "./Components/Likes";
import Profile from "./Components/Profile";
import Reels from "./Components/Reels";
import ViewBlockedUsers from "./Components/ViewBlockedUsers";
import SearchedUserProfile from "./Components/SearchedUserProfile";
import ConversationPage from "./Components/ConversationPage";
import GroupChatConversationPage from "./Components/GroupChatConversationPage";
import UserFollowingPage from "./Components/UserFollowingPage";
import UserFollowersPage from "./Components/UserFollowersPage";

function AuthChecker() {
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuth = () => {
      if (
        !localStorage.getItem("token") ||
        !localStorage.getItem("id") ||
        !localStorage.getItem("userName") ||
        !localStorage.getItem("theme")
      ) {
        navigate("/");
      }
    };
    const interval = setInterval(checkAuth, 1000);

    return () => clearInterval(interval);
  }, [navigate]);

  return null;
}

function App() {
  const [updateTheme, setUpdateTheme] = useState("");
  return (
    <BrowserRouter>
      <AuthChecker />
      <Routes>
        <Route exact path="/" element={<AuthPage />} />

        {/* Navbar Routes */}
        <Route
          exact
          path="/home"
          element={
            <>
              <Navbar />
              <Sidebar />
              <Home />
            </>
          }
        ></Route>
        <Route
          exact
          path="/explore"
          element={
            <>
              <Navbar />
              <Sidebar />
              <Explore />
            </>
          }
        ></Route>
        <Route
          exact
          path="/reels"
          element={
            <>
              <Navbar />
              <Sidebar />
              <Reels />
            </>
          }
        ></Route>
        <Route
          exact
          path="/messages"
          element={
            <>
              <Navbar />
              <Sidebar />
              <Messages />
            </>
          }
        ></Route>
        <Route
          exact
          path="/notifications"
          element={
            <>
