import React, { useState } from "react";
import Signup from "./Signup";
import Login from "./Login";
import { Scrollbars } from "react-custom-scrollbars";

const AuthPage = () => {
  const [activeTab, setActiveTab] = useState("tab1");

  return (
    <div className="authPageBG">
      <div className="min-h-screen flex justify-center items-center">
        <form className="border-2 max-h-[90vh] border-black bg-white min-w-[70vw] md:min-w-[40vw] rounded-lg p-5">
          <div className="flex justify-center mt-3">
            <h3 className="custom-font text-4xl text-black font-bold">
              Vividly
            </h3>
          </div>
          <div role="tablist" className="tabs mt-7 flex">
            <button
              type="button"
              role="tab"
              className={`tab w-1/2 h-10 text-center text-xl rounded-md ${
                activeTab === "tab1"
                  ? "bg-blue-500 text-white"
                  : "bg-gray-200 text-gray-700"
              }`}
              aria-selected={activeTab === "tab1"}
              onClick={() => setActiveTab("tab1")}
            >
              Signup
            </button>
            <button
              type="button"
              role="tab"
              className={`tab w-1/2 h-10 text-center text-xl rounded-md ${
                activeTab === "tab2"
                  ? "bg-blue-500 text-white"
                  : "bg-gray-200 text-gray-700"
              }`}
              aria-selected={activeTab === "tab2"}
              onClick={() => setActiveTab("tab2")}
            >
              Login
            </button>
          </div>

          {/* Wrap the content inside Scrollbars to make it scrollable */}
          <div className="mt-5">
            <Scrollbars
              autoHide
              style={{ maxHeight: "calc(90vh - 200px)" }} // Adjust this value to suit your layout
              renderThumbVertical={(props) => (
                <div
                  {...props}
                  style={{ backgroundColor: "red", borderRadius: "6px" }}
                />
              )}
            >
              {activeTab === "tab1" && (
                <div role="tabpanel">
                  <Signup />
                </div>
              )}
              {activeTab === "tab2" && (
                <div role="tabpanel">
                  <Login />
                </div>
              )}
            </Scrollbars>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AuthPage;
