import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const sidebarRef = useRef(null);

  // Function to handle opening and closing of the sidebar
  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Handle ESC key to close sidebar
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === "Escape" && sidebarOpen) {
        setSidebarOpen(false);
      }
    };

    // Add event listener for ESC key
    document.addEventListener("keydown", handleKeyDown);

    // Cleanup event listener on component unmount
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [sidebarOpen]);

  // Handle click outside the sidebar to close it
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
        setSidebarOpen(false); // Close the sidebar if click is outside
      }
    };

    // Add event listener to handle clicks outside the sidebar
    document.addEventListener("mousedown", handleClickOutside);

    // Cleanup event listener on component unmount
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <>
      <button
        className="sm:hidden pt-20 pl-5"
        aria-label="Open Sidebar"
        onClick={handleSidebarToggle}
      >
        <FontAwesomeIcon icon="bars" className="text-2xl" />
      </button>
      <aside className="hidden sm:block sm:w-64 h-screen bg-gray-100 border-r fixed top-0 left-0 pt-20">
        <ul className="space-y-7 p-2 sm:p-6">
          <Link to="/profile" className="block">
            <SidebarItem icon="user-circle" text="Profile" path="/profile" />
          </Link>
          <Link to="/dashboard" className="block">
            <SidebarItem
              icon="tachometer-alt"
              text="Dashboard"
              path="/dashboard"
            />
          </Link>
          <Link to="/groups" className="block">
            <SidebarItem icon="users" text="Groups" path="/groups" />
          </Link>
          <Link to="/friends" className="block">
            <SidebarItem icon="user" text="Friends" path="/friends" />
          </Link>
          <Link to="/events" className="block">
            <SidebarItem icon="calendar" text="Events" path="/events" />
          </Link>
          <Link to="/marketplace" className="block">
            <SidebarItem
              icon="shopping-bag"
              text="Marketplace"
              path="/marketplace"
            />
          </Link>

          <Link to="/subscriptions" className="block">
            <SidebarItem
              icon="credit-card"
              text="Subscriptions"
              path="/subscriptions"
            />
          </Link>
          <Link to="/likes" className="block">
            <SidebarItem icon="thumbs-up" text="Liked Posts" path="/likes" />
          </Link>
        </ul>
      </aside>

      {/* Sidebar */}
      <div
        ref={sidebarRef}
        className={`fixed inset-0 w-64 bg-gray-800 bg-opacity-50 z-40 sm:translate-x-0 sm:block transition-transform duration-300 transform ${
          sidebarOpen ? "translate-x-0" : "-translate-x-full"
        }`}
        aria-label="Sidebar Navigation"
        role="navigation"
        aria-hidden={!sidebarOpen} // Use aria-hidden to show/hide
      >
        <div className="w-64 bg-white h-full shadow-lg fixed z-50 p-6">
          <div className="flex justify-between items-center">
            {/* Sidebar Header */}
            <div className="text-xl font-bold" id="sidebar">
              Vividly
            </div>
            <button
              className="text-gray-500 sm:hidden"
              onClick={handleSidebarToggle}
              aria-label="Close Sidebar"
            >
              <FontAwesomeIcon icon="times" />
            </button>
          </div>

          {/* Sidebar Links */}
          <ul className="space-y-4 mt-6">
            <li>
              <Link
                to="/home"
                className="text-gray-700 hover:text-blue-500"
                onClick={handleSidebarToggle}
                aria-label="Go to Home"
              >
                Home
              </Link>
            </li>
            <li>
              <Link
                to="/explore"
                className="text-gray-700 hover:text-blue-500"
                onClick={handleSidebarToggle}
                aria-label="Go to Explore"
              >
                Explore
              </Link>
            </li>
            <li>
              <Link
                to="/groups"
                className="text-gray-700 hover:text-blue-500"
                onClick={handleSidebarToggle}
                aria-label="Go to Groups"
              >
                Groups
              </Link>
            </li>
            <li>
              <Link
                to="/events"
                className="text-gray-700 hover:text-blue-500"
                onClick={handleSidebarToggle}
                aria-label="Go to Events"
              >
                Events
              </Link>
            </li>
            <li>
              <Link
                to="/marketplace"
                className="text-gray-700 hover:text-blue-500"
                onClick={handleSidebarToggle}
                aria-label="Go to Marketplace"
              >
                Marketplace
              </Link>
            </li>
            <li>
              <Link
                to="/profile"
                className="text-gray-700 hover:text-blue-500"
                onClick={handleSidebarToggle}
                aria-label="Go to Profile"
              >
                Profile
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
};

const SidebarItem = ({ icon, text, path }) => {
  const location = useLocation();
  const isActive = location.pathname === path;

  return (
    <li
      className={`flex items-center space-x-3 ${
        isActive ? "activeLink" : "text-gray-700"
      } hover:text-blue-500 cursor-pointer`}
    >
      <FontAwesomeIcon icon={icon} className="w-5 h-5" />
      <span>{text}</span>
    </li>
  );
};

export default Sidebar;
