import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const ProfileDropdown = () => {
  const [open, setOpen] = useState(false);

  return (
    <div className="relative">
      <div
        className="w-8 h-8 rounded-full bg-gray-300 cursor-pointer"
        onClick={() => setOpen(!open)}
      ></div>

      {open && (
        <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg z-50">
          <ul>
            <DropdownItem icon="cog" text="Settings" />
            <DropdownItem icon="question-circle" text="Help Center" />
            <DropdownItem icon="sign-out-alt" text="Logout" />
          </ul>
        </div>
      )}
    </div>
  );
};

const DropdownItem = ({ icon, text }) => (
  <li className="flex items-center space-x-2 p-2 hover:bg-gray-100 cursor-pointer">
    <FontAwesomeIcon icon={icon} className="w-5 h-5" />
    <span>{text}</span>
  </li>
);

export default ProfileDropdown;
