import React, { useState, useEffect, useContext } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ProfileDropdown from "./ProfileDropdown";
import { Link, useLocation } from "react-router-dom";
import {
  faHome,
  faSearch,
  faBell,
  faComments,
  faPlayCircle,
} from "@fortawesome/free-solid-svg-icons";
import vividlyContext from "../Context/vividlyContext";
import io from "socket.io-client";

// Socket connection
const socket = io("http://localhost:5000"); // Adjust the URL as per your backend

const Navbar = () => {
  const [unreadCountMessage, setUnreadCountMessage] = useState(0);
  const [unReadNotifications, setUnreadNotifications] = useState(0);
  const context = useContext(vividlyContext);

  useEffect(() => {
    // Initialize socket connection on component mount
    socket.on("receive_notification", (data) => {
      // Update the unread notifications count
      setUnreadNotifications((prevCount) => prevCount + 1);
    });

    socket.on("receive_message", (data) => {
      // Update the unread message count
      setUnreadCountMessage((prevCount) => prevCount + 1);
    });

    // Cleanup on unmount
    return () => {
      socket.off("receive_notification");
      socket.off("receive_message");
    };
  }, []);

  useEffect(() => {
    // Call this to fetch unread notifications from the backend
    const fetchUnreadNotifications = async () => {
      try {
        const token = localStorage.getItem("token");
        const parsedToken = JSON.parse(token);
        const response = await fetch(
          `${ENDPOINT}/api/fetchUnreadNotifications`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "auth-token": parsedToken,
            },
          }
        );
        const data = await response.json();
        setUnreadNotifications(data.fetchNotifications.length);
      } catch (error) {
        console.log("Error:", error);
      }
    };

    fetchUnreadNotifications();
  }, []);

  return (
    <nav className="w-full bg-white border-b shadow-md fixed top-0 z-50">
      <div className="max-w-7xl mx-auto flex justify-between items-center p-4">
        <div className="smd:text-xl sm:text-3xl custom-font font-bold text-blue-500">
          Vividly
        </div>

        <div className="flex esmd:space-x-3 elmd:space-x-6">
          <Link to="/home" className="block">
            <NavItem icon={faHome} text="Home" path="/home" />
          </Link>
          <Link to="/explore" className="block">
            <NavItem icon={faSearch} text="Explore" path="/explore" />
          </Link>
          <Link to="/reels" className="block">
            <NavItem icon={faPlayCircle} text="Reels" path="/reels" />
          </Link>
          <Link to="/messages" className="block relative">
            <NavItem
              icon={faComments}
              text="Messages"
              path="/messages"
              unreadCountMessage={unreadCountMessage}
            />
          </Link>
          <Link to="/notifications" className="block relative">
            <NavItem
              icon={faBell}
              text="Notifications"
              path="/notifications"
              unReadNotifications={unReadNotifications}
            />
          </Link>
        </div>

        <div>
          <ProfileDropdown />
        </div>
      </div>
    </nav>
  );
};

const NavItem = ({
  icon,
  text,
  path,
  unReadNotifications,
  unreadCountMessage,
}) => {
  const location = useLocation();
  const isActive = location.pathname === path;

  return (
    <div
      className={`flex flex-col items-center ${
        isActive ? "activeLink" : "text-gray-600"
      } hover:text-blue-500 cursor-pointer`}
    >
      <FontAwesomeIcon
        icon={icon}
        className="esmd:w-3 esmd:h-3 lmd: w-5 lmd: h-5 elmd:w-6 elmd:h-6"
      />
      <span className="text-[10px] lmd:text-xs">{text}</span>

      {unReadNotifications > 0 && (
        <span className="absolute top-0 right-0 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
          {unReadNotifications}
        </span>
      )}
      {unreadCountMessage > 0 && (
        <span className="absolute top-0 right-[-4px] bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
          {unreadCountMessage}
        </span>
      )}
    </div>
  );
};

export default Navbar;
