import React, { useEffect, useState } from "react";

const UserFollowingPage = () => {
  const [following, setFollowing] = useState([]);
  const [loading, setLoading] = useState(true);

  // Simulated fetch for following users
  useEffect(() => {
    // Simulated API response
    setTimeout(() => {
      setFollowing([
        {
          id: 1,
          name: "Alice Johnson",
          username: "@alicej",
          profilePic: "https://via.placeholder.com/50",
        },
        {
          id: 2,
          name: "John Doe",
          username: "@johndoe",
          profilePic: "https://via.placeholder.com/50",
        },
        {
          id: 3,
          name: "Emma Watson",
          username: "@emmaw",
          profilePic: "https://via.placeholder.com/50",
        },
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const handleUnfollow = (id) => {
    setFollowing(following.filter((user) => user.id !== id));
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="text-xl font-bold">Loading...</div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto min-h-screen sm:pl-72 sm:pt-24">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Following</h1>
      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
        {following.map((user) => (
          <div
            key={user.id}
            className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center space-y-3"
          >
            <img
              src={user.profilePic}
              alt={`${user.name}'s profile`}
              className="w-16 h-16 rounded-full object-cover"
            />
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-700">
                {user.name}
              </h3>
              <p className="text-sm text-gray-500">{user.username}</p>
            </div>
            <button
              onClick={() => handleUnfollow(user.id)}
              className="px-4 py-2 bg-red-500 text-white font-semibold rounded-lg shadow hover:bg-red-600 transition"
            >
              Unfollow
            </button>
          </div>
        ))}
      </div>
      {following.length === 0 && (
        <div className="text-center mt-10 text-gray-600 text-lg">
          You are not following anyone at the moment.
        </div>
      )}
    </div>
  );
};

export default UserFollowingPage;
