import React from "react";

const HelpCenter = () => {
  const [faqs, setFaqs] = useState([
    {
      question: "How do I reset my password?",
      answer:
        "Go to Account Settings and click 'Reset Password' to set a new password.",
      open: false,
    },
    {
      question: "How can I delete my account?",
      answer:
        "Visit the 'Privacy' section in settings and click 'Delete Account.'",
      open: false,
    },
    {
      question: "How do I report a bug?",
      answer:
        "Use the 'Report a Problem' form in the Help Center to describe the issue.",
      open: false,
    },
  ]);

  const toggleFaq = (index) => {
    setFaqs(
      faqs.map((faq, i) =>
        i === index ? { ...faq, open: !faq.open } : { ...faq, open: false }
      )
    );
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 max-w-8xl mx-auto p-6 bg-white rounded-lg shadow-md">
      <h1 className="text-3xl font-bold mb-6 text-center">Help Center</h1>

      {/* Search Bar */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search for help topics..."
          className="w-full p-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      {/* FAQ Section */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-4">
          Frequently Asked Questions
        </h2>
        {faqs.map((faq, index) => (
          <div key={index} className="mb-4">
            <button
              onClick={() => toggleFaq(index)}
              className="w-full text-left p-4 bg-gray-100 rounded-lg shadow-md focus:outline-none"
            >
              <div className="flex justify-between items-center">
                <span className="text-gray-800 font-medium">
                  {faq.question}
                </span>
                <span>{faq.open ? "▲" : "▼"}</span>
              </div>
            </button>
            {faq.open && (
              <div className="mt-2 text-gray-600 p-4 bg-gray-50 rounded-lg">
                {faq.answer}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Categories Section */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-4">Help Topics</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 gap-4">
          {["Account Settings", "Privacy", "Technical Issues", "Payments"].map(
            (category, index) => (
              <button
                key={index}
                className="p-4 bg-blue-100 text-blue-800 rounded-lg shadow hover:bg-blue-200"
              >
                {category}
              </button>
            )
          )}
        </div>
      </div>

      {/* Contact Support */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-4">Need More Help?</h2>
        <div className="space-y-4">
          <button className="w-full p-4 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600">
            Contact Support
          </button>
          <button className="w-full p-4 bg-gray-100 text-gray-800 rounded-lg shadow hover:bg-gray-200">
            Report a Problem
          </button>
        </div>
      </div>

      {/* Feedback Form */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Submit Feedback</h2>
        <textarea
          placeholder="Share your feedback or describe your issue..."
          className="w-full p-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          rows="4"
        ></textarea>
        <button className="w-full p-4 bg-green-500 text-white rounded-lg shadow hover:bg-green-600">
          Submit
        </button>
      </div>
    </div>
  );
};

export default HelpCenter;
