import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import { io } from "socket.io-client";

const ConversationPage = () => {
  const context = useContext(vividlyContext);
  const { handleFetchConversation, sendMessage, handleFetchConvUserData } =
    context;
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [socket, setSocket] = useState(null);
  const [error, setError] = useState("");

  const searchedUserId = JSON.parse(localStorage.getItem("sUserId"));
  const userId = JSON.parse(localStorage.getItem("id"));

  useEffect(() => {
    const newSocket = io("http://localhost:5000");
    setSocket(newSocket);

    // Join the socket room with the user ID
    newSocket.emit("join", userId);

    // Listen for new messages
    newSocket.on("receiveMessage", (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      newSocket.disconnect();
    };
  }, [userId]);

  const handleFetchConversationFun = async () => {
    const response = await handleFetchConversation(searchedUserId);
    if (response.success) {
      setMessages(response.data.messages);
    } else {
      setError("No conversation started yet!");
    }
  };

  const handleSendMessage = async () => {
    if (newMessage.trim()) {
      const messageData = {
        senderId: userId,
        receiverId: searchedUserId,
        text: newMessage,
      };

      // Emit the message to the server
      socket.emit("sendMessage", messageData);

      // Update local state
      setMessages((prevMessages) => [...prevMessages, messageData]);
      setNewMessage("");
    }
  };

  const formatDate = (timestamp) => {
    if (!timestamp) {
      return "Invalid date"; // Handle cases where timestamp is undefined or null
    }

    const date = new Date(timestamp); // Convert timestamp to Date object

    if (isNaN(date.getTime())) {
      return "Invalid date"; // Check if the date is valid
    }

    const now = new Date();

    // Check if the date is today
    if (date.toDateString() === now.toDateString()) {
      return new Intl.DateTimeFormat("en-US", {
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    } else {
      // Return full date if it's not today
      return new Intl.DateTimeFormat("en-US", {
        month: "short",
        day: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    }
  };

  useEffect(() => {
    handleFetchConversation();
    handleFetchConversationFun();
  }, []);

  return (
    <div className="max-w-7xl mx-auto h-screen sm:pl-72 sm:pt-24 p-4 flex flex-col bg-gray-50">
      {/* Chat Header */}
      <div className="bg-blue-600 text-white p-4 shadow-md flex items-center justify-between rounded-md">
        <h2 className="text-xl font-semibold">Conversation</h2>
        <div className="flex items-center space-x-2">
          {/* User Avatar */}
          <span className="font-semibold">User Chat</span>
        </div>
      </div>

      {/* Messages Section */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-100">
        {messages.length > 0 ? (
          messages.map((msg, index) => (
            <div
              key={index}
              className={`flex ${
                msg.senderId === userId ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs p-3 rounded-lg ${
                  msg.senderId === userId
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200 text-gray-800"
                } shadow-md`}
              >
                <p className="text-sm">{msg.text}</p>
                <span
                  className={`text-xs ${
                    msg.senderId === userId ? "text-gray-300" : "text-gray-700"
                  }`}
                >
                  {formatDate(msg.createdAt)}
                </span>
              </div>
            </div>
          ))
        ) : (
          <div className="flex justify-center h-[45vh] items-center text-gray-700 custom-font text-2xl">
            No conversation started yet!
          </div>
        )}
      </div>

      {/* Message Input Section */}
      <div className="bg-white p-4 border-t border-gray-300 flex items-center">
        <input
          type="text"
          placeholder="Type a message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="w-full p-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 text-black"
        />
        <button
          onClick={handleSendMessage}
          className="ml-3 p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ConversationPage;
