import React, { useContext, useEffect, useState } from "react";
import Signup from "./Signup";
import Login from "./Login";
import OTPVerification from "./OTPVerification";
import AditionlaInfoSignup from "./AditionlaInfoSignup";
import vividlyContext from "../Context/vividlyContext";
import Alert from "./Alert";

const AuthPage = () => {
  const [activeTab, setActiveTab] = useState("tab1");
  const [websiteName, setWebsiteName] = useState("");

  const [alert, setAlert] = useState(null);
  const showAlert = (type, msg) => {
    setAlert({
      type: type,
      msg: msg,
    });
    setTimeout(() => {
      setAlert("");
    }, 2000);
  };

  const [userInfo, setUserInfo] = useState({
    fullName: "",
    userName: "",
    email: "",
    password: "",
  });

  const context = useContext(vividlyContext);
  const { sendOtp } = context;

  const [step, setStep] = useState(1);
  // const handleUpdateStep = () => {
  //   setStep(1);
  // };

  useEffect(() => {
    const name = process.env.REACT_APP_WEBSITE_NAME;
    let index = 0;
    let direction = 1;

    setInterval(() => {
      setWebsiteName(name.slice(0, index));
      index += direction;

      if (index > name.length) {
        direction = -1;
        index = name.length;
      } else if (index < 0) {
        direction = 1;
        index = 0;
      }
    }, 300);
  }, []);

  return (
    <>
      <Alert alert={alert} />
      <div className="authPageBG">
        <div className="min-h-screen flex justify-center items-center">
          <form className="border-2 smd:w-[90vw] sm:w-[70vw] md:w-[55vw] lg:w-[40vw] max-h-[90vh] overflow-y-auto border-black bg-white rounded-lg p-5 boxShadow">
            <div className="flex justify-center mt-3 h-12">
              <h3 className="custom-font text-4xl text-black font-bold">
                {websiteName}
              </h3>
            </div>
            {step === 1 && (
              <>
                <div role="tablist" className="tabs mt-4 flex">
                  <button
                    type="button"
                    role="tab"
                    className={`tab w-1/2 h-9 text-center text-xl rounded-md ${
                      activeTab === "tab1"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                    aria-selected={activeTab === "tab1"}
                    onClick={() => setActiveTab("tab1")}
                  >
                    Signup
                  </button>
                  <button
                    type="button"
                    role="tab"
                    className={`tab w-1/2 h-9 text-center text-xl rounded-md ${
                      activeTab === "tab2"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                    aria-selected={activeTab === "tab2"}
                    onClick={() => setActiveTab("tab2")}
                  >
                    Login
                  </button>
                </div>
                <div className="mt-5">
                  {activeTab === "tab1" && (
                    <div role="tabpanel">
                      <Signup
                        userInfo={userInfo}
                        setUserInfo={setUserInfo}
                        sendOtp={sendOtp}
                        step={step}
                        setStep={setStep}
                        showAlert={showAlert}
                      />
                    </div>
                  )}
                  {activeTab === "tab2" && (
                    <div role="tabpanel">
                      <Login />
                    </div>
                  )}
                </div>
              </>
            )}
            {step === 2 && (
              <>
                <div className="mt-5">
                  <h4 className="text-2xl font-semibold text-black mb-2">
                    Verify Your OTP
                  </h4>
                  <p className="text-gray-600 mb-4">
                    Please enter the 6-digit OTP sent to your registered email.
                  </p>

                  <OTPVerification
                    userInfo={userInfo}
                    step={step}
                    setStep={setStep}
                    showAlert={showAlert}
                  />
                </div>
              </>
            )}
            {step === 3 && (
              <div className="mt-5">
                <h4 className="text-xl font-semibold text-black">
                  Add your additional info
                </h4>
                <AditionlaInfoSignup
                  userInfo={userInfo}
                  showAlert={showAlert}
                />
              </div>
            )}
          </form>
        </div>
      </div>
    </>
  );
};

export default AuthPage;
