import React, { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart,
  faComment,
  faShareSquare,
} from "@fortawesome/free-regular-svg-icons";
import vividlyContext from "../Context/vividlyContext";

const Explore = () => {
  const context = useContext(vividlyContext);
  const { searchUserFunc, searchUsers } = context;

  const [keyword, setKeyword] = useState("");
  const [show, setShow] = useState(true);

  const [posts, setPosts] = useState([
    {
      id: 1,
      user: "John Doe",
      postImage: "https://via.placeholder.com/400x400?text=Post+1",
      description: "Exploring the beauty of nature!",
      likes: 100,
      comments: 30,
      shares: 12,
    },
    {
      id: 2,
      user: "Jane Smith",
      postImage: "https://via.placeholder.com/400x400?text=Post+2",
      description: "A new tech gadget I love!",
      likes: 85,
      comments: 18,
      shares: 7,
    },
    {
      id: 3,
      user: "Mark Johnson",
      postImage: "https://via.placeholder.com/400x400?text=Post+3",
      description: "Fitness journey - day 1!",
      likes: 50,
      comments: 12,
      shares: 3,
    },
    // Add more posts here...
  ]);
  const handleOnchange = (e) => {
    searchUserFunc(e.target.value);
    setKeyword(e.target.value);
    if (keyword.length >= 0) {
      setShow(false);
    } else {
      setShow(true);
    }
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-6">
      {/* Search Section */}
      {console.log(searchUsers)}
      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-3xl font-bold">Explore</h1>
        <div className="mt-4">
          <input
            type="text"
            value={keyword}
            onChange={handleOnchange}
            placeholder="Search for posts, people, or hashtags"
            className="w-full px-4 py-2 rounded-lg shadow-md bg-white text-gray-800"
          />
        </div>
      </div>

      {/* Trending Section */}
      {/* <div className="mb-8">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Trending</h2>
        <div className="flex space-x-6 overflow-x-auto">
          {["#Nature", "#Tech", "#Fitness", "#Travel", "#Food"].map(
            (tag, index) => (
              <div
                key={index}
                className="bg-gray-200 px-4 py-2 rounded-full text-sm text-gray-700 hover:bg-blue-500 hover:text-white cursor-pointer"
              >
                {tag}
              </div>
            )
          )}
        </div>
      </div> */}

      {show && (
        <>
          {" "}
          {/* Recommended Posts Section */}
          <div className="space-y-8">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">
              Recommended For You
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3  gap-6">
              {posts.map((post) => (
                <div
                  key={post.id}
                  className="bg-white rounded-lg shadow-md overflow-hidden"
                >
                  {/* Post Image */}
                  <img
                    src={post.postImage}
                    alt={post.description}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-4">
                    {/* User Info */}
                    <div className="flex items-center space-x-4">
                      <img
                        src={`https://via.placeholder.com/40?text=${post.user}`}
                        alt={post.user}
                        className="w-10 h-10 rounded-full object-cover"
                      />
                      <div>
                        <p className="font-semibold text-gray-800">
                          {post.user}
                        </p>
                      </div>
                    </div>
                    {/* Post Description */}
                    <p className="mt-2 text-gray-600">{post.description}</p>

                    {/* Interaction buttons */}
                    <div className="flex items-center space-x-8 mt-4">
                      <button className="flex items-center text-gray-600 hover:text-blue-500">
                        <div className="flex">
                          <div>
                            <FontAwesomeIcon icon={faHeart} />
                          </div>
                          <div className="w-10">{post.likes}</div>
                        </div>
                      </button>
                      <button className="flex items-center text-gray-600 hover:text-blue-500">
                        <div className="flex">
                          <div>
                            <FontAwesomeIcon icon={faComment} />
                          </div>
                          <div className="w-10">{post.comments}</div>
                        </div>
                      </button>
                      <button className="flex items-center text-gray-600 hover:text-blue-500">
                        <div className="flex">
                          <div>
                            <FontAwesomeIcon icon={faShareSquare} />
                          </div>
                          <div className="w-20"> {post.shares} Shares</div>
                        </div>
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          {/* Infinite Scroll or Load More */}
          <div className="mt-8 text-center">
            <button className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
              Load More
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Explore;
