import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link, useLocation } from "react-router-dom";

const ProfileDropdown = () => {
  const [open, setOpen] = useState(false);

  return (
    <div className="relative">
      <div onClick={() => setOpen(!open)}>
        <FontAwesomeIcon
          icon="user"
          className="text-gray-700 w-6 h-6 cursor-pointer"
        />
      </div>

      {open && (
        <div className="absolute right-0 mt-2 w-48 bg-white text-black rounded-md shadow-lg z-50">
          <ul>
            <Link to="/settings">
              <DropdownItem icon="cog" text="Settings" />{" "}
            </Link>

            <DropdownItem icon="question-circle" text="Help Center" />
            <DropdownItem icon="sign-out-alt" text="Logout" />
          </ul>
        </div>
      )}
    </div>
  );
};

const DropdownItem = ({ icon, text }) => (
  <li className="flex items-center space-x-2 p-2 hover:bg-blue-500 hover:text-white cursor-pointer">
    <FontAwesomeIcon icon={icon} className="w-5 h-5" />
    <span>{text}</span>
  </li>
);

export default ProfileDropdown;
