import { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faInfoCircle, faCog } from "@fortawesome/free-solid-svg-icons";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";

const GroupChatConversationPage = () => {
  const context = useContext(vividlyContext);
  const { handleFetchGroupData } = context;

  const [groupData, setGroupData] = useState("");
  const groupId = localStorage.getItem("groudId");
  const parsedGroupId = JSON.parse(groupId);

  const fetchGroupDataFunc = async () => {
    const response = await handleFetchGroupData(parsedGroupId);
    if (response.success) {
      setGroupData(response.data);
    }
  };

  useEffect(() => {
    fetchGroupDataFunc();
  }, []);

  // Dummy data for testing
  const groupName = "Tech Innovators";
  const groupImage = "https://via.placeholder.com/50";
  const initialMessages = [
    {
      userName: "ahmad123",
      text: "Hey everyone, how's it going?",
      isUser: true,
    },
    {
      userName: "abdullah123",
      text: "All good! How about you?",
      isUser: false,
    },
    {
      userName: "asad123",
      text: "Excited about the new project!",
      isUser: false,
    },
    {
      userName: "ahmad123",
      text: "Same here! Let's make it great.",
      isUser: true,
    },
    {
      userName: "ahmad123",
      text: "Same here! Let's make it great.",
      isUser: true,
    },
    {
      userName: "ahmad123",
      text: "Same here! Let's make it great.",
      isUser: true,
    },
    {
      userName: "ahmad123",
      text: "Same here! Let's make it great.",
      isUser: true,
    },
    {
      userName: "ahmad123",
      text: "Same here! Let's make it great.",
      isUser: true,
    },
  ];

  // Dummy function to simulate sending a message
  const onSendMessage = (newMessage) => {
    console.log("New message sent: ", newMessage);
    // Here you would typically add the new message to the messages state
  };

  const [messages, setMessages] = useState(initialMessages);
  const [newMessage, setNewMessage] = useState("");

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      // Simulate adding the new message to the messages list
      const newMessageObject = {
        userName: "ahmad123",
        text: newMessage,
        isUser: true,
      };
      setMessages([...messages, newMessageObject]);
      onSendMessage(newMessage);
      setNewMessage("");
    }
  };

  return (
    <div className="max-w-7xl mx-auto  sm:pl-72 sm:pt-24 p-4 flex flex-col bg-gray-50">
      <div className="flex flex-col h-[80vh] bg-gray-50">
        {/* Header Section */}
        <div className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white p-5 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            {/* Group Image */}
            <img
              src={groupData.groupPicture || Avatar}
              alt="Group Logo"
              className="w-12 h-12 rounded-full object-cover"
            />
            {/* Group Name */}
            <div className="text-3xl font-semibold">{groupData.groupName}</div>
          </div>
          <div className="flex items-center space-x-4">
            <button className="bg-gray-800 text-white rounded-full p-3 hover:bg-gray-700 transition duration-300">
              <FontAwesomeIcon icon={faInfoCircle} className="text-lg" />
            </button>
            <button className="bg-gray-800 text-white rounded-full p-3 hover:bg-gray-700 transition duration-300">
              <FontAwesomeIcon icon={faCog} className="text-lg" />
            </button>
          </div>
        </div>

        {/* Chat Messages Section */}
        <div className="flex-grow overflow-y-auto p-6 space-y-4">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`flex ${msg.isUser ? "justify-end" : "justify-start"}`}
            >
              <div
                className={`max-w-xs p-4 rounded-lg text-white ${
                  msg.isUser ? "bg-blue-600" : "bg-gray-700"
                }`}
              >
                <div className="font-medium">{msg.userName}</div>
                <p className="mt-1">{msg.text}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Message Input Section */}
        <div className="bg-white p-4 border-t border-gray-200">
          <div className="flex items-center space-x-4">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              className="flex-grow p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              placeholder="Type a message..."
            />
            <button
              onClick={handleSendMessage}
              className="bg-indigo-600 text-white rounded-full p-3 hover:bg-indigo-700 transition duration-300"
            >
              <span className="material-icons">send</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GroupChatConversationPage;
