import React, { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart,
  faComment,
  faShareSquare,
} from "@fortawesome/free-regular-svg-icons";
import vividlyContext from "../Context/vividlyContext";

const Reels = () => {
  const context = useContext(vividlyContext);
  const { fetchAllreelsFunc, reels } = context;

  useEffect(() => {
    fetchAllreelsFunc();
  }, []);

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-4 sm:p-6">
      {/* Header Section */}
      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-3xl font-bold">Reels</h1>
        <div className="mt-4">
          <input
            type="text"
            placeholder="Search Reels"
            className="w-full px-4 py-2 rounded-lg shadow-md bg-white text-gray-800"
          />
        </div>
      </div>

      {/* Reels Feed */}
      <div className="space-y-8">
        {reels.map((reel) => (
          <div
            key={reel._id}
            className="relative bg-white rounded-lg shadow-md overflow-hidden"
          >
            {/* Reel Video */}
            <div className="relative">
              <video
                className="w-full h-auto"
                src={reel.media}
                controls
                autoPlay
                muted
                loop
              />
            </div>

            {/* Reel Info */}
            <div className="p-4">
              <div className="flex items-center space-x-4">
                <img
                  src={reel.userId.picture}
                  alt="Failed to load user image!"
                  className="w-12 h-12 rounded-full object-cover"
                />
                <div>
                  <p className="font-semibold text-gray-800">aa</p>
                  <p className="text-sm text-gray-600">{reel.description}</p>
                </div>
              </div>

              {/* Interaction buttons */}
              <div className="flex items-center space-x-8 mt-4">
                <button className="flex items-center text-gray-600 hover:text-blue-500">
                  <div className="flex">
                    <div>
                      <FontAwesomeIcon icon={faHeart} />
                    </div>
                    <div className="w-10">{reel.likes}</div>
                  </div>
                </button>
                <button className="flex items-center text-gray-600 hover:text-blue-500">
                  <div className="flex">
                    <div>
                      <FontAwesomeIcon icon={faComment} />
                    </div>
                    <div className="w-10">{reel.comments}</div>
                  </div>
                </button>
                <button className="flex items-center text-gray-600 hover:text-blue-500">
                  <div className="flex">
                    <div>
                      <FontAwesomeIcon icon={faShareSquare} />
                    </div>
                    <div className="w-20"> {reel.shares} Shares</div>
                  </div>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reels;
