import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";

const Notifications = () => {
  const context = useContext(vividlyContext);
  const { handleNotifications } = context;
  const [notifications, setNotifications] = useState([]);
  const fetchAllNotificaions = async () => {
    const response = await handleNotifications();
    if (response.success) {
      setNotifications(response.fetchNotifications);
    }
  };
  useEffect(() => {
    fetchAllNotificaions();
  });

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();

    if (date.toDateString() === now.toDateString()) {
      return new Intl.DateTimeFormat("en-US", {
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    } else {
      return new Intl.DateTimeFormat("en-US", {
        month: "short",
        day: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    }
  };

  const markAllAsRead = () => {
    setNotifications((prevNotifications) =>
      prevNotifications.map((notification) => ({
        ...notification,
        isRead: true,
      }))
    );
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-6">
      {/* Header */}
      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-3xl font-bold">Notifications</h1>
        <p className="mt-2 text-lg">
          Stay updated with your activity on the platform.
        </p>
      </div>

      {/* Notification List */}
      <div className="space-y-4">
        {notifications.map((notification) => (
          <div
            key={notification.id}
            className={`flex items-center p-4 rounded-lg shadow-md ${
              notification.isRead ? "bg-gray-100" : "bg-blue-100"
            } hover:bg-blue-200 transition duration-200`}
          >
            <div className="flex-shrink-0">
              {notification.type === "like" && (
                <i className="fas fa-heart text-red-500 text-xl"></i>
              )}
              {notification.type === "comment" && (
                <i className="fas fa-comment text-blue-500 text-xl"></i>
              )}
              {notification.type === "message" && (
                <i className="fas fa-user-plus text-black-500 text-xl"></i>
              )}
            </div>
            <div className="ml-4 flex-1">
              <p className="font-semibold text-gray-800">
                {notification.content}
              </p>
              <span className="text-sm text-gray-500">
                {formatDate(notification.createdAt)}
              </span>
            </div>
          </div>
        ))}
      </div>

      {/* Empty State */}
      {notifications.length === 0 && (
        <div className="text-center bg-gray-100 p-6 rounded-lg shadow-md">
          <p className="text-xl text-gray-500">
            You have no new notifications.
          </p>
          <p className="mt-2 text-lg text-gray-700">Stay tuned for updates!</p>
        </div>
      )}

      {/* Clear All Button */}
      <div className="mt-6 text-center">
        <button
          onClick={markAllAsRead}
          className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600"
        >
          Mark All as Read
        </button>
      </div>
    </div>
  );
};

export default Notifications;
