import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";

const Friends = () => {
  const context = useContext(vividlyContext);
  const {
    showSuggestionFunc,
    suggestions,
    handleFetcHUserFriends,
    userFriends,
  } = context;
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const [friends, setFriends] = useState([]);
  const [suggestedFriends, setSuggestedFriends] = useState([]);
  const [friendRequests, setFriendRequests] = useState([]);
  const friendsData = [
    {
      id: 1,
      name: "Alice Smith",
      username: "alice_smith",
      photo: "https://via.placeholder.com/100",
    },
    {
      id: 2,
      name: "Bob Johnson",
      username: "bob_johnson",
      photo: "https://via.placeholder.com/100",
    },
    {
      id: 3,
      name: "Charlie Lee",
      username: "charlie_lee",
      photo: "https://via.placeholder.com/100",
    },
  ];

  const suggestedFriendsData = [
    {
      id: 4,
      name: "David Brown",
      username: "david_brown",
      photo: "https://via.placeholder.com/100",
    },
    {
      id: 5,
      name: "Eve White",
      username: "eve_white",
      photo: "https://via.placeholder.com/100",
    },
  ];

  const FriendRequestsData = [
    {
      id: 6,
      name: "Frank Green",
      username: "frank_green",
      photo: "https://via.placeholder.com/100",
    },
    {
      id: 7,
      name: "Asad",
      username: "frank_green",
      photo: "https://via.placeholder.com/100",
    },
  ];
  useEffect(() => {
    showSuggestionFunc();
    handleFetcHUserFriends();
  }, []);
  useEffect(() => {
    setFriends(friendsData);
    setSuggestedFriends(suggestedFriendsData);
    setFriendRequests(FriendRequestsData);
  }, []);

  const handleAction = (action, id) => {
    // Handle actions like "Send Message", "Remove Friend" etc.
    console.log(`${action} for user with ID: ${id}`);
  };

  const [loading, setLoading] = useState({});
  const [isFollow, setIsFollow] = useState({});

  const handleFollowUnFollow = async (uId) => {
    try {
      setLoading({ ...loading, [uId]: true });

      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);

      const response = await fetch(
        `${ENDPOINT}/api/user/followUser/${parsedId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
          body: JSON.stringify({ user: uId }),
        }
      );
      const data = await response.json();
      if (data.message === "Followed the user successfully!") {
        setIsFollow({ ...isFollow, [uId]: true });
      } else {
        setIsFollow({ ...isFollow, [uId]: false });
      }
      setLoading({ ...loading, [uId]: false });
    } catch (error) {
      console.log("Error:", error);
    }
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 bg-white p-6 rounded-lg shadow-md mt-4">
      {/* Search Bar */}
      <div className="mt-4">
        <input
          type="text"
          placeholder="Search for friends"
          className="w-full p-2 border border-gray-300 rounded-lg"
        />
      </div>

      {/* Friend Requests */}
      <div className="mt-6">
        <h3 className="text-2xl font-bold mb-4 text-black">Friend Requests</h3>
        <div className="space-y-4">
          {friendRequests.map((friend) => (
            <div
              key={friend.id}
              className="flex items-center space-x-4 bg-gray-100 p-4 rounded-lg shadow"
            >
              <img
                src={friend.photo}
                alt={friend.name}
                className="w-12 h-12 rounded-full object-cover"
              />
              <div className="text-gray-800">
                <p className="font-semibold">{friend.name}</p>
                <p className="text-sm text-gray-500">@{friend.username}</p>
              </div>
              <button
                onClick={() => handleAction("Accept", friend.id)}
                className="ml-auto text-blue-500 hover:underline"
              >
                Accept
              </button>
              <button
                onClick={() => handleAction("Reject", friend.id)}
                className="ml-2 text-red-500 hover:underline"
              >
                Reject
              </button>
            </div>
          ))}
        </div>
      </div>
      {console.log(userFriends)}

      {/* Your Friends */}

      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 text-black">Your Friends</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {userFriends.map((user) => {
            return (
              <div
                key={user._id}
                className="bg-gray-100 p-4 rounded-lg shadow hover:bg-gray-200"
              >
                <img
                  src={user.picture || Avatar}
                  alt="Failed to load img!"
                  className="w-24 h-24 rounded-full object-cover mx-auto"
                />
                <div className="text-center mt-4">
                  <p className="font-semibold text-gray-800">{user.fullName}</p>
                  <p className="text-sm text-gray-500">@{user.userName}</p>
                  <button
                    className="mt-4 text-blue-500 hover:underline"
                    onClick={() => handleFollowUnFollow(user._id)}
                  >
                    {loading[user._id] ? (
                      <div className="flex items-center justify-center">
                        <span className="ml-2">Loading...</span>
                      </div>
                    ) : isFollow[user._id] ? (
                      "Unfollow"
                    ) : (
                      "Follow"
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Suggested Friends */}
      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 text-black">
          Suggested Friends
        </h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {suggestions.map((user) => (
            <div
              key={user._id}
              className="bg-gray-100 p-4 rounded-lg shadow hover:bg-gray-200"
            >
              <img
                src={user.picture}
                alt="Failed to load image!"
                className="w-24 h-24 rounded-full object-cover mx-auto"
              />
              <div className="text-center mt-4">
                <p className="font-semibold text-gray-800">{user.fullName}</p>
                <p className="text-sm text-gray-500">@{user.userName}</p>
                <button
                  className="mt-4 text-blue-500 hover:underline"
                  onClick={() => handleFollowUnFollow(user._id)}
                >
                  {loading[user._id] ? (
                    <div className="flex items-center justify-center">
                      <span className="ml-2">Loading...</span>
                    </div>
                  ) : isFollow[user._id] ? (
                    "Unfollow"
                  ) : (
                    "Follow"
                  )}
                </button>
              </div>
            </div>
          ))}
          {suggestions.length < 0 || suggestions.length === 0 ? (
            <p className="text-gray-700">No suggestions available!</p>
          ) : (
            ""
          )}
        </div>
      </div>
    </div>
  );
};

export default Friends;
