import React, { useState } from "react";

const Messages = () => {
  const [conversations, setConversations] = useState([
    {
      id: 1,
      name: "John Doe",
      avatar: "https://via.placeholder.com/40?text=John",
      lastMessage: "Hey, how are you?",
      time: "2 minutes ago",
      isRead: false,
    },
    {
      id: 2,
      name: "Jane Smith",
      avatar: "https://via.placeholder.com/40?text=Jane",
      lastMessage: "Looking forward to our meeting!",
      time: "1 hour ago",
      isRead: true,
    },
    // More conversations can be added
  ]);

  const [messageThread, setMessageThread] = useState([
    { id: 1, sender: "You", message: "Hey, John!" },
    { id: 2, sender: "John Doe", message: "Hey! How's it going?" },
    { id: 3, sender: "You", message: "All good, let's catch up!" },
  ]);

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-4 sm:p-6">
      {/* Header Section */}
      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-3xl font-bold">Messages</h1>
        <div className="mt-4">
          <input
            type="text"
            placeholder="Search messages"
            className="w-full px-4 py-2 rounded-lg shadow-md bg-white text-gray-800"
          />
        </div>
      </div>

      {/* Conversation List */}
      <div className="space-y-4">
        {conversations.map((conversation) => (
          <div
            key={conversation.id}
            className={`flex items-center p-4 rounded-lg shadow-md ${
              conversation.isRead ? "bg-gray-100" : "bg-blue-100"
            } hover:bg-blue-200 transition duration-200`}
          >
            <img
              src={conversation.avatar}
              alt={conversation.name}
              className="w-12 h-12 rounded-full object-cover"
            />
            <div className="ml-4 flex-1">
              <p className="font-semibold text-gray-800">{conversation.name}</p>
              <p className="text-sm text-gray-600">
                {conversation.lastMessage}
              </p>
            </div>
            <span className="text-xs text-gray-500">{conversation.time}</span>
          </div>
        ))}
      </div>

      {/* Message Thread */}
      <div className="bg-white p-6 rounded-lg shadow-md mt-6">
        <h2 className="text-2xl font-bold mb-4">Conversation with John Doe</h2>

        <div className="space-y-4 overflow-y-auto max-h-80">
          {messageThread.map((message) => (
            <div
              key={message.id}
              className={`flex ${
                message.sender === "You" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs px-4 py-2 rounded-lg ${
                  message.sender === "You"
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200"
                }`}
              >
                <p>{message.message}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Message Composer */}
        <div className="mt-4">
          <textarea
            className="w-full p-4 border rounded-lg shadow-md"
            placeholder="Type your message..."
            rows="3"
          ></textarea>
          <button className="w-full mt-2 bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600">
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Messages;
