import React, { useContext, useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import vividlyContext from "../Context/vividlyContext";
// import { io } from "socket.io-client";

import {
  faThumbsUp,
  faAngleLeft,
  faAngleRight,
} from "@fortawesome/free-solid-svg-icons";
import { faComment } from "@fortawesome/free-regular-svg-icons";

const Home = () => {
  // useEffect(() => {
  //   const socket = io("http://localhost:5000");

  //   socket.emit("message", "Bilal");
  //   socket.on("response", (data) => {
  //     console.log("Server Response:", data);
  //   });
  //   return () => {
  //     socket.disconnect();
  //   };
  // }, []);

  const context = useContext(vividlyContext);
  const { handleCreateStoryWithMediaOnly, handleCreateStoryTextAndMedia } =
    context;

  const [showPrevIcon, setShowPrevIcon] = useState(false);
  const [showNextIcon, setShowNextIcon] = useState(true);
  const storiesContainerRef = useRef(null);
  const [userProfile, setUserProfile] = useState("");
  const [followedUserStories, setFolloweduserStories] = useState([]);
  const [groupedStories, setGroupedStories] = useState([]);

  const processStories = (stories) => {
    const groupedStories = {};
    stories.forEach((story) => {
      if (!groupedStories[story.user._id]) {
        groupedStories[story.user._id] = {
          user: story.user,
          stories: [],
        };
      }
      groupedStories[story.user._id].stories.push(story);
    });
    setGroupedStories(Object.values(groupedStories));

    return Object.values(groupedStories);
  };

  const handleFetchFollowedUserStories = async () => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/story/getFollowUserStories`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        processStories(data.stories);
        setFolloweduserStories(data.stories);
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handlefetchUserProfile = async () => {
    const id = localStorage.getItem("id");
    const parsedId = JSON.parse(id);
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    try {
      const response = await fetch(
        `${ENDPOINT}/api/user/getUserProfile/${parsedId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setUserProfile(data);
      } else {
        console.log("Error");
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  useEffect(() => {
    handlefetchUserProfile();
    handleFetchFollowedUserStories();
  }, []);

  const ENDPOINT = process.env.REACT_APP_ENDPOINT;

  useEffect(() => {
    const handleScroll = () => {
      if (storiesContainerRef.current) {
        const { scrollLeft, scrollWidth, clientWidth } =
          storiesContainerRef.current;

        // Show "prev" icon if scrollLeft > 0
        setShowPrevIcon(scrollLeft > 0);

        // Show "next" icon if there's more content to scroll
        setShowNextIcon(scrollLeft + clientWidth < scrollWidth - 1);
      }
    };

    // Attach scroll event listener
    const container = storiesContainerRef.current;
    container.addEventListener("scroll", handleScroll);

    // Initial state update
    handleScroll();

    return () => {
      // Cleanup event listener
      container.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const scrollStories = (direction) => {
    if (storiesContainerRef.current) {
      const scrollAmount = 150; // Adjust the scroll step as needed
      storiesContainerRef.current.scrollBy({
        left: direction === "next" ? scrollAmount : -scrollAmount,
        behavior: "smooth",
      });
    }
  };

  const [mediaPreview, setMediaPreview] = useState(null);
  const [storyMediaPreview, setStoryMediaPreview] = useState(null);

  // const handlePost = () => {};

  const [mediaFile, setMediaFile] = useState(null);
  const [storyMediaFile, setStoryMediaFile] = useState(null);
  const [media, setMedia] = useState("");
  const [caption, setCaption] = useState("");
  const [storyCaption, setStoryCaption] = useState("");
  const [selectedStory, setSelectedStory] = useState(null);

  const handleMediaChange = async (e) => {
    const file = e.target.files[0];
    if (!file) {
      return console.log("Please upload a file!");
    }

    const formdata = new FormData();
    formdata.append("media", file);

    try {
      const data = await fetch(`${ENDPOINT}/api/post/media`, {
        method: "POST",
        body: formdata,
      });
      const response = await data.json();
      if (response.success) {
        setMedia(response.url);
      }
    } catch (error) {
      console.log(error);
    }

    setMediaPreview(URL.createObjectURL(file));
    setMediaFile(file);
  };

  const handleStoryChange = async (e) => {
    const file = e.target.files[0];
    if (!file) {
      return console.log("Please upload a file!");
    }

    const formdata = new FormData();
    formdata.append("media", file);

    try {
      const data = await fetch(`${ENDPOINT}/api/post/media`, {
        method: "POST",
        body: formdata,
      });
      const response = await data.json();
      if (response.success) {
        setMedia(response.url);
      }
    } catch (error) {
      console.log(error);
    }

    setStoryMediaPreview(URL.createObjectURL(file));
    setStoryMediaFile(file);
  };

  const handleCaptionChange = (e) => {
    setCaption(e.target.value);
  };

  const handleStoryCaptionChange = (e) => {
    setStoryCaption(e.target.value);
  };

  const handleCreatePost = async () => {
    try {
      const mediaType = mediaFile.type.startsWith("video/") ? "video" : "image";
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/post/createPost`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
        body: JSON.stringify({
          description: caption,
          media: media,
          mediaType: mediaType,
        }),
      });
      const data = await response.json();
      setMediaFile(null);
      setMediaPreview("");
      setCaption("");
      // setUserPost([...userPost, data]);
      // await handleFetchUserPosts();
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const handleCreateStory = async () => {
    try {
      if (storyMediaFile && storyCaption) {
        const response = await handleCreateStoryTextAndMedia(
          media,
          storyCaption
        );
        if (response.success) {
          setStoryMediaFile(null);
          setStoryMediaPreview("");
          setStoryCaption("");
        }
      } else if (storyMediaFile) {
        const response = await handleCreateStoryWithMediaOnly(media);
        if (response.success) {
          setStoryMediaFile(null);
          setStoryMediaPreview("");
          setStoryCaption("");
        }
      }

      // setUserPost([...userPost, data]);
      // await handleFetchUserPosts();
    } catch (error) {
      console.log("Error:", error);
    }
  };

  const handleStoryClick = (stories) => {
    // When a user clicks on a picture, set the selected user's stories
    setSelectedStory(stories);
  };

  return (
    <>
      {/* Main Content Section */}
      <div className="flex min-h-screen sm:pl-72 sm:pt-24 max-w-7xl mx-auto sm:p-4">
        {/* Feed Section */}

        {/* {console.log(selectedStory)} */}
        {/* {console.log(followedUserStories)} */}
        <div className="w-full bg-white p-4 rounded-lg shadow-lg">
          {/* Stories/Highlights */}
          {/* Stories Section with Scroll Indicator */}
          <div className="relative mb-6">
            <div
              ref={storiesContainerRef}
              className="flex space-x-4 overflow-x-auto scrollbar-hide whitespace-nowrap mb-4 py-2"
            >
              {/* Story Upload Section */}
              <div className="flex-shrink-0 w-24 h-24 bg-gray-300 rounded-full cursor-pointer relative">
                <input
                  type="file"
                  accept="image/*,video/*"
                  onChange={handleStoryChange}
                  className="absolute inset-0 opacity-0 cursor-pointer"
                />
                <div className="flex items-center justify-center h-full text-gray-700">
                  <span className="text-xl font-bold">+</span>
                </div>
              </div>

              {/* Other Story Items */}
              {groupedStories &&
                groupedStories.map((story) => {
                  return (
                    <div
                      key={story.user._id}
                      className="flex-shrink-0 w-24 h-24 bg-gray-300 rounded-full cursor-pointer"
                      onClick={() => handleStoryClick(story)} // On click, display the stories
                    >
                      <img
                        src={story.user.picture}
                        alt="Failed to load profile img"
                        className="w-full h-full rounded-full object-cover border-2 border-transparent 
                  bg-gradient-to-r from-pink-600 via-yellow-500 to-pink-600 p-[2px] shadow-lg"
                      />
                    </div>
                  );
                })}
            </div>
          </div>
          {selectedStory.length > 0 &&
            selectedStory.map((story) => {
              return (
                <div
                  className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50"
                  key={story.user._id}
                >
                  <div className="relative max-w-3xl w-[90%] md:w-full bg-white p-4 rounded-lg shadow-2xl overflow-hidden">
                    <button
                      onClick={() => setSelectedStory(null)}
                      className="absolute top-4 right-4 text-white text-2xl bg-black bg-opacity-50 px-5 py-1 rounded-lg hover:bg-opacity-75 transition"
                    >
                      &times;
                    </button>

                    {/* User Info */}
                    <div className="flex items-center mb-4 hover:cursor-pointer">
                      <img
                        src={story.user.picture}
                        alt="Failed to load img"
                        className="rounded-full h-14 w-14 mr-3"
                      />
                      <div>
                        <h2 className="text-lg font-semibold text-black">
                          {selectedStory.user.fullName}
                        </h2>
                        <p className="text-base text-gray-600">
                          @{selectedStory.user.userName}
                        </p>
                      </div>
                    </div>

                    {/* Story Content */}
                    <div className="flex flex-col items-center justify-center">
                      <div className="relative w-full max-w-3xl h-[75vh] bg-black rounded-lg overflow-hidden shadow-lg">
                        {/* Story Media */}
                        {selectedStory.imageUrl.endsWith(".mp4") ? (
                          <video
                            src={story.imageUrl}
                            alt="Failed to load story"
                            controls
                            autoPlay
                            className="w-full h-full object-contain rounded-lg"
                          />
                        ) : (
                          <img
                            src={story.imageUrl}
                            alt="Failed to load story"
                            className="w-full h-full object-contain rounded-lg"
                          />
                        )}

                        {/* Story Text */}
                        {story.text && (
                          <p className="absolute bottom-14 left-4 text-white text-base font-semibold shadow-md max-w-[90%] bg-black bg-opacity-50 p-2 rounded-lg">
                            Caption: {story.text}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}

          <div className="p-6 bg-white rounded-lg shadow-lg mt-2 mb-5">
            {/* Story Media Preview Section */}
            {storyMediaPreview && (
              <div className="mt-4">
                {storyMediaFile.type.startsWith("video/") ? (
                  <video
                    src={storyMediaPreview}
                    controls
                    className="w-full h-auto rounded-lg"
                  />
                ) : (
                  <img
                    src={storyMediaPreview}
                    alt="Media Preview"
                    className="w-full h-auto rounded-lg"
                  />
                )}
              </div>
            )}

            {/* Story Text Caption */}
            {storyMediaPreview && (
              <textarea
                className="mt-4 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none text-black"
                placeholder="Write a caption..."
                value={storyCaption}
                onChange={handleStoryCaptionChange}
              />
            )}

            {/*Story Action Buttons */}
            <div className="flex justify-end mt-4 space-x-3">
              {storyMediaFile && (
                <>
                  <button
                    className="py-2 px-6 bg-gray-200 text-black rounded-lg hover:bg-gray-300"
                    onClick={() => {
                      setStoryMediaFile(null);
                      setStoryMediaPreview("");
                      setStoryCaption("");
                    }}
                    disabled={!storyMediaFile}
                  >
                    Cancel
                  </button>
                  <button
                    className={`py-2 px-6 rounded-lg text-white ${
                      storyMediaFile
                        ? "bg-blue-600 hover:bg-blue-700"
                        : "bg-gray-400 cursor-not-allowed"
                    }`}
                    onClick={handleCreateStory}
                    disabled={!storyMediaFile}
                  >
                    Upload Story
                  </button>
                </>
              )}
            </div>

            {/* Upload Section */}
            {!storyMediaFile && (
              <label
                htmlFor="mediaUpload"
                className="cursor-pointer flex flex-col items-center justify-center border-2 border-dashed border-gray-300 p-6 rounded-lg hover:border-blue-500 transition"
              >
                {mediaPreview ? (
                  <p className="text-blue-500 font-semibold">Change Media</p>
                ) : (
                  <>
                    <svg
                      className="w-12 h-12 text-gray-500"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path d="M12 4v16m8-8H4"></path>
                    </svg>
                    <p className="text-gray-600 mt-2 text-center">
                      Click to upload an image or video
