import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";

const Dashboard = () => {
  const context = useContext(vividlyContext);
  const { showSuggestionFunc, suggestions } = context;
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const [userProfile, setUserProfile] = useState("");
  const handlefetchUserProfile = async () => {
    const id = localStorage.getItem("id");
    const parsedId = JSON.parse(id);
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    try {
      const response = await fetch(
        `${ENDPOINT}/api/user/getUserProfile/${parsedId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setUserProfile(data);
      } else {
        console.log("Error");
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const [userInsights, setUserInsights] = useState("");

  const handleUserInsights = async () => {
    const id = localStorage.getItem("id");
    const parsedId = JSON.parse(id);
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    try {
      const response = await fetch(
        `${ENDPOINT}/api/user/getUserInsights/${parsedId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setUserInsights(data);
      } else {
        console.log("Error");
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const [loading, setLoading] = useState({});
  const [isFollow, setIsFollow] = useState({});

  const handleFollowUnFollow = async (uId) => {
    try {
      setLoading({ ...loading, [uId]: true });

      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);

      const response = await fetch(
        `${ENDPOINT}/api/user/followUser/${parsedId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
          body: JSON.stringify({ user: uId }),
        }
      );
      const data = await response.json();
      if (data.message === "Followed the user successfully!") {
        setIsFollow({ ...isFollow, [uId]: true });
      } else {
        setIsFollow({ ...isFollow, [uId]: false });
      }
      setLoading({ ...loading, [uId]: false });
    } catch (error) {
      console.log("Error:", error);
    }
  };

  useEffect(() => {
    handlefetchUserProfile();
    showSuggestionFunc();
    handleUserInsights();
  }, []);
  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 max-w-7xl mx-auto p-4">
      {/* User Overview Section */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <div className="flex items-center space-x-4">
          <img
            src={userProfile.uPic || Avatar}
            alt="Profile"
            className="w-16 h-16 rounded-full"
          />
          <div>
            <h3 className="text-xl font-bold text-black">
              {userProfile && userProfile.uFullname}
            </h3>
            <p className="text-gray-500">
              @{userProfile && userProfile.uUsername}
            </p>
          </div>
        </div>
        <div className="mt-4 grid grid-cols-4 gap-4 text-center">
          <div>
            <h4 className="text-lg font-bold text-black">
              {userProfile && userProfile.uPosts.length}
            </h4>
            <p className="text-sm text-black">Posts</p>
          </div>
          <div>
            <h4 className="text-lg font-bold text-black">
              {userProfile && userProfile.uFollowers.followersList.length}
            </h4>
            <p className="text-sm text-black">Followers</p>
          </div>
          <div>
            <h4 className="text-lg font-bold text-black">
              {userProfile && userProfile.uFollowing.followingList.length}
            </h4>
            <p className="text-sm text-black">Following</p>
          </div>
          <div>
            <h4 className="text-lg font-bold text-black">25</h4>
            <p className="text-sm text-black">Groups</p>
          </div>
        </div>
      </div>

      {/* Engagement Insights */}
      <div className="bg-white p-6 rounded-lg shadow-md mt-6">
        <h3 className="text-lg font-bold mb-4 text-black">
          Engagement Insights
        </h3>
        <div className="grid grid-cols-3 gap-4 text-center text-black">
          <div>
            <h4 className="text-lg font-bold text-black">
              {userInsights && userInsights.allLikes.length}
            </h4>
            <p className="text-sm text-black">Likes</p>
          </div>
          <div>
            <h4 className="text-lg font-bold text-black">
              {userInsights && userInsights.allComments.length}
            </h4>
            <p className="text-sm text-black">Comments</p>
          </div>
          <div>
            <h4 className="text-lg font-bold text-black">30</h4>
            <p className="text-sm text-black">Shares</p>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mt-6">
        <button className="bg-blue-500 text-white p-4 rounded-lg shadow hover:bg-blue-600">
          Create Post
        </button>
        <button className="bg-gray-200 p-4 rounded-lg shadow hover:bg-gray-300">
          Notifications
        </button>
        <button className="bg-gray-200 p-4 rounded-lg shadow hover:bg-gray-300">
          Groups
        </button>
        <button className="bg-gray-200 p-4 rounded-lg shadow hover:bg-gray-300">
          Events
        </button>
      </div>

      {/* Suggestions */}
      <div className="bg-white p-6 rounded-lg shadow-md mt-6">
        <h3 className="text-lg font-bold mb-4 text-black">
          Suggestions for You
        </h3>
        <ul className="space-y-3">
          {suggestions.map((data) => {
            return (
              <li
                key={data._id}
                className="flex items-center justify-between bg-gray-100 p-3 rounded-lg"
              >
                <div className="flex items-center space-x-3">
                  <img
                    src={data.picture}
                    alt="Failed to load Img"
                    className="w-12 h-12 rounded-full object-cover"
                  />
                  <p className="font-bold text-black">{data.fullName}</p>
                </div>
                <button
                  className="text-blue-500 hover:underline"
                  onClick={() => handleFollowUnFollow(data._id)}
                >
                  {loading[data._id] ? (
                    <div className="flex items-center justify-center">
                      <span className="ml-2">Loading...</span>
                    </div>
                  ) : isFollow[data._id] ? (
                    "Unfollow"
                  ) : (
                    "Follow"
                  )}
                </button>
              </li>
            );
          })}
          {suggestions.length < 0 || suggestions.length === 0 ? (
            <p className="text-gray-700">No suggestions available!</p>
          ) : (
            ""
          )}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
