import React from "react";
import VividlyContext from "./vividlyContext";

const VividlyProvider = (props) => {
  const sendOtp = async (email) => {
    const ENDPOINT = process.env.REACT_APP_ENDPOINT;

    try {
      const response = await fetch(`${ENDPOINT}/api/user/signup`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }), // Corrected body structure
      });

      if (!response.ok) {
        // Handle non-200 responses (e.g., 400, 500 errors)
        throw new Error(`Error: ${response.statusText}`);
      }

      const data = await response.json();
      console.log("Success:", data);
      return data; // Optional: Return the response data for further handling
    } catch (error) {
      console.error("Error sending OTP:", error.message);
    }
  };

  return (
    <VividlyContext.Provider value={{ sendOtp }}>
      {props.children}
    </VividlyContext.Provider>
  );
};

export default VividlyProvider;
