import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";
import SkeletonLoader from "./SkeletonLoader";

const Friends = () => {
  const context = useContext(vividlyContext);
  const {
    showSuggestionFunc,
    suggestions,
    handleFetcHUserFriends,
    userFriends,
  } = context;
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const [showSkeleton, setShowSkeleton] = useState(false);
  const [countLoading, setCountLoading] = useState(0);

  const [loading, setLoading] = useState({});
  const [isFollow, setIsFollow] = useState({});

  const handleShowSkeleton = async () => {
    if (countLoading === 0) {
      setShowSkeleton(true);
    }
    await showSuggestionFunc();
    await handleFetcHUserFriends();
    setCountLoading(1);
    setShowSkeleton(false);
  };

  useEffect(() => {
    handleShowSkeleton();
  }, []);

  const handleFollowUnFollow = async (uId) => {
    try {
      setLoading({ ...loading, [uId]: true });

      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);

      const response = await fetch(
        `${ENDPOINT}/api/user/followUser/${parsedId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
          body: JSON.stringify({ user: uId }),
        }
      );
      const data = await response.json();
      console.log(data);
      if (data.message === "Followed the user successfully!") {
        setIsFollow({ ...isFollow, [uId]: true });
      }
      setIsFollow({ ...isFollow, [uId]: false });
      setLoading({ ...loading, [uId]: false });
    } catch (error) {
      console.log("Error:", error);
    }
  };

  const handleFriendFollowUnFollow = async (uId) => {
    try {
      setLoading({ ...loading, [uId]: true });
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);

      const response = await fetch(
        `${ENDPOINT}/api/user/followUser/${parsedId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
          body: JSON.stringify({ user: uId }),
        }
      );
      const data = await response.json();
      if (data.message === "Successfully unfollowed the user.") {
        setIsFollow({ ...isFollow, [uId]: true });
      } else {
        setIsFollow({ ...isFollow, [uId]: false });
      }
      setLoading({ ...loading, [uId]: false });
    } catch (error) {
      console.log("Error:", error);
    }
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 bg-white p-6 rounded-lg shadow-md mt-4">
      {showSkeleton && <SkeletonLoader />}
      {!showSkeleton && (
        <>
          {" "}
          {/* Search Bar */}
          <div className="mt-4">
            <input
              type="text"
              placeholder="Search for friends"
              className="w-full p-2 border border-gray-300 rounded-lg"
            />
          </div>
          {/* Your Friends */}
          <div className="mt-8">
            <h3 className="text-2xl font-bold mb-4 text-black">Your Friends</h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {userFriends.map((user) => {
                return (
                  <div
                    key={user.userId._id}
                    className="bg-gray-100 p-4 rounded-lg shadow hover:bg-gray-200"
                  >
                    <img
                      src={user.userId.picture || Avatar}
                      alt="Failed to load img!"
                      className="w-24 h-24 rounded-full object-cover mx-auto"
                    />
                    <div className="text-center mt-4">
                      <p className="font-semibold text-gray-800">
                        {user.userId.fullName}
                      </p>
                      <p className="text-sm text-gray-500">
                        @{user.userId.userName}
                      </p>
                      <button
                        className="mt-4 text-blue-500 hover:underline"
                        onClick={() =>
                          handleFriendFollowUnFollow(user.userId._id)
                        }
                      >
                        {loading[user.userId._id] ? (
                          <div className="flex items-center justify-center">
                            <span className="ml-2">Loading...</span>
                          </div>
                        ) : isFollow[user.userId._id] ? (
                          "Follow"
                        ) : (
                          "UnFollow"
                        )}
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
            {userFriends.length === 0 && (
              <p className="text-xl text-center text-gray-700">
                You don't have any friends yet!
              </p>
            )}
          </div>
          {/* Suggested Friends */}
          <div className="mt-8">
            <h3 className="text-2xl font-bold mb-4 text-black">
              Suggested Friends
            </h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {suggestions.map((user) => (
                <div
                  key={user._id}
                  className="bg-gray-100 p-4 rounded-lg shadow hover:bg-gray-200"
                >
                  <img
                    src={user.picture}
                    alt="Failed to load image!"
                    className="w-24 h-24 rounded-full object-cover mx-auto"
                  />
                  <div className="text-center mt-4">
                    <p className="font-semibold text-gray-800">
                      {user.fullName}
                    </p>
                    <p className="text-sm text-gray-500">@{user.userName}</p>
                    <button
                      className="mt-4 text-blue-500 hover:underline"
                      onClick={() => handleFollowUnFollow(user._id)}
                    >
                      {loading[user._id] ? (
                        <div className="flex items-center justify-center">
                          <span className="ml-2">Loading...</span>
                        </div>
                      ) : isFollow[user._id] ? (
                        "Unfollow"
                      ) : (
                        "Follow"
                      )}
                    </button>
                  </div>
                </div>
              ))}
              {suggestions.length < 0 || suggestions.length === 0 ? (
                <p className="text-gray-700">No suggestions available!</p>
              ) : (
                ""
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Friends;
