import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";

const Home = () => {
  const storiesContainerRef = useRef(null);
  const [showScrollIndicator, setShowScrollIndicator] = useState(false);

  // Detect if scrolling is needed
  const checkScroll = () => {
    const container = storiesContainerRef.current;
    if (container.scrollWidth > container.clientWidth) {
      setShowScrollIndicator(true);
    } else {
      setShowScrollIndicator(false);
    }
  };

  // Scroll left or right
  const handleScroll = (direction) => {
    const container = storiesContainerRef.current;
    const scrollAmount = 150; // Adjust the scroll amount as needed
    container.scrollBy({
      left: direction === "right" ? scrollAmount : -scrollAmount,
      behavior: "smooth",
    });
  };

  useEffect(() => {
    checkScroll(); // Initial check
    const handleResize = () => checkScroll(); // Re-check on resize
    window.addEventListener("resize", handleResize);

    return () => window.removeEventListener("resize", handleResize);
  }, []);
  return (
    <>
      {/* Main Content Section */}
      <div className="flex smd:pl-[165px] sm:pl-60 md:pl-72 pt-24 max-w-7xl mx-auto p-4">
        {/* Sidebar */}
        <div className="hidden lg:block w-1/4 bg-white shadow-lg p-4 rounded-lg mr-4">
          <h3 className="font-semibold text-lg">Groups</h3>
          <ul className="space-y-3">
            <li>
              <Link to="/groups" className="text-gray-700 hover:text-blue-500">
                Group 1
              </Link>
            </li>
            <li>
              <Link to="/groups" className="text-gray-700 hover:text-blue-500">
                Group 2
              </Link>
            </li>
            <li>
              <Link to="/groups" className="text-gray-700 hover:text-blue-500">
                Group 3
              </Link>
            </li>
          </ul>
        </div>

        {/* Feed Section */}
        <div className="w-full lg:w-3/4 bg-white p-4 rounded-lg shadow-lg">
          {/* Stories/Highlights */}
          {/* Stories Section with Scroll Indicator */}
          <div className="relative mb-6">
            <div
              ref={storiesContainerRef}
              className="flex space-x-4 overflow-x-auto whitespace-nowrap scrollbar-hide mb-4 py-2"
            >
              {[...Array(8)].map((_, index) => (
                <div
                  key={index}
                  className="flex-shrink-0 w-24 h-24 bg-gray-300 rounded-full cursor-pointer"
                >
                  <img
                    src="https://via.placeholder.com/150"
                    alt="story"
                    className="w-full h-full rounded-full object-cover"
                  />
                </div>
              ))}
            </div>

            {/* Scroll Indicator */}
            {showScrollIndicator && (
              <div className="absolute top-1/2 left-0 right-0 flex justify-between px-4 transform -translate-y-1/2">
                <button
                  onClick={() => handleScroll("left")}
                  className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-500 bg-opacity-50 text-white"
                >
                  <FontAwesomeIcon icon="angle-left" />
                </button>
                <button
                  onClick={() => handleScroll("right")}
                  className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-500 bg-opacity-50 text-white"
                >
                  <FontAwesomeIcon icon="angle-right" />
                </button>
              </div>
            )}
          </div>

          {/* Create Post Section */}
          <div className="mb-6">
            <textarea
              placeholder="What's on your mind?"
              className="w-full p-4 border rounded-lg bg-gray-50"
              rows="3"
            ></textarea>
            <button className="mt-4 bg-blue-500 text-white p-2 rounded-lg">
              Post
            </button>
          </div>

          {/* Feed Posts */}
          <div className="space-y-4">
            {/* Sample Post */}
            <div className="border-b pb-4">
              <div className="flex items-center space-x-2">
                <div className="w-12 h-12 rounded-full bg-gray-300"></div>
                <div>
                  <div className="font-semibold">User Name</div>
                  <div className="text-sm text-gray-500">2 hours ago</div>
                </div>
              </div>
              <p className="mt-2 text-gray-700">
                This is a sample post content...
              </p>
              <div className="mt-2 flex space-x-6">
                <div className="flex items-center space-x-1 text-gray-700">
                  <FontAwesomeIcon icon="thumbs-up" className="text-xl" />
                  <span>12</span>
                </div>
                <div className="flex items-center space-x-1 text-gray-700">
                  <FontAwesomeIcon icon="comment" className="text-xl" />
                  <span>5</span>
                </div>
              </div>
            </div>
            {/* Add more posts here */}
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
