import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ProfileDropdown from "./ProfileDropdown";
import { Link, useLocation } from "react-router-dom";

const Navbar = () => {
  return (
    <nav className="w-full bg-white border-b shadow-md fixed top-0 z-50">
      <div className="max-w-7xl mx-auto flex justify-between items-center p-4">
        {/* Logo */}
        <div className="esmd:text-xl sm:text-3xl custom-font font-bold text-blue-500">
          Vividly
        </div>

        {/* Navigation */}
        <div className="flex smd:space-x-3 sm:space-x-6">
          <Link to="/home" className="block">
            <NavItem icon="home" text="Home" path="/home" />
          </Link>
          <Link to="/explore" className="block">
            <NavItem icon="search" text="Explore" path="/explore" />
          </Link>
          <Link to="/messages" className="block">
            <NavItem icon="comments" text="Messages" path="/messages" />
          </Link>
          <Link to="/notifications" className="block">
            <NavItem icon="bell" text="Notifications" path="/notifications" />
          </Link>
        </div>

        <ProfileDropdown />
      </div>
    </nav>
  );
};

const NavItem = ({ icon, text, path }) => {
  const location = useLocation();
  const isActive = location.pathname === path;
  return (
    <div
      className={`flex flex-col items-center ${
        isActive ? "activeLink" : "text-gray-600"
      } hover:text-blue-500 cursor-pointer`}
    >
      <FontAwesomeIcon icon={icon} className="smd:w-3 smd:h-3 sm:w-6 sm:h-6" />
      <span className="text-xs">{text}</span>
    </div>
  );
};

export default Navbar;
