import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faThumbsUp,
  faAngleLeft,
  faAngleRight,
} from "@fortawesome/free-solid-svg-icons";
import { faComment } from "@fortawesome/free-regular-svg-icons";

const Home = () => {
  const [showPrevIcon, setShowPrevIcon] = useState(false);
  const [showNextIcon, setShowNextIcon] = useState(true);
  const storiesContainerRef = useRef(null);
  const [userProfile, setUserProfile] = useState("");
  const [mediaPreview, setMediaPreview] = useState(null);
  const [mediaFile, setMediaFile] = useState(null);
  const [media, setMedia] = useState("");
  const [caption, setCaption] = useState("");
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;

  useEffect(() => {
    const handlefetchUserProfile = async () => {
      const id = JSON.parse(localStorage.getItem("id"));
      const token = JSON.parse(localStorage.getItem("token"));

      try {
        const response = await fetch(
          `${ENDPOINT}/api/user/getUserProfile/${id}`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "auth-token": token,
            },
          }
        );
        const data = await response.json();
        if (data.success) setUserProfile(data);
      } catch (error) {
        console.error("Error:", error.message);
      }
    };

    handlefetchUserProfile();
  }, [ENDPOINT]);

  useEffect(() => {
    const handleScroll = () => {
      const container = storiesContainerRef.current;
      if (container) {
        const { scrollLeft, scrollWidth, clientWidth } = container;
        setShowPrevIcon(scrollLeft > 0);
        setShowNextIcon(scrollLeft + clientWidth < scrollWidth - 1);
      }
    };

    const container = storiesContainerRef.current;
    container.addEventListener("scroll", handleScroll);
    handleScroll();
    return () => container.removeEventListener("scroll", handleScroll);
  }, []);

  const handleMediaChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("media", file);

    try {
      const response = await fetch(`${ENDPOINT}/api/post/media`, {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      if (data.success) setMedia(data.url);
    } catch (error) {
      console.error("Upload Error:", error);
    }

    setMediaPreview(URL.createObjectURL(file));
    setMediaFile(file);
  };

  const handleCaptionChange = (e) => setCaption(e.target.value);

  const handleCreatePost = async () => {
    try {
      const token = JSON.parse(localStorage.getItem("token"));
      const mediaType = mediaFile.type.startsWith("video/") ? "video" : "image";
      await fetch(`${ENDPOINT}/api/post/createPost`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": token,
        },
        body: JSON.stringify({
          description: caption,
          media,
          mediaType,
        }),
      });

      setMediaFile(null);
      setMediaPreview("");
      setCaption("");
    } catch (error) {
      console.error("Post Error:", error);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      {/* Story Section */}
      <div
        className="flex space-x-4 overflow-x-auto w-full mb-4"
        ref={storiesContainerRef}
      >
        <div className="w-24 h-24 bg-gray-300 rounded-full flex items-center justify-center cursor-pointer relative">
          <input
            type="file"
            accept="image/*,video/*"
            onChange={handleMediaChange}
            className="absolute inset-0 opacity-0 cursor-pointer"
          />
          <span className="text-xl font-bold">+</span>
        </div>

        {[...Array(10)].map((_, index) => (
          <div
            key={index}
            className="w-24 h-24 rounded-full bg-gray-300 overflow-hidden"
          >
            <img
              src={userProfile.uPic}
              alt={`Story ${index + 1}`}
              className="object-cover w-full h-full"
            />
          </div>
        ))}
      </div>

      {/* Upload Section */}
      <div className="p-6 bg-white rounded-lg shadow-lg w-full max-w-lg">
        <label className="cursor-pointer border-dashed border-2 p-6 rounded-lg flex flex-col items-center hover:border-blue-500">
          {mediaPreview ? (
            <p>Change Media</p>
          ) : (
            <p>Click to upload an image or video</p>
          )}
          <input
            type="file"
            accept="image/*,video/*"
            onChange={handleMediaChange}
            className="hidden"
          />
        </label>

        {mediaPreview && (
          <div className="mt-4">
            {mediaFile.type.startsWith("video/") ? (
              <video
                src={mediaPreview}
                controls
                className="w-full rounded-lg"
              />
            ) : (
              <img
                src={mediaPreview}
                alt="Preview"
                className="w-full rounded-lg"
              />
            )}
          </div>
        )}

        {mediaPreview && (
          <textarea
            value={caption}
            onChange={handleCaptionChange}
            placeholder="Write a caption..."
            className="w-full mt-4 p-2 border rounded-lg"
          />
        )}

        {mediaFile && (
          <div className="flex justify-end space-x-4 mt-4">
            <button
              className="px-4 py-2 bg-gray-300 rounded-lg"
              onClick={() => setMediaPreview("")}
            >
              Cancel
            </button>
            <button
              className="px-4 py-2 bg-blue-600 text-white rounded-lg"
              disabled={!caption}
              onClick={handleCreatePost}
            >
              Post
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
