import React, { useContext, useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Avatar from "../Images/Avatar.png";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faForward } from "@fortawesome/free-solid-svg-icons";
import { useNavigate } from "react-router-dom";

import vividlyContext from "../Context/vividlyContext";

const AditionlaInfoSignup = ({ userInfo }) => {
  const context = useContext(vividlyContext);
  const { addAdditionalInfo } = context;
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState(null);
  const [gender, setGender] = useState("");
  const [error, setError] = useState(false);
  const [image, setImage] = useState(Avatar);

  const [DOB, setDOB] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file));
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!gender) {
      setError(true);
      return;
    }
    setError(false);
    handleSubmit();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!gender) {
      setError(true); // Show error only when not selected
    } else {
      setError(false);
      alert(`Selected Gender: ${gender}`);
    }
  };

  const handleSkipStep = () => {
    navigate("/home");
  };

  const handleAddInfo = async (e) => {
    e.preventDefault();
    const response = await addAdditionalInfo(
      image,
      DOB,
      gender,
      userInfo.userName
    );
    if (response.success) {
      navigate("/home");
    }
  };

  return (
    <>
      <div className="grid h-20 sm:h-24 grid-cols-2 mt-4 mb-4">
        <div>
          <label
            htmlFor="profileImage"
            className="block mb-2 text-sm sm:text-base font-bold text-gray-900"
          >
            Upload Profile Picture
          </label>
          <input
            type="file"
            id="profileImage"
            accept="image/*"
            onChange={handleImageChange}
            className="block w-[75%] sm:w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50"
          />
        </div>
        <div className="flex justify-end">
          <img
            src={image}
            alt="Profile Preview"
            className="h-24 w-24 rounded-full object-cover"
          />
        </div>
      </div>

      <div className="grid sm:grid-cols-2 gap-x-4">
        <div>
          <label
            htmlFor="dob"
            className="block mb-1 text-sm font-bold text-gray-900"
          >
            Date of Birth
          </label>
          <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
            className="w-full p-2 border border-gray-300 rounded-lg text-black"
            required
            placeholderText="Select date"
            dateFormat="dd-MM-yyyy"
          />
        </div>

        <div className="mt-3 sm:mt-0">
          <label className="block mb-2 text-sm font-bold text-black">
            Gender
          </label>
          <div className="flex items-center space-x-4">
            <label className="flex items-center text-black">
              <input
                type="radio"
                name="gender"
                value="male"
                checked={gender === "male"}
                onChange={() => setGender("male")}
                className="mr-2"
              />
              Male
            </label>
            <label className="flex items-center text-black">
              <input
                type="radio"
                name="gender"
                value="female"
                checked={gender === "female"}
                onChange={() => setGender("female")}
                className="mr-2"
              />
              Female
            </label>
          </div>
          {error && (
            <p className="text-red-500 text-sm mt-2">
              Please select your gender!
            </p>
          )}
        </div>
      </div>

      <div className="flex justify-end space-x-4 mt-6">
        <button
          type="button"
          onClick={handleSkipStep}
          className="bg-gray-300 text-black py-2 px-6 rounded-lg hover:bg-gray-400"
        >
          Skip <FontAwesomeIcon icon={faForward} />
        </button>

        <button
          type="submit"
          className="bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700"
          onClick={handleAddInfo}
        >
          Save Info
        </button>
      </div>
    </>
  );
};

export default AditionlaInfoSignup;
