import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import { io } from "socket.io-client";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPaperclip,
  faCamera,
  faPaperPlane,
} from "@fortawesome/free-solid-svg-icons";

const ConversationPage = () => {
  const context = useContext(vividlyContext);
  const {
    handleFetchConversation,
    sendMessage,
    handleFetchConvUserData,
    sendMedia,
  } = context;
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;

  const [messages, setMessages] = useState([]);
  const searchedUserId = JSON.parse(localStorage.getItem("sUserId"));
  const [convUcred, setConUcred] = useState("");
  const [error, setError] = useState("");
  const [socket, setSocket] = useState(null);
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);
  const [newMessage, setNewMessage] = useState("");

  const handleFetchData = async () => {
    const response = await handleFetchConversation(searchedUserId);
    if (response.message === "Conversation retrieved successfully!") {
      setMessages(response.data.messages);
    } else {
      setError("No conversation started yet!");
    }

    const userResponse = await handleFetchConvUserData(searchedUserId);
    setConUcred(userResponse.userCredentials);
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();

    if (date.toDateString() === now.toDateString()) {
      return new Intl.DateTimeFormat("en-US", {
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    } else {
      return new Intl.DateTimeFormat("en-US", {
        month: "short",
        day: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    }
  };

  useEffect(() => {
    const newSocket = io("http://localhost:5000");
    setSocket(newSocket);

    handleFetchData();

    return () => {
      newSocket.disconnect();
    };
  }, [searchedUserId]);

  useEffect(() => {
    if (!socket) return;

    socket.on("receive_message", (newMsg) => {
      setMessages((prevMessages) => [...prevMessages, newMsg]);
    });

    return () => {
      socket.off("receive_message");
    };
  }, [socket]);

  const handleSendMessage = async () => {
    if (newMessage.trim()) {
      const response = await sendMessage(searchedUserId, newMessage);
      if (response.success) {
        const newMsg = response.data;
        socket.emit("send_message", newMsg);
        setMessages((prevMessages) => [...prevMessages, newMsg]);
        setNewMessage("");
      }
    }
  };

  const [selectedMedia, setSelectedMedia] = useState(null);
  const [selectedMediaType, setSelectedMediaType] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.name.endsWith(".mp4")) {
        setSelectedMediaType("video");
        setIsModalOpen(true);
      } else {
        setSelectedMediaType("image");
        setIsModalOpen(true);
      }
      const formdata = new FormData();
      formdata.append("media", file);
      try {
        const data = await fetch(`${ENDPOINT}/api/post/media`, {
          method: "POST",
          body: formdata,
        });
        const response = await data.json();
        if (response.success) {
          setSelectedMedia(response.url);
        }
      } catch (error) {
        console.log(error);
      }
    }
  };

  const closeModal = () => {
    setIsModalOpen(false); // Close the modal
    setSelectedMedia(null); // Clear the selected media
  };

  const handleSendMedia = async () => {
    const response = await sendMedia(
      searchedUserId,
      selectedMedia,
      selectedMediaType
    );
    if (response.success) {
      console.log(response);

      console.log("GG");
      closeModal();
    }
  };

  return (
    <div className="max-w-7xl mx-auto h-screen sm:pl-72 sm:pt-24 p-4 flex flex-col bg-gray-50">
      {/* Chat Header */}
      <div className="bg-blue-600 text-white p-4 shadow-md flex items-center justify-between rounded-md">
        <h2 className="text-xl font-semibold">Conversation</h2>
        <div className="flex items-center space-x-2">
          <img
            src={convUcred.picture}
            alt="User Profile"
            className="w-10 h-10 rounded-full object-cover"
          />
          <span className="font-semibold">{convUcred.fullName}</span>
        </div>
      </div>

      {/* Messages Section */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-100">
        {messages &&
          messages.length > 0 &&
          messages.map((msg) => (
            <div
              key={msg._id}
              className={`flex ${
                msg.senderId === parsedId ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs p-3 rounded-lg shadow-md ${
                  !msg.media
                    ? msg.senderId === parsedId
                      ? "bg-blue-500 text-white"
                      : "bg-gray-200 text-gray-800"
                    : ""
                }`}
              >
                {!msg.media && <p className="text-sm">{msg.text}</p>}
                {msg.media &&
                  (msg.mediaType === "image" ? (
                    <img
                      src={msg.media}
                      alt="Preview"
                      className="w-full max-h-[55vh] object-cover rounded-md"
                    />
                  ) : (
                    <video
                      src={msg.media}
                      className="w-full max-h-[55vh]"
                      controls
                      poster=""
                    ></video>
                  ))}
                <span
                  className={`text-xs ${
                    !msg.media
                      ? msg.senderId === parsedId
                        ? "text-gray-300"
                        : "text-gray-700"
                      : ""
                  }`}
                >
                  {formatDate(msg.createdAt)}
                </span>
              </div>
            </div>
          ))}
        {error && (
          <div className="flex justify-center h-[45vh] items-center text-gray-700 custom-font text-2xl">
            No conversation started yet!
          </div>
        )}
      </div>

      {/* Message Input Section */}
      <div className="bg-white p-4 border-t border-gray-300 flex items-center space-x-3">
        {/* Camera Upload Icon (for photos/videos) */}
        <label htmlFor="camera-upload" className="cursor-pointer">
          <FontAwesomeIcon
            icon={faCamera}
            className="text-gray-500 hover:text-blue-500 h-6 w-6"
          />
        </label>

        {/* Text Input */}
        <input
          type="text"
          placeholder="Type a message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="w-full p-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 text-black"
        />

        {/* Send Button */}
        <button
          onClick={handleSendMessage}
          className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          <FontAwesomeIcon icon={faPaperPlane} className="h-6 w-8" />
        </button>

        {/* Hidden File Input */}
        <input
          type="file"
          id="camera-upload"
          accept="image/*,video/*"
          style={{ display: "none" }}
          onChange={handleFileChange}
        />
      </div>

      {/* Modal for Media Preview */}
      {isModalOpen && (
        <div className="fixed inset-0 sm:mt-14 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg max-w-[90vw] sm:max-w-lg w-full">
            <div className="flex justify-between items-center">
              <h3 className="text-xl font-semibold text-black">Preview</h3>
              <button
                onClick={closeModal}
                className="text-gray-500 hover:text-red-500 text-2xl bg-gray-200 px-4 py-2 rounded-lg"
              >
                X
              </button>
            </div>

            <div className="mt-4">
              {selectedMedia && (
                <div className="mb-4">
                  {selectedMediaType === "video" ? (
                    <video
                      src={selectedMedia}
                      className="w-full max-h-[55vh]"
                      controls
                      poster=""
                    ></video>
                  ) : (
                    <img
                      src={selectedMedia}
                      alt="Preview"
                      className="w-full max-h-[55vh] object-cover rounded-lg"
                    />
                  )}
                </div>
              )}
            </div>

            {/* Send Button in Modal */}
            <button
              onClick={handleSendMedia}
              className="w-full p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ConversationPage;
