import React from "react";

const FullScreenSkeleton = () => {
  return (
    <div className="w-full h-screen flex flex-col items-center justify-center bg-gray-100">
      {/* Cover Image Skeleton */}
      <div className="w-full h-48 bg-gray-300 rounded-lg mb-6 animate-pulse"></div>

      {/* Profile Section Skeleton */}
      <div className="flex flex-col items-center mb-4 animate-pulse">
        <div className="w-24 h-24 bg-gray-300 rounded-full mb-2"></div>
        <div className="w-40 h-6 bg-gray-300 rounded-lg mb-2"></div>
        <div className="w-32 h-4 bg-gray-300 rounded-lg"></div>
      </div>

      {/* Stats Skeleton (Optional) */}
      <div className="flex space-x-8 mb-6 animate-pulse">
        <div className="w-16 h-16 bg-gray-300 rounded-lg"></div>
        <div className="w-16 h-16 bg-gray-300 rounded-lg"></div>
        <div className="w-16 h-16 bg-gray-300 rounded-lg"></div>
      </div>

      {/* Post Content Skeleton */}
      <div className="w-full max-w-md bg-white p-4 rounded-lg shadow-md animate-pulse">
        <div className="w-full h-36 bg-gray-300 rounded-lg mb-4"></div>
        <div className="w-3/4 h-6 bg-gray-300 rounded-lg mb-2"></div>
        <div className="w-2/3 h-6 bg-gray-300 rounded-lg mb-4"></div>
        <div className="w-1/2 h-6 bg-gray-300 rounded-lg"></div>
      </div>
    </div>
  );
};

export default FullScreenSkeleton;
