import React from "react";

const SkeletonLoader = ({ type = "default" }) => {
  const renderDefaultSkeleton = () => (
    <div className="w-full animate-pulse">
      <div className="bg-gray-300 h-6 w-3/4 mb-4 rounded-lg"></div>
      <div className="bg-gray-300 h-4 w-full mb-2 rounded-lg"></div>
      <div className="bg-gray-300 h-4 w-5/6 mb-2 rounded-lg"></div>
      <div className="bg-gray-300 h-4 w-3/4 rounded-lg"></div>
    </div>
  );

  const renderProfileSkeleton = () => (
    <div className="flex flex-col items-center animate-pulse">
      <div className="bg-gray-300 w-32 h-32 rounded-full mb-4"></div>
      <div className="bg-gray-300 w-48 h-6 rounded-lg mb-2"></div>
      <div className="bg-gray-300 w-32 h-4 rounded-lg"></div>
    </div>
  );

  const renderMediaSkeleton = () => (
    <div className="w-full h-80 bg-gray-300 rounded-lg animate-pulse"></div>
  );

  const renderGridSkeleton = () => (
    <div className="grid grid-cols-3 gap-4">
      <div className="bg-gray-300 h-24 rounded-lg animate-pulse"></div>
      <div className="bg-gray-300 h-24 rounded-lg animate-pulse"></div>
      <div className="bg-gray-300 h-24 rounded-lg animate-pulse"></div>
    </div>
  );

  const renderSkeleton = () => {
    switch (type) {
      case "profile":
        return renderProfileSkeleton();
      case "media":
        return renderMediaSkeleton();
      case "grid":
        return renderGridSkeleton();
      default:
        return renderDefaultSkeleton();
    }
  };

  return <div className="p-4">{renderSkeleton()}</div>;
};

export default SkeletonLoader;
