import React, { useContext, useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPenToSquare,
  faHeart as LikeHeart,
  faCamera,
} from "@fortawesome/free-solid-svg-icons";
import {
  faComment,
  faHeart,
  faPaperPlane,
  faPenToSquare as faPenRegular,
  faTrashAlt,
} from "@fortawesome/free-regular-svg-icons";
import profileCover from "../Images/profileCover.jpg";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";

const Profile = () => {
  const context = useContext(vividlyContext);
  const {
    showSuggestionFunc,
    suggestions,
    handleDeletePost,
    handleEditPost,
    handleUpdateProfileCover,
  } = context;
  const [userPost, setUserPost] = useState([]);
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const [userProfile, setUserProfile] = useState("");
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);

  const [profileCoverPic, setProfileCoverPic] = useState(profileCover);
  const [showModal, setShowModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(profileCover);

  const handlefetchUserProfile = async () => {
    const id = localStorage.getItem("id");
    const parsedId = JSON.parse(id);
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    try {
      const response = await fetch(
        `${ENDPOINT}/api/user/getUserProfile/${parsedId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setUserProfile(data);
      } else {
        console.log("Error");
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  useEffect(() => {
    handlefetchUserProfile();
    showSuggestionFunc();
  }, [userPost]);
  const [navigation, setNavigation] = useState("posts");
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState("");
  const [caption, setCaption] = useState("");

  const [media, setMedia] = useState("");

  const handleFetchPostLikes = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostLikes/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postLikes.likes;
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleLikePost = async (postId) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);
    const data = await fetch(`${ENDPOINT}/api/post/likePost/${postId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token": parsedToken,
      },
    });
    const response = await data.json();
    const userId = localStorage.getItem("id");
    const parsedUserId = JSON.parse(userId);

    const post = userPost.find((post) => post._id === postId);
    if (post) {
      const userAlreadyLiked = post.likes.some(
        (like) => like.user === parsedUserId
      );

      if (userAlreadyLiked) {
        setUserPost(
          userPost.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: p.likes.filter((like) => like.user !== parsedUserId),
                  likeStatus: "Not Liked",
                }
              : p
          )
        );
      } else {
        setUserPost(
          userPost.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: [...p.likes, { user: parsedUserId }],
                  likeStatus: "Liked",
                }
              : p
          )
        );
      }
    }

    return response;
  };

  const handleFetchPostComments = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostComments/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postComments.comments; // Return the likes for a given post
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleFetchUserPosts = async () => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/post/fetchUserPosts`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
      });
      const data = await response.json();
      if (data.success) {
        const postsWithDetails = await Promise.all(
          data.uPost.map(async (post) => {
            const likes = await handleFetchPostLikes(post._id);
            const comments = await handleFetchPostComments(post._id);
            let likeStatus = likes.some((like) => like.user === parsedId)
              ? "Liked"
              : "Not Liked";

            return { ...post, likes, comments, likeStatus };
          })
        );
        setUserPost(postsWithDetails);
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  useEffect(() => {
    handleFetchUserPosts();
  }, []);

  const handleMediaChange = async (e) => {
    const file = e.target.files[0];
    if (!file) {
      return console.log("Please upload a file!");
    }

    const formdata = new FormData();
    formdata.append("media", file);

    try {
      const data = await fetch(`${ENDPOINT}/api/post/media`, {
        method: "POST",
        body: formdata,
      });
      const response = await data.json();
      if (response.success) {
        setMedia(response.url);
      }
    } catch (error) {
      console.log(error);
    }

    setMediaPreview(URL.createObjectURL(file));
    setMediaFile(file);
  };

  const handleEditMediaChange = async (e) => {
    const file = e.target.files[0];
    if (!file) {
      return console.log("Please upload a file!");
    }

    const formdata = new FormData();
    formdata.append("media", file);

    try {
      const data = await fetch(`${ENDPOINT}/api/post/media`, {
        method: "POST",
        body: formdata,
      });
      const response = await data.json();
      if (response.success) {
        if (file.name.endsWith(".mp4")) {
          setEditPostDetails({
            ...editPostDetails,
            postMedia: URL.createObjectURL(file),
            postMediaType: "video",
          });
        } else {
          setEditPostDetails({
            ...editPostDetails,
            postMedia: URL.createObjectURL(file),
            postMediaType: "image",
          });
        }
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleCaptionChange = (e) => {
    setCaption(e.target.value);
  };

  const handleCreatePost = async () => {
    try {
      const mediaType = mediaFile.type.startsWith("video/") ? "video" : "image";
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/post/createPost`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
        body: JSON.stringify({
          description: caption,
          media: media,
          mediaType: mediaType,
        }),
      });
      const data = await response.json();
      setMediaFile(null);
      setMediaPreview("");
      setCaption("");
      setUserPost([...userPost, data]);
      await handleFetchUserPosts();
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const [specificPostComments, setSpecificPostComments] = useState([]);
  const handleSpecificPostComments = (id) => {
    const post = userPost.find((post) => post._id === id);
    setSpecificPostComments(post.comments);
  };

  const [specificPostId, setSpecificPosId] = useState("");

  const handleStoreSpecificPostId = (id) => {
    setSpecificPosId(id);
  };

  const [cText, setCText] = useState("");
  const handleCommentOnChange = (e) => {
    setCText(e.target.value);
  };

  const handleAddComment = async (postId, text, event) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    if (event.key === "Enter" || event.key === "enter") {
      setCText("");
      try {
        const response = await fetch(
          `${ENDPOINT}/api/post/commentOnPost/${postId}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
              "auth-token": parsedToken,
            },
            body: JSON.stringify({
              text,
            }),
          }
        );
        const data = await response.json();
        if (data.success) {
          setSpecificPostComments(data.updatedComment.comments);
          setUserPost(
            userPost.map((p) =>
              p._id === postId
                ? {
                    ...p,
                    comments: data.updatedComment.comments,
                  }
                : p
            )
          );
        }
      } catch (error) {
        console.log("Error:", error.message);
      }
    }
  };

  const [userFollowers, setUserFollowers] = useState([]);

  const handleFetchUFollowersList = async (userId) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);
    try {
      const response = await fetch(
        `${ENDPOINT}/api/user/userFollowersList/${userId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setUserFollowers(data.followersList.followersList);
      } else {
        console.log("Error");
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  const deletePostFunc = async (postId) => {
    const confirm = window.confirm("Are you sure to delete this post?");
    if (confirm) {
      const response = await handleDeletePost(postId);
      if (response.success) {
        setUserPost(userPost.filter((p) => p._id !== postId));
      }
    }
  };
  useEffect(() => {
    handleFetchUFollowersList(parsedId);
  }, []);

  const [editPostDetails, setEditPostDetails] = useState({
    postId: "",
    postDescription: "",
    postMedia: "",
    postMediaType: "",
  });

  const handleEditPostDetails = (pId, pDesc, pMedia, pMediaType) => {
    setEditPostDetails({
      postId: pId,
      postDescription: pDesc,
      postMedia: pMedia,
      postMediaType: pMediaType,
    });
  };

  const editModalref = useRef(null);

  const handleUpdatePostFunc = async (postId, desc, media, mediaType) => {
    editModalref.current.close();
    const response = await handleEditPost(postId, desc, media, mediaType);
    if (response.success) {
      setUserPost(
        userPost.map((p) =>
          p._id === postId
            ? {
                ...p,
                description: editPostDetails.postDescription,
                media: editPostDetails.postMedia,
                mediaType: editPostDetails.postMediaType,
              }
            : p
        )
      );
    }
  };
  const [loading, setLoading] = useState({});
  const [isFollow, setIsFollow] = useState({});

  const handleFollowUnFollow = async (uId) => {
    try {
      setLoading({ ...loading, [uId]: true });

      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);

      const response = await fetch(
        `${ENDPOINT}/api/user/followUser/${parsedId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
          body: JSON.stringify({ user: uId }),
        }
      );
      const data = await response.json();
      if (data.message === "Followed the user successfully!") {
        setIsFollow({ ...isFollow, [uId]: true });
      } else {
        setIsFollow({ ...isFollow, [uId]: false });
      }
      setLoading({ ...loading, [uId]: false });
    } catch (error) {
      console.log("Error:", error);
    }
  };

  const handleOpenModal = () => setShowModal(true);

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formdata = new FormData();
      formdata.append("media", file);
      try {
        const data = await fetch(`${ENDPOINT}/api/post/media`, {
          method: "POST",
          body: formdata,
        });
        const response = await data.json();
        if (response.successs) {
          setSelectedImage(response.url);
        }
      } catch (error) {
        console.log(error);
      }
    }
  };
  // const imageUrl = URL.createObjectURL(file);

  // // Update profile picture on button click
  // const handleUpdateProfilePic = () => {
  //   setProfileCoverPic(selectedImage);
  //   setShowModal(false);
  // };

  const UpdateProfileCoverFunc = async (pic) => {
    const response = await handleUpdateProfileCover(pic);
    if (response.success) {
      setProfileCoverPic(pic);
      setShowModal(false);
    }
  };

  return (
    <div className="max-w-7xl mx-auto min-h-screen sm:pl-72 sm:pt-24 p-4">
      {/* Header Section */}

      <div className="relative bg-gray-200 h-48 rounded-lg">
        {/* Cover Image */}
        <img
          src={profileCoverPic}
          alt="Cover"
          className="w-full h-full object-cover rounded-lg"
        />

        {/* Profile Section */}
        <div className="absolute bottom-[-50%] left-6 flex flex-col">
          <img
            src={userProfile.uPic || Avatar}
            alt="Profile"
            className="w-36 h-36 rounded-full object-cover 
            border-[4px] border-white shadow-lg"
          />

          <div className="ml-4 mt-2">
            <h2 className="text-2xl text-black font-bold">
              {userProfile?.uFullname}
            </h2>
            <p className="text-gray-600 mt-[-2px]">@{userProfile?.uUsername}</p>
          </div>
        </div>

        {/* Edit Icon with Improved Styling */}
        <div className="absolute top-4 right-4 flex space-x-2 hover:cursor-pointer">
          <FontAwesomeIcon
            className="text-2xl text-gray-700 bg-white p-2 rounded-full 
            shadow-md border border-gray-300 hover:bg-blue-500 
            hover:text-white transition-all duration-300"
            icon={faPenToSquare}
          />
        </div>

        {/* Edit Icon for Modal Trigger */}
        <div className="absolute top-4 right-4 flex space-x-2 hover:cursor-pointer">
          <FontAwesomeIcon
            className="text-2xl text-gray-700 bg-white p-2 rounded-full 
                    shadow-md border border-gray-300 hover:bg-blue-500 
                    hover:text-white transition-all duration-300"
            icon={faPenToSquare}
            onClick={handleOpenModal}
          />
        </div>

        {/* Modal for Profile Picture Edit */}
        {showModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg text-center">
              <h2 className="text-2xl font-semibold mb-4 text-black">
                Update Profile Picture
              </h2>
              {/* Display Current Image in Modal */}
              <img
                src={selectedImage}
                alt="Selected Pic"
                className="w-48 h-48 rounded-full object-cover mx-auto border-2 border-gray-300"
              />

              {/* File Upload Button inside Modal */}
              <label className="flex items-center justify-center gap-2 cursor-pointer mt-4">
                <FontAwesomeIcon
                  icon={faCamera}
                  className="text-2xl text-gray-600"
                />
                <input
                  type="file"
                  accept="image/*"
                  className="hidden"
                  onChange={handleImageChange}
                />
                <span className="text-blue-600 underline">Change Image</span>
              </label>

              {/* Save & Cancel Buttons */}
              <div className="flex justify-center mt-6 space-x-4">
                <button
                  onClick={() => {
                    setShowModal(false);
                  }}
                  className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                >
                  Save Changes
                </button>
                <button
                  onClick={() => setShowModal(false)}
                  className="px-6 py-2 bg-gray-400 text-white rounded-lg hover:bg-gray-500"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* User Stats */}
      <div className="mt-[90px] bg-white rounded-lg shadow-md p-4 grid grid-cols-3 text-center">
        <div>
          <h3 className="text-lg font-bold text-black">
            {userProfile && userProfile.uPosts.length}
          </h3>
          <p className="text-black">Posts</p>
        </div>
        <div>
          <h3 className="text-lg font-bold text-black">
            {userProfile && userProfile.uFollowers.followersList.length}
          </h3>
          <p className="text-black">Followers</p>
        </div>

        <div>
          <h3 className="text-lg font-bold text-black">
            {userProfile && userProfile.uFollowing.followingList.length}
          </h3>
          <p className="text-black">Following</p>
        </div>
      </div>
      <div className="p-6 bg-white rounded-lg shadow-lg mt-2">
        {/* Upload Section */}
        <label
          htmlFor="mediaUpload"
          className="cursor-pointer flex flex-col items-center justify-center border-2 border-dashed border-gray-300 p-6 rounded-lg hover:border-blue-500 transition"
        >
          {mediaPreview ? (
            <p className="text-blue-500 font-semibold">Change Media</p>
          ) : (
            <>
              <svg
                className="w-12 h-12 text-gray-500"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path d="M12 4v16m8-8H4"></path>
              </svg>
              <p className="text-gray-600 mt-2 text-center">
                Click to upload an image or video
              </p>
            </>
          )}
          <input
            type="file"
            id="mediaUpload"
            className="hidden"
            accept="image/*, video/*"
            onChange={handleMediaChange}
          />
        </label>

        {/* Media Preview Section */}
        {mediaPreview && (
          <div className="mt-4">
            {mediaFile.type.startsWith("video/") ? (
              <video
                src={mediaPreview}
                controls
                className="w-full h-auto rounded-lg"
              />
            ) : (
              <img
                src={mediaPreview}
                alt="Media Preview"
                className="w-full h-auto rounded-lg"
              />
            )}
          </div>
        )}

        {/* Text Caption */}
        {mediaPreview && (
          <textarea
            className="mt-4 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none text-black"
            placeholder="Write a caption..."
            value={caption}
            onChange={handleCaptionChange}
          />
        )}

        {/* Action Buttons */}
        <div className="flex justify-end mt-4 space-x-3">
          {mediaFile && (
            <>
              <button
                className="py-2 px-6 bg-gray-200 text-black rounded-lg hover:bg-gray-300"
                onClick={() => {
                  setMediaFile(null);
                  setMediaPreview("");
                  setCaption("");
                }}
                disabled={!mediaFile}
              >
                Cancel
              </button>
              <button
                className={`py-2 px-6 rounded-lg text-white ${
                  mediaFile && caption
                    ? "bg-blue-600 hover:bg-blue-700"
                    : "bg-gray-400 cursor-not-allowed"
                }`}
                onClick={handleCreatePost}
                disabled={!mediaFile || !caption}
              >
                Create Post
              </button>
            </>
          )}
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="mt-6 flex space-x-4">
        <button
          className={`py-2 ${
            navigation === "posts"
              ? "border-blue-500 border-b-2 text-blue-500"
              : "text-gray-500 hover:text-blue-500"
          }`}
          onClick={() => {
            setNavigation("posts");
          }}
        >
          Posts
        </button>
        <button
          className={`py-2 ${
            navigation === "about"
              ? "border-blue-500 border-b-2 text-blue-500"
              : "text-gray-500 hover:text-blue-500"
          }`}
          onClick={() => {
            setNavigation("about");
          }}
        >
          About
        </button>
        <button
          className={`py-2 ${
            navigation === "follower"
              ? "border-blue-500 border-b-2 text-blue-500"
              : "text-gray-500 hover:text-blue-500"
          }`}
          onClick={() => {
            setNavigation("follower");
          }}
        >
          Followers
        </button>
      </div>
      <dialog id="editPost_Modal" ref={editModalref} className="modal">
        <div className="modal-box max-h-[90vh] overflow-y-auto bg-white rounded-lg shadow-2xl p-6 border border-gray-200">
          <form method="dialog">
            <button className="btn btn-sm btn-circle btn-ghost absolute right-4 top-4 text-black hover:text-red-600 transition duration-300">
              
            </button>
          </form>

          <div className="flex justify-center mb-6">
            <h1 className="text-4xl font-extrabold text-gray-900">Edit Post</h1>
          </div>

          {/* Media Preview with Change Icon */}
          <div className="relative w-full flex justify-center mb-8 group">
            {editPostDetails.postMediaType === "image" ? (
              <img
                src={editPostDetails.postMedia || Avatar}
                alt="Post Media"
                className="w-full max-h-96 object-cover rounded-xl shadow-lg border border-gray-300"
              />
            ) : (
              <video
                src={editPostDetails.postMedia || Avatar}
                controls
                className="w-full max-h-96 object-cover rounded-xl shadow-lg border border-gray-300"
              />
            )}

            {/* Change Media Icon Overlay */}
            <label className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300 cursor-pointer">
              <input
                type="file"
                accept="image/*, video/*"
                className="hidden"
                onChange={(e) => handleEditMediaChange(e)}
              />
              <FontAwesomeIcon
                icon={faCamera}
                className="text-white text-5xl cursor-pointer drop-shadow-lg"
              />
            </label>
          </div>

          {/* Post Description */}
          <div className="flex flex-col mb-6">
            <label className="text-xl font-medium text-gray-800 mb-2">
              Post Description
            </label>
            <textarea
              className="w-full p-4 border border-gray-300 text-black rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none shadow-sm"
              rows="3"
              value={editPostDetails.postDescription}
              onChange={(e) =>
                setEditPostDetails({
                  ...editPostDetails,
                  postDescription: e.target.value,
                })
              }
            />
          </div>

          {/* Update Button */}
          <div className="flex justify-end">
            <button
              onClick={() =>
                handleUpdatePostFunc(
                  editPostDetails.postId,
                  editPostDetails.postDescription,
                  editPostDetails.postMedia,
                  editPostDetails.postMediaType
                )
              }
              className="bg-gradient-to-r from-blue-500 to-blue-700 text-white px-8 py-3 rounded-lg font-semibold shadow-lg hover:from-blue-600 hover:to-blue-800 transition duration-300 transform hover:scale-105"
            >
              Update Post
            </button>
          </div>
        </div>
      </dialog>

      <dialog id="addComment_Modal" className="modal">
        <div className="modal-box min-h-[80vh] overflow-hidden bg-white rounded-lg shadow-lg relative">
          <form method="dialog">
            <button className="btn btn-sm btn-circle btn-ghost absolute right-4 top-4 text-black hover:bg-opacity-70 transition">
              
            </button>
          </form>

          <div className="flex justify-center mb-5">
            <h1 className="cus-font text-black font-semibold text-4xl mb-4">
              Add a comment
            </h1>
          </div>

          <div className="space-y-3 overflow-y-auto max-h-[60vh] pr-2 mb-[80px]">
            {specificPostComments.length > 0 ? (
              specificPostComments.map((cData) => (
                <div
                  key={cData._id}
                  className="flex bg-gray-100 rounded-xl p-2 shadow-md border border-gray-300 hover:bg-gray-200 transition"
                >
                  <img
                    src={cData.userPic}
                    alt="Failed to load user img"
                    className="rounded-full h-16 w-16 border-2 border-gray-400"
                  />
                  <div className="flex flex-col ml-4">
                    <p className="font-semibold text-black">{cData.userName}</p>
                    <p className="text-gray-700">{cData.text}</p>
                  </div>
                </div>
              ))
            ) : (
              <div className="flex justify-center items-center h-[55vh] custom-font text-2xl sm:text-3xl text-gray-600">
                No Comments Here
              </div>
            )}
          </div>

          {/* Fixed position input */}
          <div className="absolute bottom-5 left-0 w-full px-4">
            <input
              className="w-full rounded-full bg-gray-100 focus:ring-1  focus:ring-blue-500 py-2 px-4 text-black shadow-md focus:outline-none transition"
              type="text"
              placeholder="Add a comment"
              value={cText}
              onChange={handleCommentOnChange}
              onKeyDown={(event) =>
                handleAddComment(specificPostId, cText, event)
              }
            />
          </div>
        </div>
      </dialog>

      {/* Posts Section */}
      {navigation === "posts" && (
        <div className="grid grid-cols-1 md:grid-cols-2 elg:grid-cols-3 gap-6 mt-6 p-4">
          {userPost.map((post) => (
            <div
              key={post._id}
              className="bg-white border border-gray-300 shadow-lg rounded-lg overflow-hidden transition-transform transform hover:scale-105 duration-300"
            >
              {/* Post Media Section */}
              <div className="relative group">
                {post.mediaType === "image" ? (
                  <img
                    src={post.media}
                    alt="Post Media"
                    className="w-full h-64 object-cover"
                  />
                ) : (
                  <video
                    src={post.media}
                    controls
                    className="w-full h-64 object-cover"
                  />
                )}
                {/* Edit and Delete Icons */}
                <div className="absolute top-2 right-2 flex space-x-2">
                  <FontAwesomeIcon
                    icon={faPenRegular}
                    className="text-white text-2xl cursor-pointer bg-black bg-opacity-60 p-2 rounded-full hover:text-blue-400"
                    onClick={() => {
                      handleEditPostDetails(
                        post._id,
                        post.description,
                        post.media,
                        post.mediaType
                      );
                      document.getElementById("editPost_Modal").showModal();
                    }}
                  />
                  <FontAwesomeIcon
                    icon={faTrashAlt}
                    className="text-white text-2xl cursor-pointer bg-black bg-opacity-60 p-2 rounded-full hover:text-red-500"
                    onClick={() => deletePostFunc(post._id)}
                  />
                </div>
              </div>

              {/* Post Description Section */}
              <div className="p-4">
                <p className="text-lg text-gray-800 font-semibold mb-2">
                  {post.description || "No description provided."}
                </p>
              </div>

              {/* Like, Comment, Share Section */}
              <div className="p-3 border-t border-gray-300 flex justify-between items-center">
                <div className="flex items-center space-x-4">
                  <FontAwesomeIcon
                    icon={post.likeStatus === "Liked" ? LikeHeart : faHeart}
                    className={`text-2xl cursor-pointer ${
                      post.likeStatus === "Liked"
                        ? "text-red-600"
                        : "text-gray-600"
                    } hover:scale-110 transition-transform`}
                    onClick={() => handleLikePost(post._id)}
                  />
                  <span className="text-lg text-gray-700">
                    {post.likes?.length}
                  </span>

                  <FontAwesomeIcon
                    icon={faComment}
                    className="text-2xl cursor-pointer text-gray-600 hover:text-blue-500"
                    onClick={() => {
                      handleSpecificPostComments(post._id);
                      handleStoreSpecificPostId(post._id);
                      document.getElementById("addComment_Modal").showModal();
                    }}
                  />
                  <span className="text-lg text-gray-700">
                    {post.comments?.length}
                  </span>
                </div>

                {/* Share Button */}
                <FontAwesomeIcon
                  icon={faPaperPlane}
                  className="text-2xl cursor-pointer text-gray-600 hover:text-blue-500"
                />
              </div>
            </div>
          ))}
        </div>
      )}

      {userPost.length === 0 ? (
        <p className="text-2xl text-center text-gray-600 mt-5">
          No posts yet. Share something to get started!
        </p>
      ) : (
        ""
      )}
      {navigation === "about" && (
        <div className="bg-white p-6 mt-4 rounded-lg shadow-md">
          <h3 className="text-2xl font-bold mb-4">About John Doe</h3>

          {/* User's Bio */}
          <p className="text-gray-700 mb-4">
            Hi, I'm John Doe! I'm a passionate software engineer, explorer, and
            tech enthusiast. I love building innovative solutions that make a
            difference. Currently working as a Senior Developer at TechCorp.
          </p>

          {/* User's Info */}
          <ul className="space-y-2 text-gray-700">
            <li>
              <strong>Location:</strong> San Francisco, CA
            </li>
            <li>
              <strong>Job Title:</strong> Senior Developer at TechCorp
            </li>
            <li>
              <strong>Education:</strong> B.S. in Computer Science from UC
              Berkeley
            </li>
            <li>
              <strong>Interests:</strong> Hiking, Photography, Open-Source
              Development
            </li>
          </ul>
        </div>
      )}
      {navigation === "follower" && (
        <div className="bg-white p-6 mt-4 rounded-lg shadow-md">
          <h3 className="text-2xl font-bold mb-4 text-black">Followers</h3>

          {/* Followers Section */}
          <div className="overflow-y-auto max-h-52">
            {/* Adjust max height to control scroll */}
            <ul className="space-y-4">
              {userFollowers.map((user) => {
                return (
                  <li key={user._id} className="flex items-center space-x-3">
                    <img
                      src={user.userPic}
                      alt="Failed to load img"
                      className="w-10 h-10 rounded-full object-cover"
                    />
                    <span className="text-gray-800">{user.name}</span>
                  </li>
                );
              })}
              {userFollowers.length === 0 ? (
                <p className="text-lg text-center text-gray-600">
                  No followers yet. Start connecting with people!
                </p>
              ) : (
                ""
              )}
            </ul>
          </div>
        </div>
      )}

      {/* Suggestions Section */}
      <div className="mt-6 bg-white p-4 rounded-lg shadow-md max-h-64 overflow-y-auto">
        <h3 className="text-lg font-bold mb-4 text-black">
          Suggestions for You
        </h3>
        <ul className="space-y-3">
          {suggestions.map((data) => {
            return (
              <li
                key={data._id}
                className="flex items-center justify-between bg-gray-100 p-3 rounded-lg"
              >
                <div className="flex items-center space-x-3">
                  <img
                    src={data.picture}
                    alt="Failed to load Img"
                    className="w-12 h-12 rounded-full object-cover"
                  />
                  <p className="font-bold text-black">{data.fullName}</p>
                </div>
                <button
                  className="text-blue-500 hover:underline"
                  onClick={() => handleFollowUnFollow(data._id)}
                >
                  {loading[data._id] ? (
                    <div className="flex items-center justify-center">
                      <span className="ml-2">Loading...</span>
                    </div>
                  ) : isFollow[data._id] ? (
                    "Unfollow"
                  ) : (
                    "Follow"
                  )}
                </button>
              </li>
            );
          })}
          {suggestions.length < 0 || suggestions.length === 0 ? (
            <p className="text-gray-700">No suggestions available!</p>
          ) : (
            ""
          )}
        </ul>
      </div>
    </div>
  );
};

export default Profile;
