import React from "react";

const SkeletonLoader = () => {
  return (
    <div className="max-w-7xl mx-auto min-h-screen sm:pl-72 sm:pt-24 p-4">
      {/* Header Section Skeleton */}
      <div className="bg-gray-200 h-12 w-full animate-pulse rounded-lg mb-6"></div>

      {/* Profile Section Skeleton */}
      <div className="relative bg-gray-200 h-44 md:h-48 lg:h-56 rounded-lg animate-pulse mb-6">
        {/* Cover Image Skeleton */}
        <div className="w-full h-full bg-gray-300 rounded-lg"></div>

        {/* Profile Section Skeleton */}
        <div className="absolute bottom-[-50%] left-6 flex flex-col items-center">
          {/* Profile Picture Skeleton */}
          <div className="w-36 h-36 rounded-full bg-gray-300 mb-4"></div>

          <div className="ml-4 mt-2">
            {/* User Name Skeleton */}
            <div className="h-4 w-40 bg-gray-300 rounded-lg mb-2"></div>
            {/* Username Skeleton */}
            <div className="h-3 w-32 bg-gray-300 rounded-lg"></div>
          </div>
        </div>
      </div>

      {/* User Stats Skeleton */}
      <div className="mt-6 bg-white rounded-lg shadow-md p-4 grid grid-cols-3 text-center animate-pulse">
        <div>
          <div className="h-6 w-16 bg-gray-300 rounded-lg mb-2"></div>
          <div className="h-4 w-20 bg-gray-300 rounded-lg"></div>
        </div>
        <div>
          <div className="h-6 w-16 bg-gray-300 rounded-lg mb-2"></div>
          <div className="h-4 w-20 bg-gray-300 rounded-lg"></div>
        </div>
        <div>
          <div className="h-6 w-16 bg-gray-300 rounded-lg mb-2"></div>
          <div className="h-4 w-20 bg-gray-300 rounded-lg"></div>
        </div>
      </div>

      {/* Upload Section Skeleton */}
      <div className="p-6 bg-white rounded-lg shadow-lg mt-2 animate-pulse">
        <label className="cursor-pointer flex flex-col items-center justify-center border-2 border-dashed border-gray-300 p-6 rounded-lg hover:border-blue-500 transition">
          <div className="w-12 h-12 bg-gray-300 mb-2"></div>
          <div className="h-4 w-36 bg-gray-300 rounded-lg"></div>
        </label>

        {/* Media Preview Skeleton */}
        <div className="mt-4">
          <div className="w-full h-44 bg-gray-300 rounded-lg"></div>
        </div>

        {/* Text Caption Skeleton */}
        <div className="mt-4">
          <div className="h-12 w-full bg-gray-300 rounded-lg"></div>
        </div>

        {/* Action Buttons Skeleton */}
        <div className="flex justify-end mt-4 space-x-3">
          <div className="h-10 w-24 bg-gray-300 rounded-lg"></div>
          <div className="h-10 w-24 bg-gray-300 rounded-lg"></div>
        </div>
      </div>
    </div>
  );
};

export default SkeletonLoader;
