import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";

const Groups = () => {
  const context = useContext(vividlyContext);
  const { handleFetchJoinedGroups, handleFetchFeaturedGroups } = context;
  const [joinedGroups, setJoinedGroups] = useState([]);
  const [featuredGroups, setFeaturedGroups] = useState([]);

  const fetchJoinedGroups = async () => {
    const response = await handleFetchJoinedGroups();
    if (response.success) {
      setJoinedGroups(response.data);
    }
  };

  const fetchFeaturedGroups = async () => {
    const response = await handleFetchFeaturedGroups();
    if (response.success) {
      setFeaturedGroups(response.data);
    }
  };

  // const groups = [
  //   {
  //     _id: "678c9f72399b4fbcb777ac5f",
  //     groupName: "Lahori boys",
  //     groupPicture: "",
  //     participants: [
  //       "678b91ead7a1f1d3821042a4",
  //       "678ba2c2050ea15598655381",
  //       "678b9161d7a1f1d382104204",
  //     ],
  //     admins: ["678b9161d7a1f1d382104204"],
  //     createdBy: "678b9161d7a1f1d382104204",
  //     description: "New Group",
  //     createdAt: "2025-01-19T06:45:06.257Z",
  //     updatedAt: "2025-01-19T06:45:06.257Z",
  //   },
  //   // Add more group objects here
  // ];

  useEffect(() => {
    fetchJoinedGroups();
    fetchFeaturedGroups();
  }, []);

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 mx-auto bg-white p-6 mt-4 rounded-lg shadow-md">
      {/* Search */}
      <div className="mt-4 flex justify-between items-center">
        <input
          type="text"
          placeholder="Search for groups..."
          className="p-2 border rounded-lg w-full"
        />
      </div>

      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 text-black">
          Your Joined Groups
        </h3>
        <div className="grid grid-cols-1 lsm:grid-cols-2 lg:grid-cols-3 gap-6">
          {joinedGroups.length === 0 ? (
            <p className="text-gray-500">You have not joined any groups yet.</p>
          ) : (
            joinedGroups.map((group) => (
              <div
                key={group._id}
                className="bg-gray-100 p-4 rounded-lg shadow hover:bg-gray-200"
              >
                <div className="flex items-center justify-between">
                  <div className="text-xl font-bold text-black">
                    {group.groupName}
                  </div>
                  <span className="text-sm text-gray-500">
                    {group.participants.length} members
                  </span>
                </div>
                <p className="text-gray-700 mt-2">{group.description}</p>
                <button className="mt-4 text-blue-500 hover:underline">
                  Visit Group
                </button>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 text-black">Featured Groups</h3>
        <div className="grid grid-cols-1 lsm:grid-cols-2 lg:grid-cols-3 gap-6">
          {featuredGroups.length === 0 ? (
            <p className="text-gray-600 text-md text-md font-semibold mt-2">
              No featured groups to show right now.
            </p>
          ) : (
            featuredGroups.map((group) => {
              return (
                <div
                  key={group._id}
                  className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 ease-in-out transform hover:scale-105"
                >
                  <div className="flex items-center justify-between">
                    <div className="text-2xl font-semibold text-gray-800">
                      {group.groupName}
                    </div>
                    <span className="text-sm text-gray-500">
                      {group.participants.length} members
                    </span>
                  </div>
                  <p className="text-gray-600 mt-3 text-lg">
                    {group.description}
                  </p>
                  <button className="mt-6 px-5 py-2 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors duration-300">
                    Join Group
                  </button>
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
};

export default Groups;
