import React, { useContext, useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPenToSquare,
  faHeart as LikeHeart,
} from "@fortawesome/free-solid-svg-icons";
import {
  faComment,
  faHeart,
  faPaperPlane,
  faPenToSquare as faPenRegular,
  faTrashAlt,
} from "@fortawesome/free-regular-svg-icons";
import profileCover from "../Images/profileCover.jpg";
import vividlyContext from "../Context/vividlyContext";
import Avatar from "../Images/Avatar.jpeg";

const Profile = () => {
  const context = useContext(vividlyContext);
  const { showSuggestionFunc, suggestions } = context;
  const [userPost, setUserPost] = useState([]);
  const ENDPOINT = process.env.REACT_APP_ENDPOINT;
  const [userProfile, setUserProfile] = useState("");

  const handlefetchUserProfile = async () => {
    const id = localStorage.getItem("id");
    const parsedId = JSON.parse(id);
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);

    try {
      const response = await fetch(
        `${ENDPOINT}/api/user/getUserProfile/${parsedId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        setUserProfile(data);
      } else {
        console.log("Error");
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };
  useEffect(() => {
    handlefetchUserProfile();
    showSuggestionFunc();
  }, [userPost]);
  const [navigation, setNavigation] = useState("posts");
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState("");
  const [caption, setCaption] = useState("");

  const [media, setMedia] = useState("");

  const [isULikePost, setIsULikePost] = useState(false);

  const handleFetchPostLikes = async (postId) => {
    const userId = localStorage.getItem("id");
    const parsedUserId = JSON.parse(userId);

    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostLikes/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        const alreadyLiked = data.postLikes.likes.some(
          (like) => like.user === parsedUserId
        );
        if (alreadyLiked) {
          setIsULikePost(true);
        }
        return data.postLikes.likes;
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleFetchPostComments = async (postId) => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(
        `${ENDPOINT}/api/post/fetchPostComments/${postId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": parsedToken,
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        return data.postComments.comments; // Return the likes for a given post
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleFetchUserPosts = async () => {
    try {
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/post/fetchUserPosts`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
      });
      const data = await response.json();
      if (data.success) {
        const postsWithDetails = await Promise.all(
          data.uPost.map(async (post) => {
            const likes = await handleFetchPostLikes(post._id);
            const comments = await handleFetchPostComments(post._id);
            return { ...post, likes, comments };
          })
        );
        setUserPost(postsWithDetails);
      }
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  const handleLikePost = async (postId) => {
    const token = localStorage.getItem("token");
    const parsedToken = JSON.parse(token);
    const data = await fetch(`${ENDPOINT}/api/post/likePost/${postId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token": parsedToken,
      },
    });
    const response = await data.json();
    const userId = localStorage.getItem("id");
    const parsedUserId = JSON.parse(userId);

    const post = userPost.find((post) => post._id === postId);
    if (post) {
      const userAlreadyLiked = post.likes.some(
        (like) => like.user === parsedUserId
      );

      if (userAlreadyLiked) {
        setUserPost(
          userPost.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: p.likes.filter((like) => like.user !== parsedUserId),
                }
              : p
          )
        );
      } else {
        setUserPost(
          userPost.map((p) =>
            p._id === postId
              ? {
                  ...p,
                  likes: [...p.likes, { user: parsedUserId }],
                }
              : p
          )
        );
      }
    }

    return response;
  };

  useEffect(() => {
    handleFetchUserPosts();
  }, []);

  const handleMediaChange = async (e) => {
    const file = e.target.files[0];
    if (!file) {
      return console.log("Please upload a file!");
    }

    const formdata = new FormData();
    formdata.append("media", file);

    try {
      const data = await fetch(`${ENDPOINT}/api/post/media`, {
        method: "POST",
        body: formdata,
      });
      const response = await data.json();
      if (response.success) {
        setMedia(response.url);
      }
    } catch (error) {
      console.log(error);
    }

    setMediaPreview(URL.createObjectURL(file));
    setMediaFile(file);
  };

  const handleCaptionChange = (e) => {
    setCaption(e.target.value);
  };

  const handleCreatePost = async () => {
    try {
      const mediaType = mediaFile.type.startsWith("video/") ? "video" : "image";
      const token = localStorage.getItem("token");
      const parsedToken = JSON.parse(token);
      const response = await fetch(`${ENDPOINT}/api/post/createPost`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": parsedToken,
        },
        body: JSON.stringify({
          description: caption,
          media: media,
          mediaType: mediaType,
        }),
      });
      const data = await response.json();
      setMediaFile(null);
      setMediaPreview("");
      setCaption("");
      setUserPost([...userPost, data]);
    } catch (error) {
      console.log("Error:", error.message);
    }
  };

  return (
    <div className="max-w-7xl mx-auto min-h-screen sm:pl-72 sm:pt-24 p-4">
      {/* Header Section */}
      <div className="relative bg-gray-200 h-48 rounded-lg">
        <img
          src={profileCover}
          alt="Cover"
          className="w-full h-full object-cover"
        />
        <div className="absolute bottom-[-50%] left-6 flex flex-col">
          <img
            src={userProfile.uPic || Avatar}
            alt="Profile"
            className="w-36 h-36 rounded-full object-cover 
              border-[3px] border-gray-800"
          />

          <div className="ml-4">
            <h2 className="text-2xl text-black font-bold">
              {userProfile && userProfile.uFullname}
            </h2>
            <p className="text-gray-600 mt-[-4px]">
              {userProfile && userProfile.uUsername}
            </p>
          </div>
        </div>
        <div className="absolute top-4 right-4 flex space-x-2 hover:cursor-pointer">
          <FontAwesomeIcon
            className="text-2xl text-black bg-white p-1 rounded-md"
            icon={faPenToSquare}
          />
        </div>
      </div>

      {/* User Stats */}
      <div className="mt-[90px] bg-white rounded-lg shadow-md p-4 grid grid-cols-3 text-center">
        <div>
          <h3 className="text-lg font-bold text-black">
            {userProfile && userProfile.uPosts.length}
          </h3>
          <p className="text-black">Posts</p>
        </div>
        <div>
          <h3 className="text-lg font-bold text-black">
            {userProfile && userProfile.uFollowers.length}
          </h3>
          <p className="text-black">Followers</p>
        </div>
        <div>
          <h3 className="text-lg font-bold text-black">
            {userProfile && userProfile.uFollowing.length}
          </h3>
          <p className="text-black">Following</p>
        </div>
      </div>
      <div className="max-w-7xl p-6 bg-white rounded-lg shadow-lg mt-2">
        {/* Upload Section */}
        <label
          htmlFor="mediaUpload"
          className="cursor-pointer flex flex-col items-center justify-center border-2 border-dashed border-gray-300 p-6 rounded-lg hover:border-blue-500 transition"
        >
          {mediaPreview ? (
            <p className="text-blue-500 font-semibold">Change Media</p>
          ) : (
            <>
              <svg
                className="w-12 h-12 text-gray-500"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path d="M12 4v16m8-8H4"></path>
              </svg>
              <p className="text-gray-600 mt-2 text-center">
                Click to upload an image or video
              </p>
            </>
          )}
          <input
            type="file"
            id="mediaUpload"
            className="hidden"
            accept="image/*, video/*"
            onChange={handleMediaChange}
          />
        </label>

        {/* Media Preview Section */}
        {mediaPreview && (
          <div className="mt-4">
            {mediaFile.type.startsWith("video/") ? (
              <video
                src={mediaPreview}
                controls
                className="w-full h-auto rounded-lg"
              />
            ) : (
              <img
                src={mediaPreview}
                alt="Media Preview"
                className="w-full h-auto rounded-lg"
              />
            )}
          </div>
        )}

        {/* Text Caption */}
        {mediaPreview && (
          <textarea
            className="mt-4 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none text-black"
            placeholder="Write a caption..."
            value={caption}
            onChange={handleCaptionChange}
          />
        )}

        {/* Action Buttons */}
        <div className="flex justify-end mt-4 space-x-3">
          {mediaFile && (
            <>
              <button
                className="py-2 px-6 bg-gray-200 text-black rounded-lg hover:bg-gray-300"
                onClick={() => {
                  setMediaFile(null);
                  setMediaPreview("");
                  setCaption("");
                }}
                disabled={!mediaFile}
              >
                Cancel
              </button>
              <button
                className={`py-2 px-6 rounded-lg text-white ${
                  mediaFile && caption
                    ? "bg-blue-600 hover:bg-blue-700"
                    : "bg-gray-400 cursor-not-allowed"
                }`}
                onClick={handleCreatePost}
                disabled={!mediaFile || !caption}
              >
                Create Post
              </button>
            </>
          )}
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="mt-6 flex space-x-4">
        <button
          className={`py-2 ${
            navigation === "posts"
              ? "border-blue-500 border-b-2 text-blue-500"
              : "text-gray-500 hover:text-blue-500"
          }`}
          onClick={() => {
            setNavigation("posts");
          }}
        >
          Posts
        </button>
        <button
          className={`py-2 ${
            navigation === "about"
              ? "border-blue-500 border-b-2 text-blue-500"
              : "text-gray-500 hover:text-blue-500"
          }`}
          onClick={() => {
            setNavigation("about");
          }}
        >
          About
        </button>
        <button
          className={`py-2 ${
            navigation === "follower"
              ? "border-blue-500 border-b-2 text-blue-500"
              : "text-gray-500 hover:text-blue-500"
          }`}
          onClick={() => {
            setNavigation("follower");
          }}
        >
          Followers
        </button>
        <button
          className={`py-2 ${
            navigation === "media"
              ? "border-blue-500 border-b-2 text-blue-500"
              : "text-gray-500 hover:text-blue-500"
          }`}
          onClick={() => {
            setNavigation("media");
          }}
        >
          Media
        </button>
      </div>
      {console.log(userPost)}

      {/* Posts Section */}
      {navigation === "posts" && (
        <>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
            {userPost.map((post) => {
              return (
                <div key={post._id}>
                  <div className="flex justify-end w-full bg-gray-50 shadow-2xl rounded-t-lg border-[1px] border-gray-700  pt-1 pb-1">
                    <div>
                      <FontAwesomeIcon
                        className="text-xl text-black mr-3 cursor-pointer hover:text-blue-500"
                        icon={faPenRegular}
                      />
                    </div>
                    <div>
                      <FontAwesomeIcon
                        className="text-xl text-black mr-2 cursor-pointer hover:text-red-600"
                        icon={faTrashAlt}
                      />
                    </div>
                  </div>
                  <div className="bg-gray-200 h-auto overflow-hidden shadow">
                    {post.mediaType === "image" ? (
                      <img
                        src={post.media}
                        alt="Failed to load media"
                        className="w-full h-96 object-cover"
                      />
                    ) : (
                      <video
                        src={post.media}
                        controls
                        className="w-full h-96 object-cover"
                      />
                    )}
                    <div className="w-full p-2 bg-gray-50 shadow-2xl rounded-b-lg border-b-2 border-gray-700">
                      <div className="flex">
                        <div className="ml-2 mr-1 hover:cursor-pointer">
                          <FontAwesomeIcon
                            className={`text-2xl ${
                              isULikePost ? "text-red-600" : "text-black"
                            } `}
                            icon={isULikePost ? LikeHeart : faHeart}
                            onClick={async () => {
                              await handleLikePost(post._id);
                            }}
                          />
                        </div>
                        <div className="mr-6 text-black">
                          {post.likes && post.likes.length}
                        </div>
                        <div className="hover:cursor-pointer">
                          <FontAwesomeIcon
                            className="text-black text-2xl mr-1"
                            icon={faComment}
                          />
                        </div>
                        <div className="text-black mr-6">
                          {post.comments && post.comments.length}
                        </div>
                        <div className="hover:cursor-pointer">
                          <FontAwesomeIcon
                            className="text-black text-2xl mr-1"
                            icon={faPaperPlane}
                          />
                        </div>
                        <div className="text-black">2</div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {userPost.length === 0 ? (
            <p className="text-2xl text-center text-gray-600 mt-5">
              No posts yet. Share something to get started!
            </p>
          ) : (
            ""
          )}
        </>
      )}

      {navigation === "about" && (
        <div className="bg-white p-6 mt-4 rounded-lg shadow-md">
          <h3 className="text-2xl font-bold mb-4">About John Doe</h3>

          {/* User's Bio */}
          <p className="text-gray-700 mb-4">
            Hi, I'm John Doe! I'm a passionate software engineer, explorer, and
            tech enthusiast. I love building innovative solutions that make a
            difference. Currently working as a Senior Developer at TechCorp.
          </p>

          {/* User's Info */}
          <ul className="space-y-2 text-gray-700">
            <li>
              <strong>Location:</strong> San Francisco, CA
            </li>
            <li>
              <strong>Job Title:</strong> Senior Developer at TechCorp
            </li>
            <li>
              <strong>Education:</strong> B.S. in Computer Science from UC
              Berkeley
            </li>
            <li>
              <strong>Interests:</strong> Hiking, Photography, Open-Source
              Development
            </li>
          </ul>
        </div>
      )}

      {navigation === "follower" && (
        <div className="bg-white p-6 mt-4 rounded-lg shadow-md">
          <h3 className="text-2xl font-bold mb-4 text-black">Followers</h3>

          {/* Followers Section */}
          <div className="overflow-y-auto max-h-52">
            {" "}
            {/* Adjust max height to control scroll */}
            <ul className="space-y-4">
              {/* Replace with dynamic followers list */}
              {Array(10)
                .fill("")
                .map((_, index) => (
                  <li key={index} className="flex items-center space-x-4">
                    <img
                      src={`https://via.placeholder.com/40?text=User+${
                        index + 1
                      }`}
                      alt={`User ${index + 1}`}
                      className="w-10 h-10 rounded-full object-cover"
                    />
                    <span className="text-gray-800">User {index + 1}</span>
                  </li>
                ))}
            </ul>
          </div>
        </div>
      )}

      {navigation === "media" && (
        <div className="bg-white p-6 mt-4 rounded-lg shadow-md">
          <h3 className="text-2xl font-bold mb-4 text-black">Media</h3>

          {/* Media Section */}
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            {/* Replace with dynamic media content */}
            {Array(8)
              .fill("")
              .map((_, index) => (
                <div
                  key={index}
                  className="bg-gray-200 h-40 rounded-lg overflow-hidden shadow"
                >
                  <img
                    src={`https://via.placeholder.com/150?text=Media+${
                      index + 1
                    }`}
                    alt={`Media ${index + 1}`}
                    className="w-full h-full object-cover"
                  />
                </div>
              ))}
          </div>
        </div>
      )}

      {/* Suggestions Section */}
      <div className="mt-6 bg-white p-4 rounded-lg shadow-md max-h-64 overflow-y-auto">
        <h3 className="text-lg font-bold mb-4 text-black">
          Suggestions for You
        </h3>
        <ul className="space-y-3">
          {suggestions.map((data) => {
            return (
              <li
                key={data._id}
                className="flex items-center justify-between bg-gray-100 p-3 rounded-lg"
              >
                <div className="flex items-center space-x-3">
                  <img
                    src={data.picture}
                    alt="Failed to load Img"
                    className="w-12 h-12 rounded-full object-cover"
                  />
                  <p className="font-bold text-black">{data.fullName}</p>
                </div>
                <button className="text-blue-500 hover:underline">
                  Follow
                </button>
              </li>
            );
          })}
          {suggestions.length < 0 || suggestions.length === 0 ? (
            <p className="text-gray-700">No suggestions available!</p>
          ) : (
            ""
          )}
        </ul>
      </div>
    </div>
  );
};

export default Profile;
