import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";

const Home = () => {
  const storiesContainerRef = useRef(null);
  const [showScrollIndicator, setShowScrollIndicator] = useState(false);

  const handleScroll = () => {
    const container = storiesContainerRef.current;
    if (container.scrollWidth > container.clientWidth) {
      setShowScrollIndicator(true);
    } else {
      setShowScrollIndicator(false);
    }
  };

  // Add event listener on component mount
  useEffect(() => {
    const container = storiesContainerRef.current;
    container.addEventListener("scroll", handleScroll);
    return () => container.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="bg-gray-100 min-h-screen">
        {/* Stories Section with Scroll Indicator */}
        <div className="relative mb-6">
          <div
            ref={storiesContainerRef}
            className="flex space-x-4 overflow-x-auto scrollbar-hidden mb-4 py-2"
          >
            {/* Story Item */}
            <div className="flex-shrink-0 w-24 h-24 bg-gray-300 rounded-full cursor-pointer">
              <img
                src="https://via.placeholder.com/150"
                alt="story"
                className="w-full h-full rounded-full object-cover"
              />
            </div>
            {/* Add more story items here */}
          </div>

          {/* Scroll Indicator */}
          {showScrollIndicator && (
            <div className="absolute top-1/2 left-0 right-0 flex justify-between px-4">
              <div className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-500 bg-opacity-50 text-white">
                <FontAwesomeIcon icon="angle-left" />
              </div>
              <div className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-500 bg-opacity-50 text-white">
                <FontAwesomeIcon icon="angle-right" />
              </div>
            </div>
          )}
        </div>

        {/* Feed Section */}
        <div className="w-full lg:w-3/4 bg-white p-4 rounded-lg shadow-lg">
          {/* Add Feed Content */}
        </div>
      </div>
    </>
  );
};

export default Home;
