import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMinus, faRemove } from "@fortawesome/free-solid-svg-icons";

const Groups = () => {
  const context = useContext(vividlyContext);
  const {
    handleFetchJoinedGroups,
    handleFetchFeaturedGroups,
    handleCreateGroup,
    searchUserFunc,
    searchUsers,
  } = context;
  const [joinedGroups, setJoinedGroups] = useState([]);
  const [featuredGroups, setFeaturedGroups] = useState([]);
  const [newGroupName, setNewGroupName] = useState("");
  const [newGroupDescription, setNewGroupDescription] = useState("");
  const [newGroupPicture, setNewGroupPicture] = useState(null);
  const [newGroupParticipants, setNewGroupParticipants] = useState("");
  const [addedMembers, setAddedMembers] = useState([]); // Added state for members
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  const [keyword, setKeyword] = useState("");
  const [show, setShow] = useState(true);
  const handleOnchange = (e) => {
    const searchTerm = e.target.value;
    setKeyword(e.target.value);
    if (searchTerm.length > 0) {
      searchUserFunc(e.target.value);
      setShow(false);
    } else {
      setShow(true);
    }
  };

  const fetchJoinedGroups = async () => {
    const response = await handleFetchJoinedGroups();
    if (response.success) {
      setJoinedGroups(response.data);
    }
  };

  const fetchFeaturedGroups = async () => {
    const response = await handleFetchFeaturedGroups();
    if (response.success) {
      setFeaturedGroups(response.data);
    }
  };

  const handleFetchGroupConversations = async (id) => {
    localStorage.setItem("groupId", JSON.stringify(id));
    navigate("/groupConversation");
  };

  const handleGroupCreation = async () => {
    if (newGroupName && newGroupDescription && addedMembers.length > 0) {
      const response = await handleCreateGroup({
        name: newGroupName,
        description: newGroupDescription,
        picture: newGroupPicture,
        participants: addedMembers.map((member) => member.id),
      });
      if (response.success) {
        setShowModal(false); // Close the modal
        fetchFeaturedGroups(); // Refresh the featured groups
      } else {
        alert("Failed to create group.");
      }
    } else {
      alert("Please fill in all fields and add at least one member.");
    }
  };

  const [addMemberDetails, setAddMemberDetails] = useState([]);

  const handleAddMember = (id, fullName, picture) => {
    if (!addMemberDetails.some((member) => member.id === id)) {
      setAddMemberDetails((prevMemberDetails) => [
        ...prevMemberDetails,
        { id, fullName, picture }, // Correct object syntax
      ]);
    }
  };

  const handleRemoveMember = (userId) => {
    setAddMemberDetails(
      addMemberDetails.filter((member) => member.id !== userId)
    );
  };

  useEffect(() => {
    fetchJoinedGroups();
    fetchFeaturedGroups();
  }, []);

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 mx-auto bg-white p-6 mt-4 rounded-lg shadow-md">
      <div className="mt-4 flex justify-between items-center">
        <input
          type="text"
          placeholder="Search for groups..."
          className="p-2 border rounded-lg w-full"
        />
        <button
          className="ml-4 px-5 py-2 bg-indigo-600 text-white rounded-full hover:bg-indigo-700 transition-colors duration-300"
          onClick={() => setShowModal(true)}
        >
          Create Group
        </button>
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white p-4 rounded-lg shadow-lg w-96 max-h-[90vh] overflow-y-auto sm:w-80 md:w-[600px]">
            <h2 className="text-2xl font-bold mb-4 text-black">
              Create New Group
            </h2>
            <input
              type="text"
              placeholder="Group Name"
              className="p-2 border rounded-lg w-full mb-4 text-black"
              value={newGroupName}
              onChange={(e) => setNewGroupName(e.target.value)}
            />

            <textarea
              placeholder="Group Description"
              className="p-2 border rounded-lg w-full mb-4 text-black"
              rows="3"
              value={newGroupDescription}
              onChange={(e) => setNewGroupDescription(e.target.value)}
            />

            <input
              type="text"
              placeholder="Search user and click to add"
              className="p-2 border rounded-lg w-full mb-1 text-black"
              value={keyword}
              onChange={handleOnchange}
            />
            <div className="max-h-[90vh] overflow-y-auto">
              {!show &&
                searchUsers &&
                searchUsers.map((user) => {
                  return (
                    <div
                      className="flex items-center p-2 bg-gray-50 rounded-lg shadow-md text-black hover:bg-gray-200 transition-all duration-300 cursor-pointer border-black border-b-2 mb-3"
                      onClick={() => {
                        setShow(true);
                        handleAddMember(user._id, user.fullName, user.picture);
                      }}
                      key={user._id}
                    >
                      <img
                        src={user.picture}
                        alt="Failed to load img"
                        className="w-12 h-12 ml-2 object-cover rounded-full border-2 border-gray-800"
                      />
                      <div className="ml-4">
                        <h2 className="text-base font-semibold ">
                          {user.fullName}
                        </h2>
                        <p className="text-sm ">@{user.userName}</p>
                      </div>
                    </div>
                  );
                })}
              {!show && !searchUsers ? (
                <p className="text-lg text-center text-gray-500 font-medium py-4">
                  {" "}
                  No users found. Try searching with a different keyword!
                </p>
              ) : (
                ""
              )}
            </div>

            {show && (
              <>
                <div className="mt-4">
                  {addMemberDetails.length > 0 && (
                    <div className="text-sm font-medium text-gray-700 mb-2">
                      Added Members:
                    </div>
                  )}
                  <div className="flex flex-wrap gap-2 mb-2">
                    {addMemberDetails.map((member) => (
                      <div
                        key={member.id}
                        className="flex items-center space-x-2 bg-gray-200 p-2 rounded-full text-sm hover:bg-gray-300 transition-all duration-200 ease-in-out"
                        style={{ minWidth: "110px" }}
                      >
                        <img
                          src={member.picture}
                          alt={member.fullName}
                          className="w-6 h-6 rounded-full border-2 text-gray-700 border-gray-300"
                        />
                        <span className="truncate font-medium text-gray-700">
                          {member.fullName}
                        </span>
                        <button
                          onClick={() => handleRemoveMember(member.id)}
                          className="text-red-500 text-lg hover:text-red-700 transition-all duration-200 ease-in-out"
                        >
                          <FontAwesomeIcon icon={faRemove} />
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
                <input
                  type="file"
                  className="p-2 border rounded-lg w-full mb-4 text-black"
                  onChange={(e) => setNewGroupPicture(e.target.files[0])}
                />
                <div className="flex justify-between">
                  <button
                    className="px-4 py-2 bg-gray-600 text-white rounded-full hover:bg-gray-700"
                    onClick={() => setShowModal(false)}
                  >
                    Cancel
                  </button>
                  <button
                    className="px-4 py-2 bg-indigo-600 text-white rounded-full hover:bg-indigo-700"
                    onClick={handleGroupCreation}
                  >
                    Create Group
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      )}

      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 text-black">
          Your Joined Groups
        </h3>
        <div className="grid grid-cols-1 lsm:grid-cols-2 lg:grid-cols-3 gap-6">
          {joinedGroups.length === 0 ? (
            <p className="text-gray-500">You have not joined any groups yet.</p>
          ) : (
            joinedGroups.map((group) => (
              <div
                key={group._id}
                className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-reansform duration-300 ease-in-out transform hover:scale-105"
              >
                <div className="flex items-center justify-between">
                  <div className="text-2xl font-semibold text-gray-800">
                    {group.groupName}
                  </div>
                  <span className="text-sm text-gray-500">
                    {group.participants.length} members
                  </span>
                </div>
                <p className="text-gray-600 mt-3 text-lg">
                  {group.description}
                </p>
                <button
                  className="mt-6 px-5 py-2 bg-green-600 text-white rounded-full hover:bg-green-700 transition-colors duration-300"
                  onClick={() => handleFetchGroupConversations(group._id)}
                >
                  Visit Group
                </button>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="mt-8">
        <h3 className="text-2xl font-bold mb-4 text-black">Featured Groups</h3>
        <div className="grid grid-cols-1 lsm:grid-cols-2 lg:grid-cols-3 gap-6">
          {featuredGroups.length === 0 ? (
            <p className="text-gray-600 text-md font-semibold mt-2">
              No featured groups to show right now.
            </p>
          ) : (
            featuredGroups.map((group) => (
              <div
                key={group._id}
                className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition-transform duration-300 ease-in-out transform hover:scale-105"
              >
                <div className="flex items-center justify-between">
                  <div className="text-2xl font-semibold text-gray-800">
                    {group.groupName}
                  </div>
                  <span className="text-sm text-gray-500">
                    {group.participants.length} members
                  </span>
                </div>
                <p className="text-gray-600 mt-3 text-lg">
                  {group.description}
                </p>
                <button className="mt-6 px-5 py-2 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition-colors duration-300">
                  Join Group
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Groups;
