import React, { useState } from "react";

const Message = () => {
  // Dummy data for users
  const [users, setUsers] = useState([
    {
      id: 1,
      name: "John Doe",
      username: "johndoe",
      picture: "https://via.placeholder.com/100",
    },
    {
      id: 2,
      name: "Jane Smith",
      username: "janesmith",
      picture: "https://via.placeholder.com/100",
    },
    {
      id: 3,
      name: "Michael Lee",
      username: "michaellee",
      picture: "https://via.placeholder.com/100",
    },
    {
      id: 4,
      name: "Emma White",
      username: "emmawhite",
      picture: "https://via.placeholder.com/100",
    },
    // Add more users as needed
  ]);

  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = (e) => {
    setSearchQuery(e.target.value.toLowerCase());
  };

  const filteredUsers = users.filter(
    (user) =>
      user.name.toLowerCase().includes(searchQuery) ||
      user.username.toLowerCase().includes(searchQuery)
  );

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Search Bar */}
      <div className="p-4 bg-blue-600">
        <input
          type="text"
          placeholder="Search users..."
          value={searchQuery}
          onChange={handleSearch}
          className="w-full p-3 rounded-lg bg-white text-gray-800 shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      {/* Users Grid */}
      <div className="p-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {filteredUsers.map((user) => (
          <div
            key={user.id}
            className="relative bg-white p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300"
          >
            {/* User Profile */}
            <div className="relative w-full h-40 mb-4">
              <img
                src={user.picture}
                alt={user.name}
                className="w-full h-full object-cover rounded-lg transition-transform transform hover:scale-105"
              />
              <div className="absolute inset-0 flex items-center justify-center opacity-0 hover:opacity-100 bg-black bg-opacity-50 rounded-lg">
                <span className="text-white text-lg font-semibold">
                  {user.username}
                </span>
              </div>
            </div>
            <div className="text-center">
              <h3 className="text-lg font-semibold">{user.name}</h3>
              <p className="text-gray-500">@{user.username}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Message;
