import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation();

  return (
    <aside className="smd:w-[155px] sm:w-52 md:w-64 h-screen bg-gray-100 border-r fixed top-0 left-0 pt-20">
      <ul className="space-y-6 p-2 sm:p-6">
        <Link
          to="/groups"
          className={`block ${
            location.pathname === "/groups" ? ".activeLink" : ""
          }`}
        >
          <SidebarItem icon="users" text="Groups" />
        </Link>
        <Link to="/events" className="block">
          <SidebarItem icon="calendar" text="Events" />
        </Link>
        <Link to="/marketplace" className="block">
          <SidebarItem icon="shopping-bag" text="Marketplace" />
        </Link>
        <Link to="/friends" className="block">
          <SidebarItem icon="user" text="Friends" />
        </Link>
      </ul>
    </aside>
  );
};

const SidebarItem = ({ icon, text }) => (
  <li className="flex items-center space-x-3 text-gray-700 hover:text-blue-500 cursor-pointer">
    <FontAwesomeIcon icon={icon} className="w-5 h-5" />
    <span>{text}</span>
  </li>
);

export default Sidebar;
