import React, { useState } from "react";

const HelpCenter = () => {
  const [faqs, setFaqs] = useState([
    {
      question: "How do I reset my password?",
      answer:
        "Go to Account Settings and click 'Reset Password' to set a new password.",
      open: false,
    },
    {
      question: "How can I delete my account?",
      answer:
        "Visit the 'Privacy' section in settings and click 'Delete Account.'",
      open: false,
    },
    {
      question: "How can I change the theme?",
      answer:
        "Go to Account Settings, In General Settings there is a button to change theme.",
      open: false,
    },
    {
      question: "How do I report inappropriate content?",
      answer:
        "Click the 'Report' button on the content, and provide details for the report.",
      open: false,
    },
    {
      question: "How can I block another user?",
      answer:
        "Visit the user's profile and click the 'Block' option in the menu.",
      open: false,
    },
    {
      question: "How do I manage my notifications?",
      answer:
        "Go to Account Settings, then click on 'Notifications' to customize your preferences.",
      open: false,
    },
    {
      question: "How can I recover a deleted post?",
      answer:
        "Currently, deleted posts cannot be recovered. Please confirm before deleting.",
      open: false,
    },
    {
      question: "What should I do if my account is hacked?",
      answer:
        "Contact Support immediately and change your password to secure your account.",
      open: false,
    },
    {
      question: "Can I use the app in offline mode?",
      answer:
        "Certain features, like viewing saved posts, are available offline. Others require an internet connection.",
      open: false,
    },
    {
      question: "How do I customize my profile?",
      answer:
        "Visit your profile page and click 'Edit Profile' to update your details and settings.",
      open: false,
    },
    {
      question: "What are Premium Features?",
      answer:
        "Premium features include advanced analytics, ad-free browsing, and exclusive content. Upgrade from the Subscriptions page.",
      open: false,
    },
    {
      question: "How do I cancel my subscription?",
      answer:
        "Go to the Subscriptions page and click 'Cancel Subscription.' Follow the prompts to confirm.",
      open: false,
    },
  ]);

  const toggleFaq = (index) => {
    setFaqs(
      faqs.map((faq, i) =>
        i === index ? { ...faq, open: !faq.open } : { ...faq, open: false }
      )
    );
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 mx-auto p-6 bg-white rounded-lg shadow-md">
      <h1 className="text-3xl font-bold mb-6 text-center text-black">
        Help Center
      </h1>

      {/* Search Bar */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search for help topics..."
          className="w-full p-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      {/* FAQ Section */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-4 text-black">
          Frequently Asked Questions
        </h2>
        {faqs.map((faq, index) => (
          <div key={index} className="mb-4">
            <button
              onClick={() => toggleFaq(index)}
              className="w-full text-left p-4 bg-gray-100 rounded-lg shadow-md focus:outline-none"
            >
              <div className="flex justify-between items-center">
                <span className="text-gray-800 font-medium">
                  {faq.question}
                </span>
                <span>{faq.open ? "▲" : "▼"}</span>
              </div>
            </button>
            {faq.open && (
              <div className="mt-2 text-gray-600 p-4 bg-gray-50 rounded-lg">
                {faq.answer}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HelpCenter;
