import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };
  return (
    <div>
      <div>
        Lorem ipsum dolor, sit amet consectetur adipisicing elit. Ducimus est,
        optio natus eius officia saepe, animi corrupti quisquam tenetur culpa
        iste sed voluptates soluta. Saepe qui ullam suscipit modi eaque deleniti
        rem, odio corporis aliquam cupiditate quae nesciunt placeat autem
        laboriosam nihil. Quibusdam quaerat excepturi quia repellendus eius et
        iusto, itaque ipsam totam, sunt illum. Unde porro adipisci illo sunt
        suscipit, mollitia repellat! Veritatis dolore dignissimos minus, nostrum
        corrupti iusto quisquam, porro vitae reiciendis quas ex necessitatibus
        perferendis velit numquam! Mollitia, doloribus dolore, nam ut aliquam
        debitis modi qui distinctio velit, eos libero repellendus nemo fugit
        quis? Culpa, accusamus saepe.
      </div>
      <aside className="hidden sm:block sm:w-64 h-screen bg-gray-100 border-r fixed top-0 left-0 pt-20">
        <ul className="space-y-7 p-2 sm:p-6">
          <Link to="/profile" className="block">
            <SidebarItem icon="user-circle" text="Profile" path="/profile" />
          </Link>
          <Link to="/dashboard" className="block">
            <SidebarItem
              icon="tachometer-alt"
              text="Dashboard"
              path="/dashboard"
            />
          </Link>
          <Link to="/groups" className="block">
            <SidebarItem icon="users" text="Groups" path="/groups" />
          </Link>
          <Link to="/friends" className="block">
            <SidebarItem icon="user" text="Friends" path="/friends" />
          </Link>
          <Link to="/events" className="block">
            <SidebarItem icon="calendar" text="Events" path="/events" />
          </Link>
          <Link to="/marketplace" className="block">
            <SidebarItem
              icon="shopping-bag"
              text="Marketplace"
              path="/marketplace"
            />
          </Link>

          <Link to="/subscriptions" className="block">
            <SidebarItem
              icon="credit-card"
              text="Subscriptions"
              path="/subscriptions"
            />
          </Link>
          <Link to="/likes" className="block">
            <SidebarItem icon="thumbs-up" text="Liked Posts" path="/likes" />
          </Link>
        </ul>
      </aside>
    </div>
  );
};

const SidebarItem = ({ icon, text, path }) => {
  const location = useLocation();
  const isActive = location.pathname === path;

  return (
    <li
      className={`flex items-center space-x-3 ${
        isActive ? "activeLink" : "text-gray-700"
      } hover:text-blue-500 cursor-pointer`}
    >
      <FontAwesomeIcon icon={icon} className="w-5 h-5" />
      <span>{text}</span>
    </li>
  );
};

export default Sidebar;
