import React, { useContext, useEffect, useState } from "react";
import vividlyContext from "../Context/vividlyContext";

const ConversationPage = () => {
  const context = useContext(vividlyContext);
  const { handleFetchConversation } = context;
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState("");
  // Dummy messages data
  // const [messages, setMessages] = useState([
  //   {
  //     sender: "user1",
  //     text: "Hey, how are you?",
  //     time: "10:30 AM",
  //   },
  //   {
  //     sender: "user2",
  //     text: "I'm doing well, thanks! How about you?",
  //     time: "10:32 AM",
  //   },
  //   {
  //     sender: "user1",
  //     text: "I'm good too, just working on some projects.",
  //     time: "10:35 AM",
  //   },
  // ]);

  const currentUser = "user1";
  const id = localStorage.getItem("id");
  const parsedId = JSON.parse(id);

  const [newMessage, setNewMessage] = useState("");
  const handleFechConversation = async () => {
    const response = await handleFetchConversation("678a4e9a9138a456b19baea2");
    if (response.message === "Conversation retrieved successfully!") {
      setMessages(response.data.messages);
    } else {
      setError("No conversation started yet!");
    }
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();

    // If the message is from today, show only the time
    if (date.toDateString() === now.toDateString()) {
      return new Intl.DateTimeFormat("en-US", {
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    }
    // If it's older, show date and time
    else {
      return new Intl.DateTimeFormat("en-US", {
        month: "short",
        day: "2-digit",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
        hour12: true,
      }).format(date);
    }
  };

  useEffect(() => {
    handleFechConversation();
  }, []);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const newMsg = {
        sender: currentUser,
        text: newMessage,
        time: new Date().toLocaleTimeString([], {
          hour: "2-digit",
          minute: "2-digit",
        }),
      };

      setMessages([...messages, newMsg]);
      setNewMessage("");
    }
  };

  return (
    <div className="max-w-7xl mx-auto h-screen sm:pl-72 sm:pt-24 p-4 flex flex-col bg-gray-50">
      {/* Chat Header */}
      <div className="bg-blue-600 text-white p-4 shadow-md flex items-center justify-between rounded-md">
        <h2 className="text-xl font-semibold">Conversation</h2>
        <div className="flex items-center space-x-2">
          <img
            src="https://via.placeholder.com/40"
            alt="User Profile"
            className="w-10 h-10 rounded-full object-cover"
          />
          <span className="font-semibold">Other User</span>
        </div>
      </div>

      {/* Messages Section */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-100">
        {messages.length > 0 &&
          messages.map((msg) => (
            <div
              key={msg._id}
              className={`flex ${
                msg.senderId === parsedId ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs p-3 rounded-lg ${
                  msg.senderId === parsedId
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200 text-gray-800"
                } shadow-md`}
              >
                <p className="text-sm">{msg.text}</p>
                <span className="text-xs text-gray-300">
                  {formatDate(msg.createdAt)}
                </span>
              </div>
            </div>
          ))}
      </div>

      {/* Message Input Section */}
      <div className="bg-white p-4 border-t border-gray-300 flex items-center">
        <input
          type="text"
          placeholder="Type a message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="w-full p-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 text-black"
        />
        <button
          onClick={handleSendMessage}
          className="ml-3 p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ConversationPage;
