import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const Login = ({ login }) => {
  const [alert, setAlert] = useState(null);
  const showAlert = (type, msg) => {
    setAlert({
      type: type,
      msg: msg,
    });
    setTimeout(() => {
      setAlert("");
    }, 2000);
  };
  const navigate = useNavigate();
  const [loginCred, setLoginCred] = useState({ email: "", password: "" });
  const handleOnChange = (e) => {
    setLoginCred({ ...loginCred, [e.target.name]: e.target.value });
  };
  const handleLogin = async (e) => {
    e.preventDefault();
    if (!loginCred.email || !loginCred.password) {
      return showAlert("error", "Please fill all required fields!");
    }
    const response = await login(loginCred.email, loginCred.password);
    if (response.success) {
      navigate("/home");
      localStorage.setItem("id", JSON.stringify(response.id));
      localStorage.setItem("userName", JSON.stringify(response.userName));
      localStorage.setItem("token", JSON.stringify(response.token));
    } else {
      showAlert("error", "Invalid Credentials!");
    }
  };
  return (
    <>
      <div className="max-w-md mx-auto bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg">
        {/* Alert Section */}
        <div className="mb-4">
          <Alert alert={alert} />
        </div>

        {/* Email Input Field */}
        <div className="mb-4">
          <label
            htmlFor="email"
            className="block text-sm font-semibold text-gray-900 dark:text-gray-300"
          >
            Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            autoComplete="off"
            onChange={handleOnChange}
            value={loginCred.email}
            placeholder="Enter your email"
            className="mt-1 w-full p-3 border border-gray-300 rounded-lg text-gray-900 dark:text-white bg-gray-50 dark:bg-gray-700 focus:ring-blue-500 focus:border-blue-500"
            required
          />
        </div>

        {/* Password Input Field */}
        <div className="mb-4">
          <label
            htmlFor="password"
            className="block text-sm font-semibold text-gray-900 dark:text-gray-300"
          >
            Password
          </label>
          <input
            type="password"
            id="password"
            name="password"
            onChange={handleOnChange}
            value={loginCred.password}
            placeholder="Enter your password"
            className="mt-1 w-full p-3 border border-gray-300 rounded-lg text-gray-900 dark:text-white bg-gray-50 dark:bg-gray-700 focus:ring-blue-500 focus:border-blue-500"
            required
          />
        </div>

        {/* Forgot Password Link */}
        <div className="flex justify-between items-center mb-6">
          <p className="text-sm text-gray-600 dark:text-gray-300">
            Don't have an account?{" "}
            <a href="/signup" className="text-blue-500 hover:underline">
              Sign up
            </a>
          </p>
          <a
            href="/forgot-password"
            className="text-sm text-blue-600 hover:underline focus:outline-none"
          >
            Forgot Password?
          </a>
        </div>

        {/* Login Button */}
        <button
          type="submit"
          onClick={handleLogin}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-lg shadow-md focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 transition duration-300"
        >
          Login
        </button>
      </div>
    </>
  );
};

export default Login;
