import React, { useContext, useState } from "react";
import vividlyContext from "../Context/vividlyContext";

const Messages = () => {
  const context = useContext(vividlyContext);
  const { searchUserFunc, searchUsers } = context;
  const [show, setShow] = useState(true);
  const [conversations, setConversations] = useState([
    {
      id: 1,
      name: "John Doe",
      avatar: "https://via.placeholder.com/40?text=John",
      lastMessage: "Hey, how are you?",
      time: "2 minutes ago",
      isRead: false,
    },
    {
      id: 2,
      name: "Jane Smith",
      avatar: "https://via.placeholder.com/40?text=Jane",
      lastMessage: "Looking forward to our meeting!",
      time: "1 hour ago",
      isRead: true,
    },
    // More conversations can be added
  ]);

  const [messageThread, setMessageThread] = useState([
    { id: 1, sender: "You", message: "Hey, John!" },
    { id: 2, sender: "John Doe", message: "Hey! How's it going?" },
    { id: 3, sender: "You", message: "All good, let's catch up!" },
  ]);
  const [keyword, setKeyword] = useState("");
  const handleKeywordOnChange = (e) => {
    const searchTerm = e.target.value;
    setKeyword(e.target.value);
    if (searchTerm.length > 0) {
      searchUserFunc(e.target.value);
      setShow(false);
    } else {
      setShow(true);
    }
  };

  return (
    <div className="min-h-screen sm:pl-72 sm:pt-24 container mx-auto p-4 sm:p-6">
      {/* Header Section */}
      <div className="bg-blue-500 text-white p-6 rounded-lg shadow-md mb-8">
        <h1 className="text-3xl font-bold">Messages</h1>
        <div className="mt-4">
          <input
            type="text"
            placeholder="Search messages"
            value={keyword}
            onChange={handleKeywordOnChange}
            className="w-full px-4 py-2 rounded-lg shadow-md bg-white text-gray-800"
          />
        </div>
      </div>

      {/* Conversation List */}
      <div className="space-y-4">
        {show &&
          conversations.map((conversation) => (
            <div
              key={conversation.id}
              className={`flex items-center p-4 rounded-lg shadow-md ${
                conversation.isRead ? "bg-gray-100" : "bg-blue-100"
              } hover:bg-blue-200 transition duration-200`}
            >
              <img
                src={conversation.avatar}
                alt={conversation.name}
                className="w-12 h-12 rounded-full object-cover"
              />
              <div className="ml-4 flex-1">
                <p className="font-semibold text-gray-800">
                  {conversation.name}
                </p>
                <p className="text-sm text-gray-600">
                  {conversation.lastMessage}
                </p>
              </div>
              <span className="text-xs text-gray-500">{conversation.time}</span>
            </div>
          ))}
      </div>
      <div className="max-h-[90vh] overflow-y-auto">
        {!show &&
          searchUsers &&
          searchUsers.map((user) => {
            return (
              <div
                className="flex items-center p-2 bg-gray-50 rounded-lg shadow-md text-black hover:bg-gray-200 transition-all duration-300 cursor-pointer border-black border-b-2 mb-3 "
                key={user._id}
                // onClick={() => handleFetchSearchedUData(user._id)}
              >
                <img
                  src={user.picture}
                  alt="Failed to load img"
                  className="w-16 h-16 ml-2 object-cover rounded-full border-2 border-gray-800"
                />
                <div className="ml-4">
                  <h2 className="text-lg font-semibold ">{user.fullName}</h2>
                  <p className="text-sm ">@{user.userName}</p>
                </div>
              </div>
            );
          })}
        {!show && !searchUsers ? (
          <p className="text-lg text-center text-gray-500 font-medium py-4">
            {" "}
            No users found. Try searching with a different keyword!
          </p>
        ) : (
          ""
        )}
      </div>
    </div>
  );
};

export default Messages;
