import React, { useEffect, useState } from "react";
import Signup from "./Signup";
import Login from "./Login";
import OTPVerification from "./OTPVerification";

const AuthPage = () => {
  const [activeTab, setActiveTab] = useState("tab1");
  const [websiteName, setWebsiteName] = useState("");

  const [step, setStep] = useState(2);
  // const handleUpdateStep = () => {
  //   setStep(1);
  // };

  useEffect(() => {
    const name = process.env.REACT_APP_WEBSITE_NAME;
    let index = 0;
    let direction = 1;

    setInterval(() => {
      setWebsiteName(name.slice(0, index));
      index += direction;

      if (index > name.length) {
        direction = -1;
        index = name.length;
      } else if (index < 0) {
        direction = 1;
        index = 0;
      }
    }, 300);
  }, []);

  return (
    <div className="authPageBG">
      <div className="min-h-screen flex justify-center items-center">
        <form className="border-2 smd:w-[90vw] sm:w-[70vw] md:w-[55vw] lg:w-[42vw] max-h-[80vh] overflow-y-auto border-black bg-white rounded-lg p-5 boxShadow">
          <div className="flex justify-center mt-3 h-12">
            <h3 className="custom-font text-4xl text-black font-bold">
              {websiteName}
            </h3>
          </div>
          {step === 1 && (
            <>
              <div role="tablist" className="tabs mt-4 flex">
                <button
                  type="button"
                  role="tab"
                  className={`tab w-1/2 p-4  text-center text-xl rounded-sm ${
                    activeTab === "tab1"
                      ? "bg-blue-500 text-white"
                      : "bg-gray-200 text-gray-700"
                  }`}
                  aria-selected={activeTab === "tab1"}
                  onClick={() => setActiveTab("tab1")}
                >
                  Signup
                </button>
                <button
                  type="button"
                  role="tab"
                  className={`tab w-1/2  text-center text-xl rounded-sm ${
                    activeTab === "tab2"
                      ? "bg-blue-500 text-white"
                      : "bg-gray-200 text-gray-700"
                  }`}
                  aria-selected={activeTab === "tab2"}
                  onClick={() => setActiveTab("tab2")}
                >
                  Login
                </button>
              </div>
              <div className="mt-5">
                {activeTab === "tab1" && (
                  <div role="tabpanel">
                    <Signup />
                  </div>
                )}
                {activeTab === "tab2" && (
                  <div role="tabpanel">
                    <Login />
                  </div>
                )}
              </div>
            </>
          )}
          {step === 2 && (
            <>
              <div className="mt-5">
                <h4 className="text-2xl font-semibold text-black mb-2">
                  Verify Your OTP
                </h4>
                <p className="text-gray-600 mb-4">
                  Please enter the 6-digit OTP sent to your registered email.
                </p>

                <OTPVerification />
                <div className="flex justify-center gap-4 mt-6">
                  {/* Back Button */}
                  <button
                    type="button"
                    onClick={() => setStep(1)}
                    className="bg-gray-300 text-black font-medium py-2 px-6 rounded-lg hover:bg-gray-400 transition-all duration-300"
                  >
                    ← Back
                  </button>

                  {/* Verify Button */}
                  <button
                    type="button"
                    className="bg-blue-600 text-white font-medium py-2 px-6 rounded-lg hover:bg-blue-700 transition-all duration-300"
                  >
                    Verify OTP
                  </button>
                </div>
              </div>
            </>
          )}
        </form>
      </div>
    </div>
  );
};

export default AuthPage;
